sections.css:
/* breakpoints */
$breakpoints-min-width: (
	sm: 768px,
	md: 992px,
	lg: 1200px
);
@function min-width($screen-size) {
	@return map-get($breakpoints-min-width, $screen-size);
}

@function max-width($screen-size) {
	@return map-get($breakpoints-min-width, $screen-size) - 1;
}

@function responsiveVal($variable, $viewport) {
	@return map-get($variable, $viewport);
}

/* typo3 overridable fonts */
$font-family-headline: 'Montserrat' !default;
$font-family-copy: 'Open Sans' !default;

/* typo3 overridable colors */
$primary-color: #323232 !default;
$secondary-color: #CD1316 !default;
/*
$primary-color: rgb(51,49,78) !default;
$secondary-color: rgb(255,212,0) !default;
*/

$primary-darken-10-color: darken($primary-color, 10%);
$secondary-darken-10-color: darken($secondary-color, 10%);

$error-color: #FB1E45;
$success-color: #269A40;
$info-color: #fade2e;
$info-text-color: #7e673e;

$teaser-fallback-color: #d4d4d4;

/* neutral colors */
$black-color: #000000;
$black-medium-color: #333333; // black 80%
$black-75-color: hsl(0, 0%, 25%);
$black-60-color: lighten($black-color, 40%); //black 60%
$black-light-color: #808080; //black 50%
$black-extra-light-color: #727272;
$black-38-color: lighten($black-color, 62%);
$black-30-color: lighten($black-color, 70%);
$grey-color: #bdbdbd; // black 26%
$grey-medium-color: #d4d4d4; // black 17%
$grey-light-color: #efefef; // black 10%
$white-color: #ffffff;
$yellow-color: hsl(50, 100%, 60%);

/* transparent colors */
$primary-light-transparent-color: transparentize($primary-color, .1);
$primary-transparent-color: transparentize($primary-color, .35);
$primary-transparent-50-color: transparentize($primary-color, .5);
$secondary-light-transparent-color: transparentize($secondary-color, .1);
$white-light-transparent-color: transparentize($white-color, .1);
$generic-overlay-color: transparentize($black-color, .6);
$teaser-overlay-color: transparentize($primary-color, .1);

$black-transparent-color: transparentize($black-color, .8);
$dimmer-color: $generic-overlay-color;

/* lines */
$lines-global: rgba(0, 0, 0, .2); // black 20%
$lines-global-inverted: rgba(255, 255, 255, .7); // white 70%
$lines-global-light: rgba(0, 0, 0, .12); // black 12%
$lines-global-bold: rgba(0, 0, 0, .15); // black 15%
$lines-global-bold-inverted: rgba(255, 255, 255, .8); // white 80%

/* placeholder */
$placeholder-global-color: $black-75-color; //input fields
$placeholder-paragraph-color: $lines-global; //product teaser

/* background image */
$stage-background: "http://placehold.it/320x260/008888/550000" !default;

/* spacings for text areas */
$space-text-paragraph-v: 1rem;
$space-text-headline-v: 1rem;
$space-text-image-v: 1rem;
$space-text-list-v: 1rem;
$space-text-list-item-v: .5rem;

$spacing-vertical-base: $base-size* 1.5;
$spacing-vertical-base-sm: $spacing-vertical-base* 2;
$spacing-vertical-inc: $spacing-vertical-base * 1.5;
$spacing-vertical-inc-sm: $spacing-vertical-inc * 2;

/* Generic */
$circular-cb: cubic-bezier(.79, .14, .15, .86);
$default-spinner-size: 1.5rem;
$default-spinner-size-sm: 2.5rem;

/* z-index ranges */
$z-index-page-modal: 600;
$z-index-page-overlay: 500; // 500-599
$z-index-page-header: 400; // 400-499
$z-index-page-content: 300; // 300-399
$z-index-page-footer: 200; // 200-299
$z-index-page-highlight: 100;

$z-index-btn-to-top: $z-index-page-overlay + 1;
$z-index-cookie-disclaimer: $z-index-page-overlay + 2;
$z-index-filter-area: $z-index-page-content + 1;
$z-index-dark-message: $z-index-page-overlay + 1;

/* VARIABLES */
$grid-container-padding-md: 18px;
$grid-container-padding: 25px;
$grid-gutter-width: 30px;
$grid-columns: 12;
$default-transition: .4s ease;
$hover-transition: .25s ease;
$enable-flex: true;

/* ATOMS */
$burger-height: $base-size * 1.5;
$burger-padding: $base-size / 2;
$burger-width: $base-size * 2;

$btn-social-facebook-color: #3b5998;
$btn-social-foursquare-color: #fc4575;
$btn-social-googleplus-color: #df4a32;
$btn-social-instagram-color: #c32aa3;
$btn-social-pinterest-color: #cb2027;
$btn-social-twitter-color: #00b6f1;
$btn-social-yelp-color: #d32323;
$btn-social-youtube-color: #b31217;
$btn-social-whatsapp-color: #25D366;
$btn-social-email-color: hsl(0, 0%, 35%);
$btn-spinner-size: 1.25rem;
$btn-std-width: $base-size * 14;

$btn-tooltip-margin: .75rem;
$btn-tooltip-arrow: .75rem;
$btn-tooltip--bg: $black-75-color;

$btn-round-size: $base-size * 3;
$btn-round-size--small: $base-size * 2.5;

$btn-padding-horizontal: 3rem;
$btn-padding-vertical: .5rem;
$btn-icon-size: 1.5em;
$icon-inline-spacing: 4px;

$icn-detail-line-height: 1.75rem;
$icn-detail-padding-v: .5rem;
$icn-detail-before-font-size: 1.50em;
$icn-detail-before-padding-right: .25em;
$icn-icon-padding-right: .25rem;

$inp-select-language-height: $base-size * 2;
$inp-select-dropdown-padding: .25rem;

$opn-opening-hours-font-weight: 2em;
$opn-icon-margin-right: .25rem;

$owl-arrow-size: $base-size * 3;

$search-icon-margin-right: .75rem;
$search-input-height: 2rem;
$search-input-height-f-lg: 2rem;
$search-input-margin: 1rem;
$search-input-margin-right: .25rem;
$search-input-padding-left-lg: 1rem;
$search-input-width-f-lg: 12rem;
$search-transition: ease .4s;
$search-container-height: $search-input-height + $search-input-margin * 2;

$shl-logo-height: 10rem;

$icon-arrow-font-size: 28px;
$icon-size: 2.5rem;
$icon-small-font-size: 24px;
$icon-small-size: 2rem;

$search-input-padding-left: $icon-small-size + .25rem;

$ttl-headline-padding-h: 1.5rem;
$txt-blockquote-padding-left: 1rem;

/* MOLECULES */
$btn-round-small: 45px;

$cookie-disclaimer-bg: transparentize($primary-color, .1);
$cookie-disclaimer-border: transparentize($white-color, .75);
$infolist-item-margin-h-md: .75rem;

$mnu-btn-sublist-color: $secondary-color;
$mnu-btn-sublist-font-size: 1.25rem;
$mnu-btn-sublist-margin-v: .5rem;
$menu-buttons-width: 40px;

$mnu-dropdown-item-padding-v: .5rem;
$mnu-dropdown-item-padding-h: 1rem;
$mnu-dropdown-visibility-transition: $default-transition;
$mnu-dropdown-opacity-transition: $default-transition;
$mnu-dropdown-padding-bottom: .25rem;
$mnu-dropdown-width: 9rem;

$mnu-infolist-item-padding: .625rem 0;

$mnu-lang-item-padding-h: 1rem;
$mnu-lang-item-padding-h-md: .5rem;
$menu-list-btn-sublist-min-width: 40px;
$menu-list-label-after-size: 14px;
$menu-list-label-padding: .75rem 0;
$menu-list-label-padding-md: .25em 1em .9em;
$menu-list-margin-bottom: 40px;
$menu-list-transition: ease .4s;
$mnu-sublist-transition: .25s;
$mnu-sublist-delay: .85s;

$menu-padding: 1rem;
$mnu-sublist-item-margin-v-md: .75rem;
$mnu-sublist-item-padding: .5rem 1rem .5rem 2rem;
$mnu-sublist-item-padding-md: .5rem 2rem;

$fltr-list-item-border-width: .25rem;
$fltr-list-item-padding: 1.5rem;
$fltr-dropdown-margin: 1rem;
$fltr-dropdown-max-width: 250px;
$fltr-menu-border-bottom: 1px;
$fltr-submenu-list-padding-top: 1.25rem;
$fltr-nav-disabled-opacity: .2;

$ftsh-control-margin: 1rem .5rem;

// _tsr.scss
$tsr-separator-line-width: 5rem;
$tsr-separator-line-space-right: calc(100% - #{$tsr-separator-line-width});
$tsr-category-padding: 3px .5rem;
$tsr-content-padding: 1.25rem;
$tsr-margin-bottom: 2rem;

$tsr-stage-media-size: (xs: 5rem, sm: 255px);
$tsr-stage-header-margin-bottom: 1rem;
$tsr-stage-content-padding: 1.25rem 1.25rem 3.5rem 1.25rem;
$tsr-stage-content-padding-sm: 2rem;
$tsr-header-after-padding: .75rem;
$tsr-hover-translate-distance: .5rem;

// _tsr-press.scss
$tsr-press-padding: 1.25rem;
$tsr-press-height: 355px;

// _tsr-transparent.scss
$tsr-transparent-height: 350px;
$tsr-transparent-min-height-sm: 400px;

// _tsr-service.scss
$tss-icon-margin-bottom: .5rem;
$tss-margin-bottom: 2rem;
$tss-teaser-height: 10rem;
$tss-teaser-height-sm: 200px;

// _services
$srv-icon-size: 65px;
$srv-icon-margin-bottom: .5rem;
$srv-bar-button-margin: .5rem .125rem;
$srv-search-bar-padding: .75rem 0;
$srv-header-padding: 2rem 0 1rem;

// _str-full-width.scss
$tsr-full-width-height: 460px;

/* ORGANISMS */
// _dark-site
$drk-message-max-width: 768px;

// _mdl-modal
$mdl-width-xs: 100%;
$mdl-height-xs: 100%;
$mdl-padding-inner-vrt: 40px;
$mdl-padding-inner-hrz: 40px;
$mdl-padding-outer-vrt: 80px;

// modal, overlay animation fade in/out
// this maps is used with mdl-modal-animation mixin
$map-modal-animation-fade: (
	in : fadeIn,
	out : fadeOut,
	duration : .4s,
	timing : linear,
	delay : 0s
);
$map-overlay-animation-fade: (
	in : fadeIn,
	out : fadeOut,
	duration : .15s,
	timing : linear,
	delay : 0s
);

// _directions.scss
$dir-accordion-content-max-width: 768px;
$dir-button-size: 50px;
$dir-button-size-sm: 100px;
$dir-form-title-margin-v: .5rem;
$dir-form-button-margin-top: 1rem;
$dir-controls-left: 2.5rem;
$dir-controls-top: 2.5rem;
$dir-controls-max-height: calc(100% - 2 * #{$dir-controls-top});
$dir-controls-width: 320px;
$dir-controls-max-width: 45%;
$dir-controls-padding: .75rem;
$dir-element-margin-bottom: .5rem;
$dir-element-icon-padding: .25rem;
$dir-element-icon-size: 25px;
$dir-input-padding: .25rem .5rem .25rem $dir-element-icon-size;
$dir-map-height: 270px;
$dir-map-height-sm: 670px;
$dir-map-controls-margin-bottom: 1rem;
// circa 100% - padding - dir-form height
$dir-panel-max-height-sm: 350px;

// _footer.scss
$ftr-aside-margin: 1rem;
$ftr-aside-footer-padding-v: 1rem;
$ftr-article-title-padding-bottom: 1rem;
$ftr-article-title-margin-bottom: 1rem;
$ftr-article-margin-bottom: 2rem;
$ftr-article-link-font-size: 1rem;
$ftr-adresse-link-margin-bottom: 1rem;
$ftr-article-link-margin-right: .5rem;
$ftr-adresse-margin-bottom: 1.5rem;
$ftr-article-store-icon-max-height: 40px;
$ftr-article-store-icon-margin-right: 1.5rem;
$ftr-container-max-width: min-width(lg);
$ftr-email-height: 2.5rem;
$ftr-email-width: $ftr-email-height;
$ftr-email-input-margin: .25rem;
$ftr-email-input-max-width: 300px;
$ftr-email-input-width: calc(100% - 1rem - #{$ftr-email-width} - #{$ftr-email-input-margin});
$ftr-email-input-padding-left: 1rem;
$ftr-email-button-font-size: 1.75rem;
$ftr-email-font-size: 1rem;
$ftr-lang-margin-top: 2rem;
$ftr-lang-margin-bottom: 1rem;
$ftr-lang-pseudo-font-size: 20px;
$ftr-lang-pseudo-min-width: 20px;
$ftr-lang-height: 35px;

$ftr-nav-sublist-margin-h: 1rem;
$ftr-nav-list-li-padding: 1rem;
$ftr-social-button-margin: .5rem;

$tsr-grp-content-width-padding: 8px;

// _header.scss
$header-logo-max-height: 70px;

$hdr-burger-margin-left: .25rem;
$hdr-container-max-width: min-width(lg);
$hdr-dimmer-transition: .4s ease;
$hdr-header-height: (xs: 70px, md: 100px);
$hdr-header-menu-padding-top: .5rem;
$hdr-header-menu-padding-top-lg: .75rem;
$hdr-header-padding-v: .5rem;
$hdr-header-padding-h: 1rem;
$hdr-header-padding-h-md: 3rem;
$hdr-header-padding-h-lg: calc(50% - #{min-width(lg)} / 2 + 25px);
$hdr-search-margin-top: .875rem;

$hdr-icon-search-position-top-md: .5rem;

$hdr-search-transition: .4s ease;
$hdr-search-margin-right: $hdr-header-padding-h-lg;

$hdr-lang-margin-left: $infolist-item-margin-h-md;
$hdr-lang-margin-right: 1rem;

$hdr-logo-landscape-height: 54px;
$hdr-logo-landscape-height-md: 68px;
$hdr-logo-landscape-padding-v: $hdr-header-padding-v;
$hdr-logo-landscape-padding-v-md: 1rem;

$hdr-logo-llandscape-width: 150px;
$hdr-logo-llandscape-width-md: 210px;

$hdr-logo-portrait-height: responsiveVal($hdr-header-height, xs) + 8px;
$hdr-logo-portrait-height-md: responsiveVal($hdr-header-height, md) + 8px;
$hdr-logo-portrait-max-width: $hdr-logo-portrait-height / 120 * 100;
$hdr-logo-portrait-max-width-md: $hdr-logo-portrait-height-md / 120 * 100;

// _stage-detail.scss
$std-breadcrumbs-after-padding-h: .25rem;
$std-breadcrumbs-margin-bottom: 1.5rem;
$std-breadcrumbs-margin-bottom-sm: 2rem;
$std-controls-margin-bottom: 1rem;
$std-head-margin-bottom: .5rem;
$std-hover-opacity: .7;
$std-title-padding-top: 1.25rem;
$std-title-text-padding-h: 1rem;
$std-stage-padding-top: 16px + responsiveVal($hdr-header-height, xs);
$std-stage-padding-top-md: 16px + responsiveVal($hdr-header-height, md);

// _stage-fallback.scss
$stf-carousel-margin-top: 230px;
$stf-carousel-prev-margin-right: .125rem;

$stf-main-min-height: 320px;
$stf-main-min-height-f-sm: 600px;
$stf-main-min-height-f-md: 640px;
$stf-main-min-height-f-lg: 685px;

$stf-carousel-max-width: (xs: 30rem, sm: 45rem);

// _stage-overview.scss
$sto-container-padding-top-md: responsiveVal($hdr-header-height, md);
$sto-container-padding-bottom: 2.5rem;
$sto-container-padding-top: responsiveVal($hdr-header-height, xs);
$sto-container-min-height: 320px;
$sto-container-height-sm: 350px;
$sto-stage-min-height: 20rem;
$sto-teaser-title-margin-v: 1.5rem;
$sto-teaser-rule-color: $secondary-color;
$sto-teaser-rule-height: .25rem;
$sto-teaser-rule-margin-top: 1.5rem;
$sto-teaser-rule-width: 7rem;

// _detail-page.scss
$dtl-contact-logo-margin-bottom: 1.5rem;
$dtl-contact-border-width: 1px 0;
$dtl-contact-logo-width: 210px;
$dtl-contact-margin-right: 1rem;
$dtl-contact-max-width: 360px;
$dtl-header-contact-margin-bottom: 2rem;
$dtl-header-title-padding-bottom: .75rem;
$dtl-header-subtitle-padding: 1rem;
$dtl-location-contact-padding: 0 0 1rem 0;
$dtl-location-cols-margin-bottom: 2rem;
$dtl-location-logo-margin-bottom: 1.25rem;
$dtl-location-logo-max-width: 288px;
$dtl-description-max-width: 770px;
$dtl-description-header-margin-bottom: 2rem;
$dtl-description-content-margin-bottom: 2rem;
$dtl-stage-bild-margin-bottom: 1.5rem;
$dtl-stage-bild-margin-bottom-sm: 2.5rem;
$dtl-service-icon-size: 90px;
$dtl-service-icon-size-sm: 120px;

$tsr-category-dot-space: ($spacing-vertical-inc - $btn-round-size / 4) / 2;
$tsr-category-dot-space-sm: ($spacing-vertical-inc-sm - $btn-round-size / 4) / 2;

// _quicklinks.scss
$qln-icon-size: $base-size * 3;
$qln-icon-padding: $qln-icon-size * .25;

// _newsletter.scss
$nws-registration-bg-width: 25rem;

/* TEMPLATES */
$wall-padding: 2rem 0;

