@mixin hidden {
	display: none !important;
}

@mixin visuallyhidden {
	border: 0;
	clip: rect(0 0 0 0);
	height: 1px;
	margin: -1px;
	overflow: hidden;
	padding: 0;
	position: absolute !important;
	width: 1px;
}

.hidden {
	@include hidden;
}

.visuallyhidden {
	@include visuallyhidden;
}

.faded-out {
	opacity: 0 !important;
	pointer-events: none;
}

html.mobile .visuallyhidden-mobile {
	@include visuallyhidden;
}

html.mobile .hidden-mobile {
	@include hidden;
}

html:not(.mobile) .visuallyhidden-desktop {
	@include visuallyhidden;
}

html:not(.mobile) .hidden-desktop {
	@include hidden;
}

@each $size in sm, md, lg {
	@include media-up($size) {
		.hidden-#{$size}-up {
			display: none !important;
		}
	}

	@include media-down($size) {
		.hidden-#{$size}-down {
			display: none !important;
		}
	}
}

@mixin fixed-ratio($w, $h) {
	height: auto;

	&::before {
		content: "";
		display: block;
		padding-top: $h / $w * 100%;
	}
}

@mixin hover {
	html:not(.can-touch) & {
		&:hover {
			@content;
		}
	}
}

@mixin ie11-only {
	@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
		@content;
	}
}

// Padding and margin util classes
@each $direction in top,right,bottom,left {
	@each $property in padding,margin {
		.hlp-#{$property}-#{$direction}-1rem {
			#{$property}-#{$direction}: 1rem;
		}
	}
}

@mixin spacing-responsive-base($type: "margin", $dir: "bottom") {
	#{$type}-#{$dir}: $spacing-vertical-base;

	@include media(sm) {
		#{$type}-#{$dir}: $spacing-vertical-base-sm;
	}
}

@mixin spacing-responsive-inc($type: "margin", $dir: "bottom") {
	#{$type}-#{$dir}: $spacing-vertical-inc;

	@include media(sm) {
		#{$type}-#{$dir}: $spacing-vertical-inc-sm;
	}
}

@mixin spacing-responsive-base-negative($type: "margin", $dir: "bottom") {
	#{$type}-#{$dir}: -$spacing-vertical-base;

	@include media(sm) {
		#{$type}-#{$dir}: -$spacing-vertical-base-sm;
	}
}

@mixin section-padding {
	@include spacing-responsive-base("padding", "top");
	@include spacing-responsive-base("padding", "bottom");
}

.section-padding {
	@include section-padding;
}

@mixin section-margin {
	@include spacing-responsive-base("margin", "top");
	@include spacing-responsive-base("margin", "bottom");
}

.section-margin {
	@include section-margin;
}

@mixin section-padding-asymmetric {
	@include spacing-responsive-base("padding", "top");
	@include spacing-responsive-inc("padding", "bottom");
}

@mixin button-margin {
	margin-bottom: 1rem;
	margin-top: 1rem;
}

@mixin button-margin-large {
	margin-bottom: 2rem;
	margin-top: 2rem;
}

.button-margin-large {
	@include button-margin-large;
}

@mixin button-padding {
	padding-bottom: 1rem;
	padding-top: 1rem;
}

.button-padding {
	@include button-padding;
}

.section-padding {
	@include section-padding;
}

.section-padding-asymmetric {
	@include section-padding-asymmetric;
}

.spacing-base {
	margin-bottom: $spacing-vertical-base;
}

.spacing-responsive-inc {
	@include spacing-responsive-inc;
}

.spacing-responsive-base {
	@include spacing-responsive-base;
}

@mixin ruler-top {
	border-top: 1px solid $lines-global;
	display: block;
	margin-top: 2rem;
	padding-bottom: 2rem;
	width: 100%;
}

@mixin ruler-bottom {
