$banderole-bg-color: getThemeColorValue("highlight-color");
$banderole-bg-darker-color: darken($banderole-bg-color, 15%);
$banderole-bg-color--disabled: hsl(0, 0%, 20%);
$banderole-bg-darker-color--disabled: hsl(0, 0%, 15%);
$banderole-text-color: color-contrast($banderole-bg-color, $black-75-color, $white-color);
$banderole-size: 150px;
$banderole-envelope: 6px;

.bdr {
	&-banderole {
		height: $banderole-size;
		overflow: hidden;
		position: absolute;
		width: $banderole-size;
		z-index: $z-index-page-content;

		&::before, &::after {
			border: $banderole-envelope/2 solid $banderole-bg-darker-color;
			content: '';
			display: block;
			position: absolute;
			z-index: 0;
		}

		&__container {
			background-color: $banderole-bg-color;
			box-shadow: 0 1px 3px rgba(0, 0, 0, .3);
			color: $banderole-text-color;
			display: block;
			@include font(banderole-text);
			font-weight: 600;
			height: 48px;
			line-height: .9rem;
			padding: 15px 0;
			position: absolute;
			text-align: center;
			text-transform: uppercase;
			width: 225px;
		}

		&__text--disabled {
			display: none;
		}

		&--disabled {
			&::before, &::after {
				border-color: $banderole-bg-darker-color--disabled;
			}
		}

		&--disabled  &__container {
			background-color: $banderole-bg-color--disabled;
		}

		&--disabled &__text--default {
			display: none;
		}

		&--disabled &__text--disabled {
			display: block;
		}

		/* top left*/
		&--top-left {
			left: -$banderole-envelope;
			top: -$banderole-envelope;

			&::before, &::after {
				border-left-color: transparent;
				border-top-color: transparent;
			}

			&::before {
				right: 0;
				top: 0;
			}

			&::after {
				bottom: 0;
				left: 0;
			}
		}

		&--top-left &__container {
			@include flexbox;
			@include align-items(center);
			@include justify-content(center);
			right: -25px;
			top: 30px;
			transform: rotate(-45deg);
		}

		&--top-left &__text {
			max-width: 70px;
		}
	}
}

.burger {
	cursor: pointer;
	padding: $burger-padding;

	&__container {
		height: $burger-height;
		position: relative;
		width: $burger-width;
	}

	&__inner {
		top: 0px;

		&::before, &::after {
			content: "";
			display: block;
			position: absolute;
		}

		&, &::before, &::after {
			background-color: getThemeColorValue("header__text");
			height: 4px;
			transition: opacity, transform .3s ease;
			width: 100%;
		}

		&::before {
			top: 10px;
		}

		&::after {
			top: 20px;
		}

		body.menu-active & {
			transform: translateY(10px) rotate(45deg);

			&::before {
				opacity: 0;
				transform: translate(-6px, -6px) rotate(-45deg);
			}

			&::after {
				transform: translateY(-20px) rotate(-90deg);
			}
		}
	}
}

.btn {
	// Reset unusual Firefox-on-Android default style; see https://github.com/necolas/normalize.css/issues/214
	background-image: none;
	color: inherit;
	cursor: pointer;
	display: inline-block;
	overflow: hidden;
	position: relative;
	touch-action: manipulation;
	transition: $hover-transition;
	vertical-align: middle;

	&--facebook {
		background-color: $btn-social-facebook-color;
	}

	&--foursquare {
		background-color: $btn-social-foursquare-color;
	}

	&--yelp {
		background-color: $btn-social-yelp-color;
	}

	&--googleplus {
		background-color: $btn-social-googleplus-color;
	}

	&--instagram {
		background-color: $btn-social-instagram-color;
	}

	&--pinterest {
		background-color: $btn-social-pinterest-color;
	}

	&--twitter {
		background-color: $btn-social-twitter-color;
	}

	&--youtube {
		background-color: $btn-social-youtube-color;
	}

	&--email {
		background-color: $btn-social-email-color;
	}

	&--whatsapp {
		background-color: $btn-social-whatsapp-color;
	}

	&--dark-hover {
		overflow: hidden; // for round buttons

		&:before {
			background-color: hsla(0, 0, 0, 0);
			content: '';
			height: 100%;
			left: 0;
			position: absolute;
			top: 0;
			transition: $hover-transition;
			width: 100%;
		}

		&:hover::before {
			background-color: hsla(0, 0, 0, .3);
		}
	}

	&[disabled],
	&.disabled {
		opacity: .5;
		pointer-events: none;
	}
}

.btn {
	.btn__inner {
		display: inline-block;
		text-align: left;
	}

	.btn__icon,
	.btn__icon::before,
	.btn__label {
		display: inline-block; // all lined-up elements share the same display to get the same height
	}

	.btn__icon {
		position: relative;
		vertical-align: top;

		// let the empty inline element acquire the correct height if it had some text in it
		&::before {
			content: ' ';
			width: 0;
		}

		svg {
			color: currentColor;
			fill: currentColor;
			font-size: inherit;
			height: $btn-icon-size;
			margin-top: -$btn-icon-size / 2;
			overflow: hidden;
			position: absolute;
			right: $icon-inline-spacing;
			top: 50%;
			width: $btn-icon-size;
		}

		&--spinner {
			display: none;
		}

		&--follows {
			svg {
				left: $icon-inline-spacing;
			}
		}
	}

	// variant for links in copy text
	&--copy {
		color: $primary-transparent-color;
		font-weight: bold;
		text-decoration: underline;
	}

	&--icon {
		.btn__inner {
			// the padding is on the inner wraper just for multiline capability
			padding-left: calc(#{$btn-icon-size} + #{$icon-inline-spacing});
		}

		&-follows {
			.btn__inner {
				// the padding is on the inner wraper just for multiline capability
				padding-right: calc(#{$btn-icon-size} + #{$icon-inline-spacing});
			}
		}
	}
}

.btn {

	&--badge {
		overflow: visible;
		position: relative;

		&[data-badge]::before {
			background-color: getThemeColorValue('header__text');
			border-radius: 1em;
			color: getThemeColorValue('header');
			content: attr(data-badge);
			font-size: 12px;
			line-height: 1.4;
			margin-left: 10px;
			min-width: 1em;
			padding: 1px 4px 0px 4px;
			text-align: center;
			transition: $default-transition;
		}

		&[data-badge='0']::before {
			display: none;
		}

		@include media(md) {
			&[data-badge]::before {
				font-size: 8px;
				position: absolute;
				right: 0;
				top: 0;
				transform: translate(80%, -85%);
			}
		}
	}

	// rectangular variants
	&--light, &--solid {
		@include font(button);
		line-height: 1.5;
		padding: $btn-padding-vertical $btn-padding-horizontal;
		text-align: center;
		text-transform: uppercase;
	}

	&--light {
		background-color: getThemeColorValue('btn-light__bg');
		border: 1px solid getThemeColorValue('btn-light__border');
		color: getThemeColorValue('btn-light__text');

		@include hover {
			background-color: getThemeColorValue('btn-light--hover__bg');
			color: getThemeColorValue('btn-light--hover__text');
		}

	}

	&--solid {
		background-color: getThemeColorValue('btn-solid__bg');
		border: 1px solid;
		border-color: getThemeColorValue('btn-solid__border');
		color: getThemeColorValue('btn-solid__text');

		html:not(.can-touch) & {
			&:hover {
				background-color: getThemeColorValue('btn-solid--hover__bg');
				color: getThemeColorValue('btn-solid--hover__text');
			}
		}
	}

	&--solid.btn--important {
		background-color: getThemeColorValue('btn-solid__bg') !important;
		border: 1px solid !important;
		border-color: getThemeColorValue('btn-solid__border') !important;
		color: getThemeColorValue('btn-solid__text') !important;

		@include hover {
			background-color: getThemeColorValue('btn-solid--hover__bg') !important;
			color: getThemeColorValue('btn-solid--hover__text') !important;
		}
	}

	&--inverse {
		background-color: getThemeColorValue('btn-inverse__bg');
		border-color: getThemeColorValue('btn-inverse__border');
		color: getThemeColorValue('btn-inverse__text');

		html:not(.can-touch) & {
			&:hover {
				background-color: getThemeColorValue('btn-inverse--hover__bg');
				color: getThemeColorValue('btn-inverse--hover__text');
			}
		}
	}

	&--small {
		font-size: .875rem;
		padding: .25rem 1.5rem;
	}

	&--big {
		padding: .5625rem 2rem;
	}

	// full-width variant
	&--full-width {
		text-align: center;
		width: 100%;

		&-xs {
			@include media-down(sm) {
				width: 100%;
			}
		}
	}


	&--spinner {
		.icn-spinner {
			display: block;
		}

		.btn__icon {
			display: none;

			&--spinner {
				display: inline-block;

				svg {
					animation: spinner-rotate 1s infinite linear, spinner-stroke 1.5s alternate infinite linear;
					fill: transparent;
					stroke: currentColor;
					stroke-dasharray: 140 189;
					stroke-width: 8;
				}
			}
		}
	}
}

.btn {
	&-round {
		border-radius: 50%;
		color: getThemeColorValue('btn-solid__text');
		cursor: pointer;
		display: inline-block;
		height: $btn-round-size;
		width: $btn-round-size;

		svg {
			padding: 15%;
		}

		&--small {
			height: $btn-round-size--small;
			width: $btn-round-size--small;
		}

		&--hoverable {
			transition: $hover-transition;
			@include hover{
				background-color: getThemeColorValue('btn-solid--hover__bg');
				color: getThemeColorValue('btn-solid--hover__text');
			}
		}

		&--social {
			height: $btn-round-size * 1.25;
			position: relative;
			width: $btn-round-size * 1.25;
		}
	}

	&-to-top {
		background-color: getThemeColorValue('btn-solid__bg');
		bottom: $btn-round-size / 2;
		box-shadow: 0px 0px 3px 0px rgba(255, 255, 255, .5);
		cursor: pointer;
		padding: $btn-round-size * .25;
		position: fixed;
		right: $btn-round-size / 2;
		z-index: $z-index-btn-to-top;
	}

	&-play {
		align-items: center;
		background-color: $primary-color;
		border: 2px solid $white-color;
		border-radius: 100%;
		display: flex;
		height: $btn-round-size;
		justify-content: center;
		left: 50%;
		margin: (-$btn-round-size / 2) 0 0 (-$btn-round-size / 2);
		position: absolute;
		text-align: center;
		top: 50%;
		width: $btn-round-size;

		&::before {
			border-color: transparent;
			border-left-color: $white-color;
			border-style: solid;
			border-width: 10px 0 10px 18px;
			content: '';
			display: block;
			height: 0;
			margin-left: 4px;
			width: 0;
		}
	}
}

// Convert a color to the format rgb(xx,xx,xx)
@function colorToRGB($color) {
	@return 'rgb(#{floor(red($color))}, #{floor(green($color))}, #{floor(blue($color))})'
}

.btn {
	&-tooltip {
		overflow: visible; // IE11 button don't show pseudo elements otherwise
		position: relative;

		&::after {
			@include font(small);

			background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 100 50' width='100' height='50'><path fill='#{colorToRGB($btn-tooltip--bg)}' d='M25 25L0 0L50 0L100 0L75 25L50 50L25 25Z'/></svg>"), linear-gradient(to top, transparent, transparent #{$btn-tooltip-arrow - .2rem}, #{$btn-tooltip--bg} #{$btn-tooltip-arrow - .2rem}, #{$btn-tooltip--bg} );
			background-position: bottom center;
			background-repeat: no-repeat;
			background-size: #{$btn-tooltip-arrow * 2} $btn-tooltip-arrow, 100%;
			color: white;
			content: attr(data-tooltip-content);
			left: 50%;
			opacity: 0;
			padding: .5rem 1rem 1.25rem 1rem;
			position: absolute;
			top: 0;
			transform: translate(-50%, -100%) translateY(-.5rem);
			transition: opacity $default-transition;
			visibility: hidden;
			white-space: nowrap;
		}

		@include hover {
			&::after {
				opacity: 1;
				visibility: visible;
			}
		}

		&--no-margin {
			@include hover {
				&::after {
					transform: translate(-50%, -100%);
				}
			}
		}
	}
}

.icn-icon {
	color: currentColor;
	fill: currentColor;
	height: 1em;
	overflow: hidden;
	vertical-align: -.15em;
	width: 1em;

	&--fit {
		height: 100%;
		vertical-align: baseline;
		width: 100%;
	}
}

.icn-icon--hover2 {
	transition: $hover-transition;

	@include hover {
		color: $secondary-color;
	}
}

.icn--svg, .icn-container {
	svg {
		height: 100%;
		width: 100%;
	}

	svg, path, polygon, rect, circle {
		fill: currentColor !important;
	}
}

.icn-like {
	cursor: pointer;

	&__container {
		transition: opacity .25s .25s;

		// Hide for Services and other items, that cannot be bookmarked
		&[data-uid=""], &[data-uid="-"], &:not([data-uid]) {
			display: none;
		}
	}

	.border {
		fill: currentColor;
	}

	.solid-fill {
		fill: transparent;
	}

	&:not(.icn-like--active):hover {
		.solid-fill {
			fill: currentColor;
			opacity: .15;
		}
	}

	&--active {
		animation: icn-heartbeat ease-in-out .25s;

		.solid-fill {
			fill: currentColor;
		}
	}
}


.icn-spinner {
	@include spinner;

	display: none;
	fill: transparent;
	stroke: currentColor;
	stroke-dasharray: 140 189;
	stroke-linecap: round;
	stroke-width: 8;
}

.inp {
	&-select {
		display: inline-block;
		position: relative;

		&__icon {
			color: currentColor;
			pointer-events: none;
			position: absolute;
			top: 50%;
			transform: translateY(-45%);
			z-index: 2;

			&--right {
				right: .5em;
			}

			&--left {
				left: .5em;
			}
		}

		&__select {
			appearance: none;
			background-color: inherit;
			border: 0;
			color: currentColor;
			font-family: inherit;
			font-size: 1rem;
			font-weight: 600;
			outline: none;
			position: relative;
			width: 100%;
			z-index: 1;

			@include media(md) {
				font-size: .875rem;
			}


			&::-ms-expand {
				display: none;
			}
		}

		&--disabled {
			cursor: not-allowed;
			opacity: .5;

			select {
				pointer-events: none;
			}
		}

		&--language {
			border: 1px solid $black-extra-light-color;
			line-height: $inp-select-language-height;
		}

		&--language &__select {
			height: $inp-select-language-height;
			padding-left: 2.5rem;
			padding-right: 1.5rem;
		}

		&--language &__icon {
			font-size: 1.25em;

			&--left {
				left: .75rem;
			}

			&--right {
				right: .25rem;
			}
		}

		&--dropdown {
			background-color: $white-color;
			border: 1px $grey-color solid;

			&::after {
				background-color: $grey-color;
				content: "";
				height: calc(100% - 2 * #{$inp-select-dropdown-padding});
				pointer-events: none;
				position: absolute;
				right: 2rem;
				top: $inp-select-dropdown-padding;
				width: 1px;
				z-index: 2;
			}
		}

		&--dropdown &__select {
			padding: $inp-select-dropdown-padding 2.5rem $inp-select-dropdown-padding .5rem;
		}
	}
}

// Hide native show password in IE
::-ms-reveal {
	display: none;
}

.inp {
	&-list {
		@include reset-list;

		background-color: $white-color;
		box-shadow: 4px 4px 8px transparentize($black-medium-color, .6);
		padding-bottom: $mnu-dropdown-padding-bottom;
		width: $mnu-dropdown-width;

		&__item {
			color: $black-medium-color;
			transition: background-color .3s ease;

			&:hover, &:focus {
				background-color: $grey-light-color;
			}

			&[data-status="ACT"] {
				color: transparentize($black-color, .5);

				@include hover {
					background-color: transparent;
				}
			}
		}

		&__text {
			cursor: pointer;
			display: block;
			padding: $mnu-dropdown-item-padding-v $mnu-dropdown-item-padding-h;
		}

		&__separator {
			@include font(inp-dropdown-separator);

			border-top: 1px solid $grey-color;
			color: $black-38-color;
			padding: .5rem;
		}
	}

	&-dropdown {
		&__trigger {
			cursor: pointer;
		}
		&__list {
			margin-top: .75rem;
			opacity: 0;
			position: absolute;
			transition: opacity $mnu-dropdown-opacity-transition, visibility $mnu-dropdown-visibility-transition;
			visibility: hidden;
			z-index: 11;

			.dropdown-active ~ & {
				opacity: 1;
				visibility: visible;
			}
		}
	}
}

.inp {
	&-search {
		display: flex;
		position: relative;

		&__input {
			@include font(lead);
			@include placeholder($placeholder-global-color, italic);

			background-color: transparent;
			border: none;
			border-radius: 0;
			flex: 1 1 0px;
			height: calc(2rem - 1px);
			min-width: 0;

			// clear icon by default in 'X' in IE
			&::-ms-clear {
				height: 0;
				width: 0;
			}
		}

		&__icon {
			color: $black-light-color;
			font-size: 1.5em;
			height: calc(2rem - 1px);
			max-width: 2rem;

			//IE bug
			transition: opacity $hover-transition;
		}

		// Search autosuggest
		&__dropdown {
			bottom: 0;
			left: 0;
			position: absolute;
			transform: translateY(100%);
			width: 100%;
			z-index: 50;
		}

		// hide/show the right icon (search, clear or spinner)
		.js-icon-close {
			cursor: pointer;
			display: none;
		}

		.js-icon-spinner {
			display: none;
		}

		&__input:not([value=""]) ~ &__icon {
			.js-icon-close {
				display: inline;
			}
			.js-icon-search {
				display: none;
			}
		}

		&--active {
			.js-icon-close {
				display: none !important;
			}
			.js-icon-search {
				display: none !important;
			}
			.js-icon-spinner {
				display: inline;
			}
		}

		&--default {
			background-color: $white-color;

			.inp-search__input {
				padding-left: $base-size / 2;

				@include media(sm) {
					padding-left: $base-size;
				}
			}
		}

		&--light {
			border-bottom: 2px $grey-color solid;
		}
	}
}

/*
Styles for classes applied by filter.js
*/

.inp-filter {
	&-active {
		.inp-filter-highlight {
			background: yellow;
		}

		.inp-filter-element:not(.inp-filter-match) {
			display: none;
		}
	}
}

.mda {
	&-frame {
		background-color: $white-color;
		border: 1px solid $lines-global;
		height: $shl-logo-height;
		position: relative;

		&__container {
			// background for lazy-loading
			background-position: 50% 50%;
			background-repeat: no-repeat;
			background-size: contain;
			height: 60%;
			left: 20%;
			position: absolute;
			top: 20%;
			transform: translate3d(0, 0, 0); //Safari pixeled base64
			width: 60%;
		}

		img, &__media {
			font-family: 'object-fit: contain;'; // IE polyfill
			height: 100%;
			object-fit: contain;
			position: absolute;
			top: 0;
			width: 100%;
		}

		&--16-9 {
			@include fixed-ratio(16, 9);
		}

		&--4-3 {
			@include fixed-ratio(4, 3);
		}

		&--1-1 {
			@include fixed-ratio(1, 1);
		}

		&--5-6 {
			@include fixed-ratio(5, 6);
		}

		&--no-border {
			border-width: 0;
		}

		&--grey {
			background-color: $grey-medium-color;
			border-color: $grey-medium-color;

			.mda-frame__media {
				color: $grey-light-color;
			}
		}

		&--small-padding {
			.mda-frame__media {
				padding: 6px;
			}
		}
	}
}

.tbl-table {
	@include section-margin;

	table {
		border-collapse: collapse;
		width: 100%;
	}

	thead {
		background-color: $grey-medium-color;
		color: $black-color;
		th {
			border-right: 1px solid $white-color;
			&:last-child {
				border-right: 0;
			}
		}
	}

	tr {
		border-bottom: 1px solid $lines-global-bold;
	}

	&--striped {
		tr {
			border: 0;
		}
		tr {
			&:nth-child(2n) {
				background-color: $grey-light-color;
			}
		}
	}

	@include media(sm) {
		td, th {
			padding: .5rem .75rem;
			text-align: left;
		}
	}

	@include media-down(sm) {
		thead {
			display: none;
		}

		td {
			display: block;

			&::before {
				@include font(tbl-header);
				content: attr(data-header) ":";
				display: inline-block;
				min-width: 30%;
			}
		}

		tr {
			border-color: $lines-global-bold;
			display: block;
			padding: 1rem;

			&:last-child {
				border-bottom: 0;
			}
		}
	}
}

.tme {
	@include spacing-responsive-base;

	&-media {
		width: 100%;

		&__container {
			position: relative;
		}

		img, video, iframe {
			left: 0;
			position: absolute;
			top: 0;
			width: 100%;
		}

		iframe {
			height: 100%;
		}
	}

	&-styles {
		h3 {
			@include content-section;
		}

		abstract {
			@include font(lead);

			display: block;
			margin-bottom: $base-size * 2;
		}

		a:not(.btn) {
			@extend .btn--copy;
		}

		a.line-through {
			text-decoration: line-through;
		}

		a.ga-disabled {
			text-decoration: none;
			text-indent: 1rem;
		}

		a.ga-disabled:before {
			content: " ";
		}

		p {
			@include font(copy);
		}

		del {
			text-decoration: line-through;
		}

		small {
			font-size: 14px;
		}

		strong {
			font-weight: 600;
		}

		sub {
			font-size: .6em;
			vertical-align: bottom;
		}

		sup {
			font-size: .6em;
			vertical-align: top;
		}

		.text-center {
			text-align: center;
		}

		.text-justify {
			text-align: justify;
		}

		.text-right {
			text-align: right;
		}

		blockquote {
			border-left: 4px solid $grey-light-color;
			color: $black-extra-light-color;
			margin: 0;
			padding-left: $txt-blockquote-padding-left;
		}

		.ce-gallery {
			margin-bottom: 2rem;

			.ce-column {
				display: flex;
				justify-content: center;
			}

			.video,
			.image {
				flex: 0 0 auto;

				// hello there IE11
				max-width: 100%;
			}

			.image-embed-item,
			.video-embed-item {
				max-width: 100%;
			}

			figcaption {
				@include font(small);

				margin-top: .25rem;
			}
		}
	}
}

.tme {
	&__header {
		&--heading-2 {
			@include make-container;
		}

		&--heading-3 {
			@include make-content-container;

			.heading-3 {
				border-bottom: 1px solid $lines-global;
				margin-bottom: $base-size * 2;
				padding-bottom: $dtl-header-title-padding-bottom;
			}
		}
	}
}

.tme-strong {
	font-weight: 600;
}

.tme-center {
	text-align: center;
}

.tme-list {
	padding-left: 1rem;

	li {
		margin-bottom: 1rem;
		margin-top: 1rem;
	}

	&__paragraph {
		display: block;
		margin-bottom: .5rem;
		margin-top: .5rem;
	}
}

.tme-placeholder-block {
	box-shadow: inset 0 .75em $placeholder-paragraph-color;
	color: transparent;
}

/*
  Styles for CKEditor Email obfuscation sets @ and dots
*/
.email__at::after {
	content: "\0040";

	/* Unicode character for @ symbol */
}

.email__dot::after {
	content: "\002E";

	/* Unicode character for . symbol */
}

h1, .heading-1 {
	@include font(h1);

	margin: 0 0 1rem 0;
	text-transform: uppercase;
}

h2, .heading-2 {
	@include font(h2);

	margin: 0 0 1rem 0;
	text-transform: uppercase;
}

h3, .heading-3 {
	@include font(h3);

	margin: 0 0 1rem 0;
	text-transform: uppercase;
}

h4, .heading-4 {
	@include font(h4);

	margin: 0 0 1rem 0;
	text-transform: uppercase;
}

h5, .heading-5 {
	@include font(h5);

	margin: 0 0 .5rem 0;
	text-transform: none;
}

h6, .heading-6 {
	@include font(h6);

	margin: 0 0 .5rem 0;
	text-transform: none;
}

@mixin content-section {
	// used also in text-media-element
	@include font(h3);

	margin: ($base-size * 2) 0;
	padding-bottom: $dtl-header-title-padding-bottom;
	text-transform: uppercase;

	// after instead of border to account for padding
	&::after {
		background: $lines-global;
		bottom: -$dtl-header-title-padding-bottom;
		content: "";
		display: block;
		height: 1px;
		position: relative;
		width: 100%;
	}

	&:first-child {
		margin-top: 0;
	}
}

.ttl {
	color: getThemeColorValue("ttl__text");

	&__headline {
		display:block;
		margin: 0;
		padding: 0 $ttl-headline-padding-h;
		text-align: center;
		text-transform: uppercase;
	}

	&--page {
		.ttl__headline {
			@include font(h1);

			border-top: 3px solid $lines-global-bold;
			padding-top: $grid-gutter-width / 2;
			width: 100%;
		}

		@include media(sm) {
			.ttl__headline {
				border-top: none;
				padding-top: 0;
				width: auto;
			}
		}
	}

	&--section {
		@include spacing-responsive-base;

		.ttl__headline {
			@include font(h2);
		}

		@include media(sm) {
			&::before,
			&::after {
				height: 3px;
			}
		}
	}

	&--subsection {
		@include content-section;
	}
}


.dtl__page-title {
	@include make-container;
	@include spacing-responsive-inc;
}

.frm {
	&-radio {
		input[type=radio] {
			opacity: 0;
			position: absolute
		}

		&__label {
			margin-right: .5rem;
			position: relative;
		}

		&__span {
			&::before {
				background-color: $grey-light-color;
				border-radius: 100%;
				content: "";
				display: inline-block;
				height: 1rem;
				margin-right: .25rem;
				vertical-align: -2px;
				width: 1rem;
			}
		}

		input:checked ~ &__span::before {
			background-color: getThemeColorValue("hover--on-bright");
			border: .25rem solid $grey-light-color;
		}

		&__span:active::before {
			box-shadow: inset 0 0 2px $lines-global;
		}
	}
}

.frm {
	&-checkbox {
		input[type=checkbox] {
			cursor: pointer;
			opacity: 0;
			position: absolute;
		}

		input:focus ~ &__label:before {
			outline: 1px dotted;
		}

		&__label {
			&::before {
				border: 1px solid black;
				content: "";
				display: inline-block;
				height: 1rem;
				margin-right: .25rem;
				vertical-align: -2px;
				width: 1rem;
			}

			&:active::before {
				box-shadow: inset 0 0 1px $lines-global;
			}
		}

		input:checked ~ &__label::after {
			border-color: getThemeColorValue("hover--on-bright");
			border-style: solid;
			border-width: 0 2px 2px 0;
			content: "";
			height: 1rem;
			left: .5rem;
			position: absolute;
			top: 2px;
			transform: rotate(50deg);
			transform-origin: center;
			width: .5rem;
		}

		.has-error &__label::before {
			background-color: transparentize($error-color, .9);
			border-color: $error-color;
		}
	}
}

.frm {
	&-recaptcha {
		box-shadow: 0 0 0 1px $lines-global;
		filter: grayscale(100%);
		min-height: 60px;
		overflow: hidden;
		transform: scale(.8);
		transform-origin: bottom left;
	}
}

.frm {
	&-error {
		@include font(frm-error);

		color: $error-color;
		display: block;
		margin-top: $base-size / 2;
	}
}

.control-label {
	display: block;
	font-weight: bold;
	margin-bottom: $base-size / 2;

	.has-error & {
		color: $error-color;
	}
}

.error { // TODO clean
	color: $error-color;
	display: block;
	font-weight: bold;
	margin-top: $base-size / 2;
	transform: scale(.75);
	transform-origin: left top;
}

.t3-form {
	position: relative;

	fieldset {
		border: 0;
		padding: 0;
	}

	input:invalid {
		box-shadow: none;
		outline: none;
	}

	.general-response {
		@include spacing-responsive-base;

		align-items: center;
		display: flex;
		padding: $base-size (2 * $base-size);

		&__icon {
			height: 2 * $base-size;
			margin-right: $base-size;
			width: 2 * $base-size;
		}

		&.error {
			background-color: transparentize($error-color, .9);
			border: 1px solid $error-color;
			color: $error-color;

			.general-response__icon {
				color: $error-color;
			}
		}

		&.success {
			background-color: transparentize($success-color, .9);
			border: 1px solid $success-color;
			color: $success-color;

			.general-response__icon {
				color: $success-color;
			}
		}

		&.info {
			background-color: transparentize($black-38-color, .9);
			border: 1px solid $black-38-color;
			color: $black-60-color;

			.general-response__icon {
				color: $black-60-color;
			}
		}
	}

	.clearfix {
		.dtl-title {
			margin-top: $base-size * 3;
		}
	}

	.form-group {
		margin: $base-size 0 ($base-size * 2);
		position: relative;


		input[type=text], input[type=password], input[type=email] {
			background-color: $grey-light-color;
			font: inherit;
			height: $base-size * 3;
			padding-left: $base-size / 2;
			width: 100%;
		}

		&--hint{
			input {
				position: relative;
				z-index: 1;
			}

			label {
				position: relative;
				z-index: 2;
			}

			.input {
				overflow: hidden;
			}
		}

		.pwd-hint {
			border-color: $lines-global;
			border-style: solid;
			border-width: 0px;
			display: block;
			max-height: 0;
			overflow: hidden;
			padding: 0 .5rem;
			transform: translateY(-100%);
			transition: transform .6s, max-height .6s, border-width 0s .6s;

			&--match {
				color: green;
			}

			&__body {
				margin: .5rem 0;
			}
		}

		&.is-writing .pwd-hint {
			border-width: 1px;
			max-height: 500px;
			transform: translateY(0);
			transition: transform .6s, max-height .6s;
		}

		.show-password {
			@include font(small);

			color: $black-75-color;
			cursor: pointer;
			position: absolute;
			right: 0;
			top: 0;

			&:hover {
				color: $black-38-color;
			}
		}

		textarea {
			// Stop Safari Mobile from giving input buttons rounded corners
			-webkit-appearance: none;

			background-color: $grey-light-color;
			border: none;
			border-radius: 0;
			font: inherit;
			height: $base-size * 10;
			padding-left: $base-size / 2;
			padding-top: $base-size / 2;
			resize: none;
			width: 100%;
		}

		.inp-select {
			width: 100%;
		}

		input:focus, textarea:focus {
			background-color: $grey-light-color;
			outline: none;
		}

		.inputs-list .form-group {
			display: flex;
			margin: 0;

			.form-check {
				margin-right: $base-size;
			}
		}

		.form-check-label {
			input {
				margin-right: $base-size / 2;
			}
		}

		&.floating-label {
			margin-top: 0;

			.control-label {
				color: $placeholder-global-color;
				font-style: italic;
				font-weight: normal;
				margin-bottom: 0;

				//opacity: 0.5;
				pointer-events: none;
				transform: translate3d($base-size / 2, 2.25em, 0) scale(1);
				transform-origin: left top;
				transition: 240ms;
			}

			&.focused-or-filled .control-label {
				font-style: normal;
				opacity: 1;
				text-transform: uppercase;
				transform: scale(.75);
			}

			::-webkit-input-placeholder {
				/* Chrome/Opera/Safari */
				color: transparent;
			}

			::-moz-placeholder {
				/* Firefox 19+ */
				color: transparent;
			}

			:-ms-input-placeholder {
				/* IE 10+ */
				color: transparent;
			}
		}

		&.has-error {
			.pwd-hint {
				border-top-width: 0;
			}

			input[type=text], input[type=password], input[type=email], textarea {
				border-bottom: 2px solid $error-color;
			}
		}
	}

	.actions {
		margin-bottom: $base-size;
	}

	.hint {
		@include spacing-responsive-base("margin", "top");
	}
}

