.btc-bar {
	align-items: center;
	display: flex;
	justify-content: center;

	&__width-adjust {
		align-items: stretch;
		display: inline-flex;
		flex-direction: column;
		flex-grow: 1;
		justify-content: center;
	}

	.btn {
		margin-top: $base-size;

		&:first-of-type {
			margin-top: 0;
		}
	}

	&__text {
		margin: 0;

		&:first-child {
			margin-bottom: 2rem;
		}

		&:last-child {
			margin-top: 2rem;
		}
	}

	@include media(sm) {
		&__width-adjust {
			flex-direction: row;
		}

		.btn {
			margin: 0 1rem;

			&:first-child {
				margin-left: 0;
			}

			&:last-child {
				margin-right: 0;
			}
		}

		&__text {
			max-width: 50%;

			&:first-child {
				margin: 0;
				margin-right: 2rem;
			}

			&:last-child {
				margin: 0;
				margin-left: 2rem;
			}
		}
	}

	&--left {
		.btc-bar__width-adjust {
			justify-content: flex-start;
			width: 100%;
		}
	}

	&--right {
		.btc-bar__width-adjust {
			justify-content: flex-end;
			width: 100%;
		}
	}

	&--space-between {
		@include media(sm) {
			.btc-bar__width-adjust {
				justify-content: space-between;
				width: 100%;
			}
		}
	}

	&--vertical-center {
		.btc-bar__width-adjust {
			align-items: center;
		}
	}
}

.btc {
	&-round-label {
		display: block;
		max-width: 110px;
		text-align: center;
		width: calc(#{$srv-icon-size} + 5%);

		&__button {
			background-color: getThemeColorValue("btn-round-label__bg");
			color: getThemeColorValue("btn-round-label__fg");
			display: inline-block;
			height: $srv-icon-size;
			margin-bottom: $srv-icon-margin-bottom;
			overflow: hidden;
			transition: $hover-transition;
			width: $srv-icon-size;
		}

		&__label {
			@include font(srv-button-text);

			color: $black-extra-light-color;
			display: block;
			overflow: hidden;
			text-align: center;
			text-overflow: ellipsis;
			text-transform: uppercase;
			white-space: nowrap;
			width: 100%;

			@include media(lg) {
				hyphens: auto;
				white-space: normal;
			}
		}

		&--small {
			max-width: 80px;
			width: calc(#{$btn-round-small} + 5%);

			.btc-round-label {
				&__button {
					height: $btn-round-small;
					width: $btn-round-small;
				}

				&__label {
					font-size: 9px;
				}
			}
		}

		// BUTTON and LABEL hover and active with variants
		// TODO II refactor as --bright
		&:hover &__button, &--active &__button {
			background-color: getThemeColorValue("header__bg");
			color: getThemeColorValue("header__fg");
		}

		&:hover &__label, &--active &__label {
			color: getThemeColorValue("btn-round-label--hover__label");
		}

		&--secondary &__button {
			background-color: $grey-medium-color;
			color: getThemeColorValue("srv-button-icon--secondary__fg");
		}

		&--secondary:hover &__button, .srv-button--active.srv-button--secondary &__icon {
			background-color: getThemeColorValue("srv-button-icon--secondary--active__bg");
			color: getThemeColorValue("srv-button-icon--secondary--active__fg");
		}

		&--header {
			.btc-round-label__button{
				background-color: getThemeColorValue("header__bg");
				color: getThemeColorValue("header__fg");
			}
			.btc-round-label__label{
				color: getThemeColorValue("header__text");
			}

			@include hover {
				// disable hover
				.btc-round-label__button {
					background-color: getThemeColorValue("header__bg");
					color: getThemeColorValue("header__fg");
				}
				.btc-round-label__label {
					color: getThemeColorValue("header__text");
				}
			}
		}
	}
}

.btncmp-round-label {
	align-items: center;
	cursor: pointer;

	// when using a replacement for button like div the pointer may not be set by the browser
	display: flex;
	flex-direction: column;
	margin: 0 ($btn-round-size / 4);
	width: $btn-round-size;

	.btn-round {
		background-color: getThemeColorValue("btn-round-label__bg");
		color: getThemeColorValue("btn-round-label__fg");
		transition: $hover-transition;
	}

	&__label {
		display: none;
	}

	html:not(.can-touch) & {
		&:hover {
			.btn-round {
				background-color: getThemeColorValue("header__bg");
				color: getThemeColorValue("header__fg");
			}
		}
	}

	@include media(sm) {
		margin: 0 ($btn-round-size / 2);
		overflow: visible;

		&__label {
			@include font(srv-button-text);

			color: getThemeColorValue("btn-round-label__label");
			display: block;
			margin-top: $base-size / 4;
			max-width: $btn-round-size * 2;
			overflow: hidden;
			padding: 0 ($base-size / 4);
			text-overflow: ellipsis;
			text-transform: uppercase;
			white-space: nowrap;
		}

		html:not(.can-touch) & {
			&:hover {
				.btncmp-round-label__label {
					color: getThemeColorValue("btn-round-label--hover__label");
				}
			}
		}
	}
}

.cnt {
	&-contact {
		@include spacing-responsive-base;

		border-color: $lines-global;
		border-style: solid;
		border-width: $dtl-contact-border-width;
		padding: $spacing-vertical-base 0;

		&__content p {
			margin-top: 0;

			&:last-of-type {
				margin-bottom: 0;
			}
		}

		&__logo {
			margin-bottom: $dtl-contact-logo-margin-bottom;
			max-width: $dtl-contact-max-width;

			&::before {
				// change container proportions
				padding-top: .43 * 100%;
			}
		}

		@include media(sm) {
			&__logo {
				display: inline-block;
				margin-bottom: 0;
				margin-right: $dtl-contact-margin-right;
				width: $dtl-contact-logo-width;

				&::before {
					// change container proportions
					padding-top: .73 * 100%;
				}
			}

			&__content {
				display: inline-block;
				vertical-align: top;
			}
		}
	}
}

.cookie-disclaimer {
	background-color: $cookie-disclaimer-bg;
	border-top: 1px solid $cookie-disclaimer-border;
	bottom: 0;
	left: 0;
	position: fixed;
	transform: translate3d(0, 100%, 0);
	transition: transform .666s ease-out;
	width: 100%;
	z-index: $z-index-cookie-disclaimer;

	&.is-displayed {
		transform: translate3d(0, 0, 0);
	}

	&__container {
		@include make-container;

		align-items: center;
		display: flex;
		flex-direction: column;
		padding-bottom: $base-size;
		padding-top: $base-size;

		@include media(sm) {
			flex-direction: row;
		}
	}

	&__hint {
		color: transparentize(color-contrast($cookie-disclaimer-bg, $white-color, $black-color), .2);
		max-width: 100%;

		@include media(sm) {
			margin-right: $base-size;
		}
	}

	.btn {
		flex: none;
		margin-top: $base-size;

		@include media(sm) {
			margin-left: auto;
			margin-top: 0;
		}
	}

	.text-link {
		color: $white-color;
	}
}

.fltr {
	&-menu {
		@include spacing-responsive-base;

		background-color: $grey-light-color;
		border-bottom: $fltr-menu-border-bottom solid $lines-global;

		&.has-submenu {
			margin-bottom: 0;
		}
	}

	&-container {
		@include make-container;
	}

	&-container-list {
		display: none;
		position: relative;

		html.js & {
			overflow: hidden;

			.fltr-list {
				&::-webkit-scrollbar {
					display: none;
				}

				//will hopefully work in the future
				overflow: -moz-scrollbars-none;
			}

			.fltr-nav {
				visibility: visible;
			}

			&:not(.fltr-container-list--has-overflow) {
				.fltr-nav {
					visibility: hidden;
				}
			}

			&:not(.fltr-container-list--overflow-prev) {
				.fltr-nav__prev {
					visibility: hidden;
				}
			}

			&:not(.fltr-container-list--overflow-next) {
				.fltr-nav__next {
					visibility: hidden;
				}
			}
		}
	}

	&-list {
		-webkit-overflow-scrolling: touch;
		display: flex;
		overflow-x: scroll;
		overflow-y: hidden;
		padding: 0px;
		position: relative;

		&__item {
			@include font(fltr-list);

			border-bottom: $fltr-list-item-border-width solid transparent;
			color: $black-medium-color;
			cursor: pointer;
			display: block;
			padding: $fltr-list-item-padding;
			text-align: center;
			text-transform: uppercase;
			white-space: nowrap;

			//preserve the list from resizing on hover
			//https://stackoverflow.com/questions/556153/inline-elements-shifting-when-made-bold-on-hover#answer-20249560
			&::after {
				color: transparent;
				content: attr(title);
				display: block;
				font-weight: bold;
				height: 1px;
				overflow: hidden;
				visibility: hidden;
			}

			&:hover, &--active {
				color: $primary-color;
				font-weight: bold;
			}

			&--active {
				border-bottom-color: $primary-color;
			}
		}
	}

	&-container-dropdown {
		background-color: $grey-light-color;
		text-align: center;
	}

	&-dropdown {
		background-color: $white-color;
		margin: $fltr-dropdown-margin auto;
		max-width: $fltr-dropdown-max-width;
		width: 90%;
	}

	&-nav {

		align-items: center;
		cursor: pointer;
		display: flex;
		font-style: normal;
		height: 100%;
		position: absolute;
		top: 0px;
		visibility: hidden;

		&::before, &::after {
			display: block;
		}

		&__prev {
			left: 0px;
		}

		&__next {
			right: 0px;
		}

		.icn-icon {
			font-size: 1.5em;
		}
	}

	@include media(sm) {
		&-container-list {
			display: block;
		}

		&-dropdown {
			display: none;
		}
	}
}

.fltr {
	&-submenu {
		@include spacing-responsive-base;

		background-color: $white-color;
		text-align: center;

		&__list {
			@include reset-list;
			@include make-container;

			display: none;
			flex-flow: row wrap;
		}

		&__item {
			@include font(fltr-submenu);

			color: $black-medium-color;
			margin: 1rem 1rem .25rem 0;
			padding-right: 1rem;
			text-transform: uppercase;

			&:not(:last-child) {
				border-right: 1px solid $black-light-color;
			}

			a {
				border-bottom: 2px solid transparent;

				//preserve the list from resizing on hover
				//https://stackoverflow.com/questions/556153/inline-elements-shifting-when-made-bold-on-hover#answer-20249560
				&::after {
					color: transparent;
					content: attr(title);
					display: block;
					font-weight: bold;
					height: 1px;
					overflow: hidden;
					visibility: hidden;
				}

				html:not(.can-touch) & {
					&:hover {
						border-bottom: 2px solid $primary-color;
						color: $primary-color;
						font-weight: 600;
					}
				}
			}

			&--active {
				a {
					border-bottom: 2px solid $primary-color;
					color: $primary-color;
					font-weight: 600;
				}
			}
		}

		&__dropdown {
			display: inline-block;
			justify-content: center;
		}

		@include media(sm) {
			&__list {
				display: flex;
				padding-top: $fltr-submenu-list-padding-top;
			}

			&__dropdown {
				display: none;
			}
		}
	}
}

.ftsh {
	&-area {
		background-color: $grey-light-color;
		position: relative;
		z-index: $z-index-filter-area;

		// above following content

	}

	&-container {
		@include make-container;

		display: flex;
		flex-direction: column;
	}

	&-control {
		display: block;
		line-height: 2rem;
		margin: 1rem .5rem;
	}

	&-control__search {
		margin-bottom: 2rem;
	}

	&-control__dropdown {
		color: $black-60-color;
	}

	@include media(sm) {
		&-container {
			align-items: flex-start;
			flex-direction: row;
		}

		&-controls {
			display: flex;
			flex: 1 1 0px;
			flex-wrap: wrap;
		}

		&-control__search {
			margin-bottom: 1rem;
			width: 100%;
		}

		&-control__dropdown {
			flex: 1 1 0px;
		}

		&-ribbons {
			display: flex;
			margin-left: 1rem;
		}

		&-ribbon {
			margin: -1rem .5rem 0;
			min-height: 4.25rem;
		}
	}

	&-result {
		&__title {
			@include make-container;
			@include font(src-title);

			color: $grey-color;
			margin-bottom: 2rem;
			text-transform: uppercase;
		}

		&__loading-indicator {
			@include make-container;

			text-align: center;

			.icn-spinner {
				display: inline-block;
				height: 50px;
				stroke: $primary-color;
				width: 50px;
			}
		}
	}

	@include media(md) {
		&-controls {
			flex-wrap: nowrap;
		}

		&-control__search {
			flex: 1.5 1 0px;
			width: auto;
		}
	}
}

.info-box {
	background-color: transparentize($white-color, .65);
	color: currentColor;
	opacity: .9;
	padding: $base-size $base-size $base-size ($base-size * 3.5);
	position: relative;

	.icn--svg {
		height: $base-size * 1.5;
		left: $base-size;
		position: absolute;
		top: $base-size;
		width: $base-size * 1.5;
	}

	&__text {
		@include font(emph);

		text-transform: uppercase;

		&--light {
			@include font(small);
		}
	}
}

.mnu {
	&-sublist {
		@include font(h6);
		@include reset-list;

		background-color: getThemeColorValue("mnu-sublist__bg");
		color: getThemeColorValue("mnu-sublist__text");

		&__item {
			> a {
				color: inherit;
				display: block;
				padding: $mnu-sublist-item-padding;
				transition: color $hover-transition, border-color $hover-transition;
			}

			&--active {
				background-color: $black-transparent-color;
			}

			&--accent {
				text-transform: uppercase;
			}
		}

		@include media(md) {
			@include font(menu-sublist);

			display: flex;
			flex-wrap: wrap;
			justify-content: center;
			text-transform: uppercase;

			&__item {
				flex: 0 1 auto;
				margin: $mnu-sublist-item-margin-v-md 0;
				max-width: 19em;
				text-align: center;

				> a {
					padding: $mnu-sublist-item-padding-md;
				}

				&:not(:first-child) {
					border-left: 1px solid $grey-color;
				}

				&--active {
					background-color: transparent;

					> a {
						text-decoration: underline;
					}
				}

				html:not(.can-touch) & {
					&:hover {
						color: getThemeColorValue("mnu-sublist--hover__text");

						> a {
							border-bottom-color: $black-light-color;
						}
					}
				}
			}
		}
	}
}

