sections.css:
.mnu {
	&-list {
		@include font(menu-list);
		@include reset-list;

		&__btn-sublist {
			background-color: getThemeColorValue("mnu-btn-sublist__bg");
			color: getThemeColorValue("mnu-btn-sublist__color");
			font-size: $mnu-btn-sublist-font-size;
			margin: $mnu-btn-sublist-margin-v 0;
			min-width: $menu-list-btn-sublist-min-width;

			//transition: transform $menu-list-transition;
			svg {
				transition: transform $menu-list-transition;
			}

			.mnu-sublist-active & svg {
				transform: perspective(500px) rotateZ(-180deg);
			}
		}

		&__item:not(:last-child) {
			border-bottom: 1px solid;
			border-color: getThemeColorValue("mnu-list-item__border");

			&.mnu-sublist-active {
				border-bottom: 0;
			}
		}

		&__label {
			display: flex;
			position: relative;

			> a {
				color: getThemeColorValue("header__text");
				flex: 1 0 auto;
				padding: $menu-list-label-padding;
				transition: color $hover-transition, border-color $hover-transition;
				white-space: nowrap;
			}

			&--active > a {
				color: $secondary-color;
			}
		}

		&__label:only-child &__btn-sublist {
			display: none;
		}

		&__sublist {
			max-height: 0;
			opacity: 0; // IE media query support is poor
			overflow: hidden;
			transition: max-height $mnu-sublist-transition;

			.mnu-sublist-active & {
				max-height: 400px;
				opacity: 1;
				overflow: initial;
			}
		}

		@include media(md) {
			display: flex;
			justify-content: flex-end;
			text-transform: uppercase;

			&__btn-sublist {
				display: none;
			}

			&__item {
				transition: color $default-transition;

				&:not(:last-child) {
					border-bottom: 0;
				}
			}

			&__label {
				overflow-y: hidden;
				padding: $menu-list-label-padding-md;

				> a {
					border-bottom: 2px solid transparent;
					color: getThemeColorValue("header__text");
					padding-bottom: $base-size / 16;
				}

				&--active > a {
					border-bottom: 2px solid getThemeColorValue("header__text");
				}

				// the small gray arrow when opening a sublist
				&::after {
					border: 8px solid transparent;
					border-bottom: 8px solid getThemeColorValue("mnu-sublist__bg");
					bottom: -8px;
					content: "";
					height: 1px;
					left: calc(50% - 8px);
					position: absolute;
					transition: all $mnu-sublist-transition;
					width: 1px;
				}
			}

			&__sublist {
				max-height: none;
				opacity: 0;
				position: absolute;
				right: 0;
				transition: opacity $mnu-sublist-transition $mnu-sublist-delay, visibility $mnu-sublist-transition $mnu-sublist-delay;
				visibility: hidden;
				width: 100vw;
			}

			// close the sublist w/o delay if another item is hovered
			&:hover &__item:not(:hover) &__sublist {
				transition: opacity $mnu-sublist-transition .05s, visibility $mnu-sublist-transition .5s;
			}

			&__item:hover &__sublist {
				opacity: 1;
				transition: opacity $mnu-sublist-transition .05s, visibility $mnu-sublist-transition .05s;
				visibility: visible;
			}

			&__item:hover &__label {
				> a {
					color: getThemeColorValue("header--hover__text");
				}

				&--active > a {
					border-bottom-color: getThemeColorValue("header--hover__text");
				}

				&:not(:only-child)::after {
					transform: translateY(-8px);
				}
			}
		}
	}
}

.mnu {
	&-infolist {
		@include font(infolist);
		@include reset-list;

		color: getThemeColorValue("header__text");

		&__item {
			border-bottom: 1px solid getThemeColorValue("mnu-list-item__border");
			padding: $mnu-infolist-item-padding;

			&:last-of-type {
				border-bottom: 0;
			}

			html:not(.can-touch) & {
				.btn:hover {
					color: getThemeColorValue("header--hover__text");
				}
			}

			.btn__icon {
				color: getThemeColorValue("mnu-infolist-icon__fg");
			}
		}

		&__icon-search {
			display: none;
			font-size: 2rem;
		}

		&__search {
			display: none;
			position: relative;

			// autosuggest
			.inp-dropdown {
				bottom: 0;
				position: absolute;
				right: 0;
				transform: translateY(100%) translateY(.25rem);
				width: 320px;
			}
		}

		@include media(md) {
			align-items: center;
			display: flex;
			justify-content: flex-end;

			&__item {
				margin: 0 $infolist-item-margin-h-md;
				padding: 0;

				&:not(:last-child) {
					border-bottom: 0;
				}

				.btn__icon {
					color: inherit;
				}
			}

			&__icon-search {
				cursor: pointer;
				display: block;
			}
		}


		@include media(lg) {
			&__icon-search {
				display: none;
			}

			&__search {
				border: 1px solid getThemeColorValue("header__border");
				display: block;
				position: relative;
				top: -1px;
			}
		}
	}
}

.mnu {
	&-top {
		@include media(md) {
			align-items: center;
			display: flex;
			justify-content: flex-end;
		}
	}
}

.mnu {
	&-lang {
		@include font(infolist);
		@include reset-list;

		color: getThemeColorValue("header__text");

		&__item {
			display: table-cell;
			padding: 0 $mnu-lang-item-padding-h;

			> a {
				color: inherit;
				transition: color $hover-transition;

				@include hover {
					color: getThemeColorValue("header--hover__text");
				}
			}

			&--active {
				color: transparentize(getThemeColorValue("header__text"), .4);
			}

			&:not(:last-child) {
				border-right: 2px solid getThemeColorValue("header__text");
			}
		}

		@include media(md) {
			&__item {
				padding: 0 $mnu-lang-item-padding-h-md;
			}
		}
	}
}

.mnu {
	&-search {
		@include font(mnu-search);

		background-color: $white-color;
		border-radius: 3px;
		color: $black-60-color;
		height: 2rem;
		padding: 0 .5rem;

		&__input {
			@include placeholder($placeholder-global-color, italic);

			font-family: inherit;
			font-size: inherit;
			height: 100%;
			width: 220px;

			&:focus {
				outline: none;
			}
		}

		&__dropdown {
			z-index: 50;
		}

		&__button {
			font-size: 1.5em;
			vertical-align: middle;
		}
	}
}

.mnu {
	&-infolist-xs {
		border-color: getThemeColorValue("mnu-list-item__border");
		border-style: solid;
		border-width: 1px 0;
		display: flex;
		margin: auto;
		padding: 1rem 0;

		&__item {
			flex-grow: 1;
			max-width: 90px;
			padding: 2px;
		}
	}
}

.mnu {
	&-menu {
		background-color: getThemeColorValue("mnu__bg");
		color: getThemeColorValue("header__text");
		height: 100%;
		padding: $menu-padding $menu-padding 3rem;
		transition: transform ease .4s;
		width: 320px;

		&__lang {
			margin-left: -$mnu-lang-item-padding-h;
		}

		&__list {
			flex: 1 1 100%;
			margin-bottom: $menu-list-margin-bottom;
		}

		&__sublist {
			box-sizing: content-box;
			margin-left: -$menu-padding;
			margin-right: -$menu-padding;
		}

		&__title {
			@include font(h2);

			margin-top: 0;
			max-width: 60%;
		}

		&__top {
			justify-content: center;
		}

		@include media(md) {
			align-items: flex-end;
			background-color: transparent;
			display: flex;
			flex-flow: column nowrap;
			justify-content: space-between;
			padding: 0;
			position: static;
			transition: none;
			width: auto;

			&__lang {
				margin-left: 0;
			}

			&__list {
				align-self: flex-end;
				flex: 0 0 auto;
				margin-bottom: 0;
				margin-right: -1em;
				order: 1;
			}

			&__sublist {
				margin: 0;
			}

			&__title {
				display: none;
			}

			&__top {
				justify-content: flex-end;
			}
		}
	}
}

.msg {
	&-message {
		@include message-box;

		position: relative;
		width: 100%;

		&--info {
			@include message-box-colors($info-color, $info-text-color);
		}

		&--error {
			@include message-box-colors($error-color, $error-color);
		}

		&--success {
			@include message-box-colors($success-color, $success-color);
		}

		&--default {
			@include message-box-colors($black-60-color, $black-60-color);
		}

		&--no-borders {
			border-color: transparent;
		}

		&__btn--close {
			cursor: pointer;
			position: absolute;
			right: 1rem;
			top: 1rem;
		}
	}
}

.tss {
	background-color: $primary-color;
	display: block;
	height: $tss-teaser-height;
	overflow: hidden;
	position: relative;

	@include media(sm) {
		height: $tss-teaser-height-sm;
	}

	// dimmer
	&::before {
		background-color: hsla(0, 100%, 0%, .2);
		content: "";
		display: block;
		height: 100%;
		position: absolute;
		width: 100%;
	}

	&:hover::before {
		background-color: hsla(0, 100%, 0%, .4);
	}

	&__image {

		// IE polyfill
		font-family: 'object-fit: cover;';
		height: 100%;
		object-fit: cover;
		width: 100%;
	}

	&__container {
		@include font(tss-teaser);

		color: $white-color;
		left: 0%;
		padding: 2rem;
		position: absolute;
		text-align: center;
		text-shadow: 0 0 8px hsla(0, 0%, 0%, .4);
		top: 50%;
		transform: translateY(-50%);
		width: 100%;


		.btn-round {
			background-color: $white-light-transparent-color;
			box-shadow: 0 0 4px hsla(0, 0%, 0%, .8);
			color: getThemeColorValue("dark-color");
			display: block;
			margin: auto;
			margin-bottom: $tss-icon-margin-bottom;
		}
	}

	&__name {
		@include font(tss-name);

		text-transform: uppercase;
	}
}

// aku, 2018-04-19
// fix
// couldn't fix it with the default style in row 35, cause it was overwritten somewhere???
// see ticket https://jira.aquarius.digital/browse/ECQS-716
.srv-wall__item .tss {
	background-color: $primary-color !important;
}

// Keyframe have to be a single line -> gulp-color
@keyframes teaser-hover { from { opacity: .001; visibility: hidden; } to { opacity: 1; visibility: visible; } }

.tsr-shop {
	overflow: hidden;
	position: relative;

	&__overlay {
		background-color: $teaser-overlay-color;
		display: flex;
		flex-direction: column;
		height: 100%;
		justify-content: center;
		left: 0;
		opacity: .001;
		overflow: hidden;
		position: absolute;
		top: 0;
		visibility: hidden;
		width: 100%;
	}

	&__name {
		@include font(h5);

		align-items: center;
		color: $white-color;
		display: flex;
		flex: 1 1 0px;
		justify-content: center;
		margin: auto;
		text-align: center;

		a {
			display: inline-block;
			padding: 5px;
			white-space: normal;
		}
	}

	&__links {
		border-top: 1px solid $white-color;
		display: flex;
		margin-top: auto;
		padding: 5px 0;
		position: relative;
		transform: translateY(100%);
		transition: transform $default-transition;
		width: 100%;
	}

	&__link {
		flex: 1 1 0px;
		margin: 0 5px;
		text-align: center;

		// vertical bar between the links
		&:not(:last-child)::before {
			color: $white-color;
			content: '|';
			left: 50%;
			position: absolute;
			top: 50%;
			transform: translate(-50%, -50%);
		}

		@include hover {
			.btn__label {
				color: getThemeContrast($teaser-overlay-color, "tsr-shop-label--hover__text");
			}
		}

		.btn {
			color: transparentize(color-contrast($teaser-overlay-color, black, white), .2);// tansparentize($white-color, .2), transparentize($secondary-color, .2));
			font-size: 12px;

			&__icon {
				color: $secondary-color;
			}

			&__label {
				display: none;

				@include media(md) {
					display: inline-block;
				}
			}
		}
	}

	&:hover {
		.tsr-shop__overlay {
			animation: teaser-hover .1s 1;
			animation-fill-mode: forwards;
		}

		.tsr-shop__links {
			transform: translateY(0);
		}
	}


	// Used in Shop Banderole (_teaser-group.scss)
	// Buttons without label
	&--banderole {
		.btn__label {
			@include media(md) {
				display: none;
			}
		}
	}
}

.tsr-solid {
	border: 1px solid $lines-global;
	display: flex;
	flex-direction: column;
	margin-bottom: 1px; // prevent line disappearing in Windows / Chrome
	position: relative;
	width: 100%;

	.tsr__container {
		background: linear-gradient(to top, getThemeColorValue("tsr__underline") $tsr-hover-translate-distance, $teaser-fallback-color $tsr-hover-translate-distance);
		display: flex;
		flex-direction: column;
		transition: $hover-transition;
	}

	.tsr__content {
		background-color: getThemeColorValue("tsr-content__bg");
		border: 0 solid $lines-global;
		border-top-width: 1px;
		color: $black-medium-color;
		flex-grow: 1;
	}

	.tsr__media {
		filter: brightness(100%);
		transition: $hover-transition;
	}

	html:not(.can-touch) & {
		&:hover {
			.tsr__media {
				filter: brightness(85%);
			}
		}
	}

	&--columns-2 {
		@include media(md) {
			.tsr__content {
				display: flex;
			}

			.tsr__header {
				padding-right: $tsr-press-padding;
				width: 36%;
			}

			.tsr__text {
				max-width: 100%; // IE grows it too much otherwise (with much text)
				padding-left: $tsr-press-padding;
			}
		}
		@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
			.tsr__header {
				width: auto;
			}
		}
	}

	&--content {
	}

	&--news {
		.tsr__content {
			background-color: getThemeColorValue("tsr--dark__bg");
			border-color: getThemeColorValue("tsr--dark__bg");
			color: $white-color;
		}

		.tsr__container {
			background: linear-gradient(to top, getThemeColorValue("tsr--dark__underline") $tsr-hover-translate-distance, $teaser-fallback-color $tsr-hover-translate-distance);
		}

		.tsr__category {
			background-color: getThemeColorValue("tsr-category--dark__bg");
			color: getThemeColorValue("tsr-category--dark__text");
		}
	}

	&--offer {
		.tsr__date {
			font-size: 14px;
			text-transform: uppercase;
		}
	}

	&--job {
		.shl-logo {
			background-color: $white-color;
			border-width: 0;
		}
	}

	&--ad {
		border: 0;

		.tsr__container {
			background: transparent; // -color -> included from gulp-extractcolor.js
		}

		.tsr__placeholder::before {
			padding-top: 3.6 / 3 * 100%;
		}
	}

	&--placeholder {
		pointer-events: none;

		.shl-container {
			color: $placeholder-paragraph-color;
		}

	}

	&--blurred {
		pointer-events: none;

		.tsr__media {
			color: $placeholder-paragraph-color;
			filter: blur(5px);
			overflow: hidden;
		}

		.tme-placeholder-block {
			box-shadow: none;
			color: transparent;
			text-shadow: 0 0 15px black;
		}
	}
}

.tsr-transparent {
	display: flex;
	margin-bottom: 1px; // prevent line disappearing in Windows / Chrome
	min-height: $tsr-transparent-height;
	position: relative;
	width: 100%;

	// floating label for comm. group Reason
	&__category-floating {
		display: none;
	}

	&__container {
		display: flex;
		height: auto;
		position: relative;
		transition: $hover-transition;
	}

	&__content {
		bottom: 0;
		left: 0;
		position: absolute;
		transition: $hover-transition;
		width: 100%;
	}

	&__media {
		display: flex;
		filter: brightness(100%);
		height: auto;
		overflow: hidden;
		transition: $hover-transition;
		width: 100%;

		img {
			// IE polyfill
			font-family: 'object-fit: cover;';
			height: 100%;
			object-fit: cover;
			width: 100%;
		}

		picture {
			display: flex;
			width: 100%;
		}

		video {
			height: 100%;
			left: 50%;
			position: absolute;
			transform: translateX(-50%);
			width: auto;
		}
	}

	html:not(.can-touch) & {
		&:hover {
			.tsr-transparent__media {
				filter: brightness(85%);
			}
		}
	}


	@include media(sm) {
		height: auto;
		min-height: $tsr-transparent-min-height-sm;
	}


	&--columns-2 {
		@include media(md) {
			.tsr__content {
				transform: translate(25px, -25px);
				width: 50%;
			}
		}
	}

	&--columns-3 {
		@include media(md) {
			.tsr__content {
				transform: translate(25px, -25px);
				width: 35%;
			}
		}
	}

	&--event {
		.tsr__category {
			background-color: getThemeColorValue("tsr-category--dark__bg");
			color: getThemeColorValue("tsr-category--dark__text");
		}

		.tsr__container {
			background-color: $primary-color;
		}

		.tsr__content {
			background-color: getThemeColorValue("tsr-content--event__bg");
			color: $white-color;
		}
	}

	&--blog {
		.tsr__container {
			background-color: $white-color;
		}

		.tsr__content {
			background-color: $white-light-transparent-color;
		}
	}
}

.tsr {
	&__actions {
		position: absolute;
		right: .5rem;
		top: .5rem;
	}

	&__action {
		background-color: $white-color;
		color: $black-medium-color;
		margin-left: .5rem;
	}

	&__placeholder {
		overflow: hidden;

		.tsr-solid &::before {
			content: "";
			display: block;
			padding-top: 9 / 16 * 100%;
			width: 100%;
		}

		.tsr-solid--columns-2 &::before {
			@include media(md) {
				padding-top: 1 / 3.5 * 100%;
			}
		}

		img, video {
			position: absolute;
			top: 0;
			width: 100%;
		}

		// Hide iOS play button for videos in teasers
		/* Old shadow dom for play button */
		video::-webkit-media-controls-play-button {
			-webkit-appearance: none;
			display: none!important;
		}

		/* New shadow dom for play button */
		video::-webkit-media-controls-start-playback-button {
			-webkit-appearance: none;
			display: none!important;
		}
	}

	&__author,
	&__time {
		@include font(tsr__author);

		opacity: .75;
		text-transform: uppercase;
	}

	&__category {
		@include font(tsr-category);

		background-color: getThemeColorValue("tsr-category__bg");
		color: getThemeContrast("tsr-category__bg", "tsr-category__text");
		flex-grow: 0;
		padding: $tsr-category-padding;
		text-transform: uppercase;
	}

	&__container {
		display: block;
		flex-grow: 1;
	}

	&__content {
		padding: $tsr-content-padding;
	}

	&__date {
		@include font(tsr-date);

		&--medium {
			@include font(h3);

			margin-bottom: .1rem;
		}

		&--big {
			@include font(h1);

			margin-bottom: .25rem;
		}
	}

	&__header__group {
		align-items: flex-start;
		display: flex;
		justify-content: space-between;
		margin-bottom: 6px;
	}

	&__media {
		background-size: cover;
		position: relative;
		// transform: translate3d(0, 0, 0); //Fix Safari Pixeled base64

		picture {
			animation: fade-in 250ms ease-in;
		}
	}

	&__text p {
		@include font(tsr-text);

		margin: 0;
	}

	&__text .tsr__paragraph--margin-small {
		margin: .5rem 0;
	}

	&__title {
		@include font(tsr-title);

		background-color: getThemeColorValue("tsr-title__bg");
		color: getThemeColorValue("tsr-title__text");
		margin-bottom: 0;
		text-transform: uppercase;
	}

	&--disabled {
		pointer-events: none;
	}

	&--disabled &__container{
		position: relative;
	}

	&--disabled &__container::before {
		background-color: rgba(239, 239, 239,.7);
		content: "";
		display: block;
		height: 100%;
		position: absolute;
		width: 100%;
		z-index: 100;
	}

	&--disabled &__media {
		filter: grayscale(1);
	}
}

.tsr-stage {
	background-color: $white-color;
	display: flex;
	max-height: responsiveVal($tsr-stage-media-size, xs);
	text-align: left;
	width: 100%;

	&__content {
		flex-grow: 1;
		padding: $tsr-stage-content-padding;
	}

	&__media {
		flex: 0 0 responsiveVal($tsr-stage-media-size, xs);
	}

	&__text {
		margin-bottom: 1em;
	}

	// make button to an invisible overlay in sm only
	@media (max-width: 768px - 1px) {
		.btn {
			bottom: 0;
			left: 0;
			opacity: 0;
			position: absolute;
			right: 0;
			top: 0;
		}

		.tsr__title {
			font-size: 15px;
			line-height: 1.3;
		}

		&__media img {
			max-height: 80px;
		}

		&__header::after {
			content: unset !important;  //theme 1 sets it
		}

		&__content {
			padding: .5rem;
		}
	}

	@include media(sm) {
		max-height: responsiveVal($tsr-stage-media-size, sm);

		&__content {
			padding: $tsr-stage-content-padding;
		}

		&__header {
			margin-bottom: 0;
		}

		&__media {
			flex-basis: responsiveVal($tsr-stage-media-size, sm);
		}
	}
}

/*
Placeholder

On inline elment, can be used to fake content. E.g. Product teaser.
*/

.tsr-placeholder {
	box-decoration-break: clone;
	box-shadow: inset 0 0 0 2em $placeholder-paragraph-color;
	color: transparent;
}

html.js {
	.lazy-load {
		img, video {
			opacity: 0;
			transition: opacity .666s ease-in-out;

			&.lazy-load-complete {
				background-color: white; //fix IE rounding error
				opacity: 1;
			}
		}
	}
}

.tsr-horizontal {
	.tsr__container {
		align-items: flex-start;
		background: none; // -color
		display: flex;
	}

	.tsr__content {
		border: none;
		flex-grow: 1;
		padding-top: 0;
	}

	.tsr__category {
		// overwrite theme rules
		position: static !important;
		transform: none !important;
	}

	.tsr__media {
		background-size: initial;
		flex: 30% 0 0;
		position: relative;
	}

	.tsr__text p {
		margin: .25rem 0;
	}

	&--small {
		.tsr__category {
			font-size: 10px;
		}

		.tsr__content {
			padding: 0 .75rem .75rem .75rem;
		}

		.tsr__title {
			@include font(h6);

			margin-bottom: .25rem;
		}

		.tsr__text p {
			@include font(small);

			margin: 0;
		}
	}
}

.dtl-info {
	@include make-content-container;
	@include spacing-responsive-base;
	@include section-padding;

	border-bottom: 1px solid $lines-global;

	&__title {
		@include make-col-span(12);
	}

	&__col-1 {
		margin-bottom: $spacing-vertical-base;
	}

	@include media(sm) {
		@include make-content-container(10px);
		@include make-row(0);

		&__col-1 {
			@include make-col;
			@include make-col-span(4);
		}

		&__col-2 {
			@include make-col(0);
			@include make-col-span(8); // opening-hours has padding from its content
		}
	}

	&--3-cols & {
		&__col-1 {
			margin-bottom: $spacing-vertical-base;
		}

		&__col-3 {
			@include make-col(0);
			@include make-col-span(12);
		}

		@include media(sm) {
			&__col-1, &__col-2 {
				@include make-col-span(6);
			}

			&__col-3 {
				@include make-col;
			}
		}

		@include media(md) {
			&__col-1, &__col-2 {
				@include make-col-span(3);
			}

			&__col-3 {
				@include make-col-span(6);
			}
		}
	}

	&--3-cols {
		@include media(sm) {
			@include make-container(10px);
		}
	}
}

.dtl-opening-hours {
	align-content: flex-start;

	@include media(sm) {
		@include make-row(0);

		&__title {
			@include make-col;
			@include make-col-span(12);
		}

		&__column {
			@include make-col;
			@include make-col-span(12);
		}

		// two columns side-by-side in desktop viewports
		&--2-cols &__column {
			// First and second cols have different width
			&--1 {
				@include make-col-span(7);
			}

			&--2 {
				@include make-col-span(5);
			}

			> *:first-child {
				margin-top: 0;
			}
		}
	}
}

.dtl-opening-hours {
	.dtl-opening-hours__info-box {
		background: $grey-medium-color;
		margin-left: 15px;
		margin-top: .75rem;
		width: 100%;
	}
}

.dtl__service-icon {
	@include spacing-responsive-base;

	background-color: getThemeColorValue("btn-solid__bg");
	display: block;
	height: $dtl-service-icon-size;
	margin: 0 auto;
	width: $dtl-service-icon-size;

	@include media(sm) {
		height: $dtl-service-icon-size-sm;
		width: $dtl-service-icon-size-sm;
	}
}

.dtl-map {
	@include section-margin;

	position: relative;

	&__spinner {
		@include spinner-centered;

		color: $black-light-color;
		display: none;

		.mapplic-loading ~ & {
			display: block;
		}
	}
}

.dtl__section {
	@include make-content-container;
	@include spacing-responsive-inc;
}

.dtl-opening-table {
	@include spacing-responsive-base;

	td {
		padding-bottom: .5rem;
		vertical-align: top;

		@include media-up(sm) {
			padding-bottom: 0;
		}
	}

	&__weekday,
	&__date,
	&__hours,
	&__shop-name {
		padding-right: $base-size;
	}

	&__shop-name {
		@include font(h5);
	}
}

.dtl-opening-page {
	@include make-content-container;
}

.dtl {
	&-ical {
		position: relative;
		top: -7px;

		&--sm-up {
			display: none;
		}

		@include media(sm) {
			display: none;
		}

		&--sm-up {
			@include media(sm) {
				display: block;
				float: right;
				top: -6px;
			}
		}

		&__icon {
			position: relative;
			top: 9px;

			.icn--svg {
				display: inline-block;
				svg {
					width: 1.75rem;
				}
			}
		}

		a {
			color: $grey-color;

			@include font(ical);

			html:not(.can-touch) & {
				transition: $hover-transition;
			}

			svg {
				color: $grey-color;

				html:not(.can-touch) & {
					transition: $hover-transition;
				}
			}

			@include hover {
				color: getThemeColorValue('dtl-ical__icon');

				svg {
					color: getThemeColorValue('dtl-ical__icon');
				}
			}
		}
	}
}

