.st404 {
	@include spacing-responsive-base;

	display: flex;
	min-height: $stf-main-min-height;
	position: relative;

	@include media(sm) {
		min-height: $stf-main-min-height-f-sm;

		&__overlay {
			min-height: $stf-main-min-height-f-sm;
		}
	}


	@include media(md) {
		min-height: $stf-main-min-height-f-md;

		&__overlay {
			min-height: $stf-main-min-height-f-md;
		}
	}


	@include media(lg) {
		min-height: $stf-main-min-height-f-lg;

		&__overlay {
			min-height: $stf-main-min-height-f-lg;
		}
	}


	&__bg-image {
		font-family: 'object-fit: cover; object-position: 50% 50%;';

		// IE polyfill
		height: 100%;
		object-fit: cover;
		object-position: 50% 50%;
		position: absolute;
		width: 100%;
	}

	&__overlay {
		background-color: $generic-overlay-color;
		min-height: $stf-main-min-height;
		position: relative;
		width: 100%;
		z-index: 1;
	}

	&__content-container {
		@include make-container;

		padding-top: responsiveVal($hdr-header-height, xs);

		@include media(md) {
			padding-top: responsiveVal($hdr-header-height, md);
		}
	}

	&__content {
		@include word-wrap;

		color: $white-color;
		max-width: 602px;
		padding: ($base-size * 3) 0;

		@include media(sm) {
			padding-top: $base-size * 10;
		}


		@include media(lg) {
			padding-top: $base-size * 11;
		}
	}

	&__headline {
		@include font(display);

		margin-bottom: $base-size * 2;
		text-transform: uppercase;

		@include media(sm) {
			margin-bottom: $base-size * 4;
		}
	}

	&__form {
		position: relative;
	}

	&__subheadline {
		@include font(h2);

		margin-bottom: $base-size * 3;
		text-transform: uppercase;

		@include media(sm) {
			margin-bottom: $base-size * 4;
		}
	}

	.inp-search {
		&__input {
			@include media(sm) {
				height: 45px;
			}
		}

		&__icon {
			@include media(sm) {
				height: 45px;
				width: 45px;
			}
		}
	}
}

.std {
	&-breadcrumbs {
		@include font(std-breadcrumb);
		@include reset-list;

		color: $black-38-color;

		&__item {
			display: inline-block;

			&:not(:last-child)::after {
				content: "";
				display: inline-block;
				padding: 0 $std-breadcrumbs-after-padding-h;
			}

			html:not(.can-touch) & {
				& > a:hover {
					color: $secondary-color;
					transition: color $default-transition;
				}
			}
		}
	}

	&-button {
		@include font(std-button);

		text-transform: uppercase;

		&:hover {
			opacity: $std-hover-opacity;
		}
	}

	&-controls {
		color: $black-30-color;
		display: flex;
		justify-content: space-between;
		margin-bottom: $std-controls-margin-bottom;

		&__icons {
			display: flex;
			font-size: 2rem;
		}

		&__icon {
			cursor: pointer;
			margin-left: .5rem;

			&:hover {
				opacity: $std-hover-opacity;
			}
		}

		@include media(sm) {
			margin-bottom: 0;
		}
	}

	&-head {
		&__breadcrumbs {
			margin-bottom: $std-breadcrumbs-margin-bottom;
		}

		@include media(sm) {
			&__breadcrumbs {
				margin-bottom: $std-breadcrumbs-margin-bottom-sm;
			}
		}
	}

	&-stage {
		@include make-container;

		min-height: 190px;
		padding-top: $std-stage-padding-top;

		@include media(md) {
			padding-top: $std-stage-padding-top-md;
		}

		&__head {
			margin-bottom: $std-head-margin-bottom;
		}
	}
}

.std {
	&-social {
		&__list {
			display: flex;
			flex-wrap: wrap;
			justify-content: space-around;
			justify-content: space-evenly;
			list-style: none;
			margin: 0;
			padding: 0;
		}

		&__item {
			flex-basis: 33%;
			margin-bottom: 2rem;
			padding: 0 .5rem;
			text-align: center;
		}
	}
}

.std {
	&-social-icon {
		&__title {
			color: $black-60-color;
			margin-top: .5rem;
			text-transform: uppercase;
		}
	}
}

.stf {
	&-carousel {
		bottom: .5rem;
		left: 50%;
		list-style: none;
		margin: 0;
		max-width: responsiveVal($stf-carousel-max-width, xs);
		padding: 0 ($grid-gutter-width / 2);
		position: absolute !important; //owl carousel css overwrite
		transform: translateX(-50%);

		.owl {
			&-nav {
				visibility: hidden;
			}
		}

		@include media(sm) {
			max-width: responsiveVal($stf-carousel-max-width, sm);
		}

		@include media(md) {
			padding: 0;
		}
	}

	&-opening {
		@include font(stf-opening);

		background-color: $generic-overlay-color;
		bottom: 0;
		color: $white-color;
		display: none;
		padding: $base-size;
		position: absolute;
		width: 100%;

		&.state-opening,
		&.state-closing {
			align-items: center;
			display: flex;
			justify-content: center;
		}
	}

	&-main {
		align-items: flex-end;
		min-height: $stf-main-min-height;
		position: relative;

		&__carousel {
			height: 124px;
			opacity: 0;
			transition: opacity 1.5s;
			transition-delay: .25s;
			visibility: hidden;

			&--single {
				animation: fade-in .75s .75s both;
				height: auto;
				visibility: visible;
				width: 100%;
				z-index: 1;
			}
		}

		.owl-dots {
			margin-top: .5rem;
			transform: scale(.75);
		}

		// fade slider in when loaded
		.owl-loaded {
			height: auto;
			opacity: 1;
			visibility: visible;
		}
	}

	@include media(sm) {
		&-main {
			// IE11 flexbox align-items fix
			height: 100px;
			min-height: $stf-main-min-height-f-sm;

			.owl-dots {
				margin-top: 1rem;
				padding-bottom: 1rem;
				transform: scale(1);
			}
		}
	}


	@include media(md) {
		&-main {
			align-items: center;
			min-height: $stf-main-min-height-f-md;
		}

		&-carousel {
			bottom: 5%;
			left: auto;
			right: 5%;
			transform: translate(0);

			.owl-nav {
				visibility: visible;
			}
		}
	}


	@include media(lg) {
		&-main {
			min-height: $stf-main-min-height-f-lg;
		}
	}


	&-fallback {
		background-color: $grey-light-color;
		overflow: hidden;
		position: relative;

		&__background {
			background: linear-gradient(hsla(0, 0, 0, .5), hsla(0, 0, 0, .5)) no-repeat;
			background-position: center;
			background-size: cover;
			height: 100%;
			left: 0;
			position: absolute;
			top: 0;
			width: 100%;

			img {
				font-family: 'object-fit: cover;'; // IE polyfill
				height: 100%;
				object-fit: cover;
				width: 100%;
			}
		}

		&--no-slider {
			.stf-fallback__background {
				height: 100%;
			}
		}
	}
}

.sto {
	&-background {
		display: block;
		font-family: 'object-fit: cover;';

		// IE polyfill
		height: 100%;
		object-fit: cover;
		position: absolute;
		top: 0;
		width: 100%;
		z-index: -2;
	}

	&-teaser {
		color: $white-color;
		text-align: center;

		&__title {
			@include font(display);

			margin: $sto-teaser-title-margin-v 0;
			text-transform: uppercase;

			&::after {
				background-color: $sto-teaser-rule-color;
				content: "";
				display: block;
				height: $sto-teaser-rule-height;
				margin: auto;
				margin-top: $sto-teaser-rule-margin-top;
				width: $sto-teaser-rule-width;
			}
		}

		&__text {
			@include font(lead-inverted);

			@include media(md) {
				@include make-col;
				@include make-col-span(10);
				@include make-col-offset(1);
			}
		}
	}

	&-container {
		@include make-container;

		min-height: $sto-container-min-height;
		padding-bottom: $sto-container-padding-bottom;
		padding-top: $sto-container-padding-top;
	}

	&-stage {
		overflow-x: hidden;
		position: relative;

		&::after {
			// dimmer
			background-color: $black-color;
			content: "";
			display: block;
			height: 100%;
			opacity: .35;
			position: absolute;
			top: 0;
			width: 100%;
			z-index: -1;
		}
	}

	@include media(sm) {
		&-container {
			display: flex;
			flex-direction: column;
			justify-content: center;
			min-height: $sto-container-height-sm;
		}
	}


	@include media(md) {
		&-container {
			padding-top: $sto-container-padding-top-md;
		}
	}
}

.bkm {
	&-button {
		@include font(bkm-button);

		color: $black-extra-light-color;
		cursor: pointer;

		&:hover {
			opacity: .6;
		}
	}

	&-footer {
		display: flex;
		justify-content: flex-end;
	}

	&-dropdown {
		&__trigger {
			cursor: pointer;
		}

		&__trigger:hover &__list {
			opacity: 1;
			transition-delay: 0s;
			visibility: visible;
		}

		&__list {
			@include reset-list;

			background-color: $white-color;
			box-shadow: 4px 4px 8px transparentize($black-medium-color, .6);
			margin-top: .75rem;
			opacity: 0;
			padding-bottom: $mnu-dropdown-padding-bottom;
			position: absolute;
			transition: opacity $mnu-dropdown-opacity-transition, visibility $mnu-dropdown-visibility-transition;
			transition-delay: .5s;
			visibility: hidden;
			width: 22rem;
			z-index: 11;
		}

		&__headline {
			@include font(h3);

			margin-bottom: 0;
			text-transform: uppercase;
		}

		h4 {
			font-size: 12px;
			font-weight: 600;
			margin-bottom: 0;
			text-transform: none;
		}

		&__item {
			color: $black-medium-color;
			padding: $mnu-dropdown-item-padding-v $mnu-dropdown-item-padding-h;

			.btn {
				width: 100%;
			}

			.mnu-infolist__item & {
				html:not(.can-touch) & {
					.btn:hover {
						color: $white-color;
					}
				}
			}

			&--scrollable {
				padding-right: 0;

				ul {
					@include reset-list;

					max-height: 25rem;
					overflow-y: auto;
				}
			}

			.icn-spinner {
				color: getThemeColorValue("mdl-modal-inline-spinner");
				display: block;
				font-size: 1.5rem;
				margin: 1rem auto;
			}
		}
	}

	&-list {
		&__item {
			background-color: white;
			padding: $mnu-dropdown-item-padding-v $mnu-dropdown-item-padding-h $mnu-dropdown-item-padding-v 0;
			position: relative;
			z-index: $z-index-page-highlight;
		}

		article {
			border-bottom: 1px solid $lines-global;
			display: flex;
			flex-direction: column;
			padding-bottom: 1.25rem;

			> a {
				display: inherit;
			}
		}
	}
}

.js-bookmark-removing {
	filter: grayscale(1) opacity(.5);
	pointer-events: none;
	transition: filter $default-transition, margin $default-transition, opacity 1s;
}

.js-bookmark-removed {
	opacity: 0;
	z-index: calc(#{$z-index-page-highlight} - 1);
}

@mixin acc-hover {
	.acc-accordion__title {
		border-color: $primary-color;
		color: $primary-color;
	}

	.acc-accordion__title::after {
		color: $primary-color;
	}

	.acc-accordion__icon {
		color: $primary-color;
	}
}

.acc {
	&-accordion {
		&__title {
			border-bottom: thin solid $lines-global;
			cursor: pointer;
			display: flex;
			justify-content: space-between;
			padding-bottom: .5rem;

			&:hover {
				border-color: $primary-color;
				color: $primary-color;

				@include acc-hover;
			}
		}

		&__icon {
			color: $lines-global;
			display: inline-block;
			height: 1.75rem;
			margin: 0 .25rem;
			vertical-align: middle;
			width: 1.75rem;

			&--open {
				display: none;
			}
		}

		&__body {
			height: 0;
			overflow: hidden;

			// TODO it could be e.g. a div, with own margin or no margin
			p:last-of-type {
				margin-bottom: 0;
			}

			// TODO II it should be possible to have a tme that is not a container
			.tme__content {
				padding: 0;
			}
		}

		&--active {
			@include acc-hover;
		}

		&--active &__body {
			@include spacing-responsive-base;

			height: auto;
		}

		&--active &__icon {
			display: none;

			&--open {
				display: block;
			}
		}
	}

	// No javascript fallback
	&-section--no-js {
		.acc-accordion__title:focus,
		.acc-accordion:focus-within .acc-accordion__title {
			@include acc-hover;

			pointer-events: none;
		}

		.acc-accordion__title:focus .acc-accordion__icon {
			display: none;
		}

		.acc-accordion__title:focus .acc-accordion__icon--open {
			display: block;
		}
		.acc-accordion__title:focus ~ .acc-accordion__body,
		.acc-accordion__body:focus,
		.acc-accordion__body:focus-within {
			@include spacing-responsive-base;

			height: auto;
		}
	}
}

.acc {
	&-section {
		&__title {
			margin-bottom: .75rem;
		}
	}
}

.content-switch {
	@include make-content-container;
	@include section-padding;

	&__label {
		display: none;

		&--inner {
			@include font(button);

			border: 1px solid $grey-color;
			color: $grey-color;
			display: block;
			padding: .25em 1em;
			text-transform: uppercase;
		}
	}

	&__radio {
		left: -9999px;
		position: absolute;

		&:checked {
			& + .content-switch__label {
				background-color: $primary-color;
				border: 1px solid $primary-color;
				color: $white-color;
			}

			@for $i from 1 through 5 {
				&:nth-of-type(#{$i}) ~ .content-switch__content-wrapper:nth-of-type(#{$i}) {
					height: auto;

					.content-switch__label--inner {
						background-color: $primary-color;
						border: 1px solid $primary-color;
						color: $white-color;
					}

					.content-switch__content {
						height: auto;
					}
				}
			}
		}
	}

	&__content {
		// we can't hide the content with display none or filter-taby js in content
		// cannot compute its dimensions so we hide with height 0 and overflow hidden.
		height: 0;
		margin: 0 -25px;
		overflow: hidden;
	}

	@include media(sm) {
		&__label {
			@include font(button);

			border: 1px solid $grey-color;
			color: $grey-color;
			display: block;
			float: left;
			padding: .25em 1em;
			text-transform: uppercase;

			&--inner {
				display: none;
			}
		}

		&__content-wrapper {
			height: 0;
			overflow: hidden;
		}

		&__content {
			height: auto;
		}
	}
}

.content-tabs {
	.fltr-container-list {
		display: block;
		z-index: 2;

		&:before {
			background-color: $grey-color;
			bottom: 0;
			content: '';
			display: block;
			height: 1px;
			position: absolute;
			width: 100%;
		}
	}

	&__content {
		@include spacing-responsive-base("padding", "top");

		display: none;
		z-index: 1;

		&--active {
			display: block;
		}
	}
}

.cpn {
	&-detail {
		.col-xs-12:nth-child(2) {
			margin-top: 1.5rem;
			@include media-up(sm) {
				margin-top: 0;
			}
		}

		&__discountpane {
			position: relative;

			img {
				height: auto;
				width: 100%;
			}
		}

		&__app {
			margin-top: 3rem;
			h3 {
				color: getThemeColorValue("ttl__text")
			}
			a {
				display: inline-block;
			}
		}

		.btc-bar {
			align-items: normal;
			@include media-up(sm) {
				align-items: center;
			}
		}

		&__message {
			@include message-box();
			margin-bottom: 2rem;

			&--default {
				@include message-box-colors($black-60-color, $black-60-color);
			}
		}
	}
}

.cpn {
	&-redeem {
		&__discountpane {
			margin-bottom: 2rem;
			img {
				height: auto;
				width: 100%;
			}
		}

		h3.heading-3 {
			margin: 0 0 2rem 0;
		}

		.btc-bar {
			align-items: normal;
			@include media-up(sm) {
				align-items: center;
			}
		}

		&__message {
			@include message-box(action);

			margin-bottom: 2rem;

			&--info {
				@include message-box-colors($info-color, $info-text-color);

				// fix
				.cpn-detail__message__icon {
					font-size: 2em;
					margin-top: -10px;
				}
			}

			&--error {
				@include message-box-colors($error-color, $error-color);
			}

			&--success {
				@include message-box-colors($success-color, $success-color);
			}
		}
	}
}

.drk-message {
	//overlay
	background-color: $dimmer-color;
	display: block;
	height: 100vh;
	left: 0;
	opacity: 0;
	position: fixed;
	top: 0;
	transition: opacity $default-transition, visibility $default-transition;
	visibility: hidden;
	width: 100vw;
	z-index: $z-index-page-overlay;

	&.is-displayed {
		opacity: 1;
		visibility: visible;
	}

	&__article {
		background-color: hsla(0, 0%, 0%, .85);
		box-shadow: 15px 15px 25px hsla(0, 0%, 0%, .2);
		color: $white-color;
		max-height: 100vh;
		overflow: auto;
		padding: $grid-gutter-width;
		position: absolute;
		top: 0;
		width: 100%;
		z-index: $z-index-dark-message;
	}

	&__button {
		// Must override the default light button colors
		background-color: transparent !important;
		border-color: $white-color !important;
		color: $white-color !important;
		margin-top: $base-size;

		&:hover {
			color: hsla(0, 0%, 100%, .6) !important;
		}
	}

	&__footer {
		text-align: center;
	}

	&__title {
		&::after {
			background-color: $lines-global-inverted;
			content: "";
			display: block;
			height: 1px;
			margin-top: $base-size / 2;
			width: 100%
		}
	}

	@include media(sm) {
		&__article {
			left: 50%;
			margin: auto;
			top: 50%;
			transform: translate(-50%, -50%);
			width: $drk-message-max-width;
		}

		&-footer {
			text-align: right;
		}
	}
}

.dtl {
	&-description {
		@include make-content-container;
		@include spacing-responsive-inc;

		&__header {
			margin-bottom: $dtl-description-header-margin-bottom;
		}

		&__content {
			margin-bottom: $dtl-description-content-margin-bottom;
		}

		&__lead {
			@include font(lead);
			@include spacing-responsive-base;
		}
	}
}

.dtl {
	&-header {
		&.dtl-contact {
			margin-bottom: $dtl-header-contact-margin-bottom;
		}
	}
}

.dtl-title {
	margin-bottom: $base-size * 2;

	&__upper {
		border-bottom: 1px solid $lines-global;
		margin-bottom: $base-size * .75;
	}

	&__title {
		@include font(h3);

		margin-bottom: $base-size * .5;
		text-transform: uppercase;
	}

	&__author {
		//@include font(tsr__author);
		@include font(tsr-date);

		color: $black-75-color;
		margin-bottom: $base-size * .75;
		text-transform: uppercase;
	}

	&__subtitle {
		padding-right: $dtl-header-subtitle-padding;
		text-transform: uppercase;

		&:nth-child(3) {
			color: $black-extra-light-color;
			display: block;

			@include media(sm) {
				border-left: 1px solid $lines-global;
				display: inline;
				padding-left: $dtl-header-subtitle-padding;
			}
		}
	}
}

/*
Stage Image
*/

.dtl {
	&-stage-image {
		@include make-container;

		margin-bottom: $dtl-stage-bild-margin-bottom;
		width: 100%;

		@include media(sm) {
			margin-bottom: $dtl-stage-bild-margin-bottom-sm;
		}
	}

	&-stage-video {
		@include make-container(0); // because abs positioning

		margin-bottom: $dtl-stage-bild-margin-bottom;
		position: relative;

		&::after {
			content: "";
			display: block;
			padding-bottom: 55%;
		}

		@include media(sm) {
			margin-bottom: $dtl-stage-bild-margin-bottom-sm;
		}

		iframe,
		video {
			@include make-container; // because abs positioning

			border: 0;
			height: 100%;
			position: absolute;
			width: 100%;
		}
	}
}

// Video Container
.dtl {
	&-video {
		padding-bottom: 55%; // 16:9
		position: relative;

		iframe,
		video {
			height: 100%;
			position: absolute;
			width: 100%;
		}
	}
}

.dir {
	&-element {
		@include font(dir-label);

		color: $black-light-color;
		display: block;
		text-transform: uppercase;

		&__icon {
			display: block;
			height: $dir-element-icon-size;
			padding: $dir-element-icon-padding;
			position: absolute;
			width: $dir-element-icon-size;

			svg {
				height: 100%;
				width: 100%;
			}
		}

		&__input {
			@include font(dir-input);
			@include placeholder($placeholder-global-color);

			background-color: $grey-light-color;
			color: $black-color;
			display: block;
			padding: $dir-input-padding;
			width: 100%;

			&:disabled {
				color: $black-color;
				cursor: not-allowed;
				opacity: .65;
			}
		}
	}
}

.dir {
	&-form {
		&__title {
			@include font(h4);

			margin: $dir-form-title-margin-v 0;
			text-transform: uppercase;
		}

		&__element {
			margin-bottom: $dir-element-margin-bottom;
			width: 100%;
		}

		.btn {
			margin-top: $dir-form-button-margin-top;
		}
	}
}

.dir {
	&-controls {
		background-color: $white-color;
		padding: $dir-controls-padding;

		&__panel {
			width: 100%;

			& .adp-directions {
				width: 100%;
			}

			& .adp-placemark {
				background-color: transparent;
				border-left: 0;
				border-right: 0;
			}
		}

		@include media(sm) {
			border: 1px solid $lines-global;
		}
	}
}

.dir {
	&-map {
		background-color: rgb(249, 249, 249);
		padding-bottom: $dir-map-height;
		position: relative;

		&__controls {
			margin-bottom: $dir-map-controls-margin-bottom;
		}

		&__chart-wrapper {
			bottom: 0;
			height: $dir-map-height;
			position: absolute;
			width: 100%;
		}

		&__spinner {
			@include spinner-centered;

			color: $black-light-color;
		}

		&__chart {
			height: 100%;
			visibility: hidden;

			&.dir-visible {
				animation: fade-in 250ms forwards;
				visibility: visible;
			}
		}

		@include media(sm) {
			padding-bottom: $dir-map-height-sm;

			&__controls {
				left: $dir-controls-left;
				max-height: $dir-controls-max-height;
				max-width: $dir-controls-max-width;
				position: absolute;
				top: $dir-controls-top;
				width: $dir-controls-width;
				z-index: 1;
			}

			&__chart-wrapper {
				height: $dir-map-height-sm;
			}

			&__panel {
				max-height: $dir-panel-max-height-sm;
				overflow-y: scroll;
			}
		}
	}
}

.dir {
	&-button {
		&__icon {
			background-color: getThemeColorValue("dir-button-icon__bg");
			color: getThemeColorValue("dir-button-icon__color");
			margin: auto;
			margin-bottom: $base-size;
			transition: $hover-transition;
		}

		&__label {
			@include font(dir-button-label);

			color: $black-extra-light-color;
			display: none;
			margin-bottom: $base-size;
			text-transform: uppercase;

			.dir-bar__item:hover &, .dir-bar__item--active & {
				color: getThemeColorValue("dir-button-label--hover__text");
			}
		}

		@include media(sm) {
			&__icon {
				height: $dir-button-size-sm;
				margin-bottom: $base-size / 2;
				padding: 5%;
				width: $dir-button-size-sm;

				&::before {
					font-size: 4rem;
					line-height: $dir-button-size-sm;
				}
			}

			&__label {
				display: block;
			}
		}
	}
}

.dir {
	&-bar {
		@include reset-list;

		border-bottom: $fltr-menu-border-bottom solid $lines-global;
		text-align: center;

		&__item {
			border-bottom: 4px solid transparent;
			display: inline-block;
			max-width: 22%;
			padding: 0 ($dir-button-size / 4);

			&--active {
				cursor: default;
			}

			@include media(sm) {
				padding: 0 ($dir-button-size-sm / 4);

				&--active {
					border-bottom: 4px solid getThemeColorValue("dir-bar-item--active__border");
				}
			}
		}

		&__item--active .dir-button__icon, &__item:hover .dir-button__icon {
			background-color: getThemeColorValue("dir-button-icon--active__bg");
			color: getThemeColorValue("dir-button-icon--active__color");
		}
	}
}

.dir-description {
	.btc-bar {
		@include spacing-responsive-base;
	}
}

/*
<div class="dir-directions" data-module="dir-description">
		<div class="dir-directions__container">
			<sg-insert>1.7.2</sg-insert> <!-- section title -->
			<sg-insert>3.5.8</sg-insert> <!-- dir description -->
			<sg-insert>3.5.1</sg-insert> <!-- map -->
		</div>
</div>
*/

.dir {
	&-directions {
		@include spacing-responsive-base("padding", "top");

		.ttl {
			@include make-container;
		}

		.ttl {
			@include make-container;
		}

		&__form {
			transition: max-height $default-transition, opacity $default-transition;
		}

		&:not([travelMode]) &__form {
			max-height: 0;
			opacity: 0;
			overflow-y: hidden;
		}

		&:not([travelMode]) &__controls {
			margin: 0;
			opacity: 0;
		}
	}
}

// bug in Safari, marker not shown correctely
.adp-marker {
	visibility: hidden;
}

/* Atoms */
.ftr {
	&-article {
		@include font(small);

		color: getThemeColorValue("ftr-article__text");

		&__adresse {
			display: block;
			margin-bottom: $ftr-adresse-margin-bottom;
		}

		&__title {
			@include font(h4);

			border-bottom: 1px solid getThemeColorValue("ftr-article-title__border");
			color: getThemeColorValue("ftr-article-title__text");
			margin-bottom: $ftr-article-title-margin-bottom;
			padding-bottom: $ftr-article-title-padding-bottom;
			text-transform: uppercase;
		}

		.opn-list {
			margin-bottom: $ftr-article-title-margin-bottom;
		}

		&__opening {
			@include font(emph);
		}

		&__link {
			margin-bottom: $ftr-adresse-link-margin-bottom;
		}

		&__store-icon {
			background-color: transparent !important;
			display: inline-block;
			margin-bottom: .3125rem;
			margin-right: $ftr-article-store-icon-margin-right;
			max-height: $ftr-article-store-icon-max-height;
		}
	}

	&-email {
		&__input {
			display: inline-block;
			font-size: $ftr-email-font-size;
			height: $ftr-email-height;
			margin-right: $ftr-email-input-margin;
			max-width: $ftr-email-input-max-width;
			opacity: .7;
			padding-left: $ftr-email-input-padding-left;
			transition: $hover-transition;
			vertical-align: middle;
			width: $ftr-email-input-width;

			&:hover,
			&:focus {
				opacity: 1;
				outline: none;

				// in this special case (singe input in footer)

			}
		}

		&__button {
			background-color: transparent;
			border: 1px solid $white-color;
			cursor: pointer;
			display: inline-block;
			font-size: 1.5rem;
			height: $ftr-email-height;
			min-width: $ftr-email-height;
			padding: 0;
			text-align: center;
			transition: $hover-transition;
			vertical-align: middle;

			html:not(.can-touch) & {
				&:hover {
					background-color: $primary-color;
					color: $white-color;
				}
			}
		}
	}

	&-nav {
		@include reset-list;

		> li {
			padding: $ftr-nav-list-li-padding;
			transition: color $default-transition;
		}

		&--list {
			> li {
				border-bottom: 1px solid $black-extra-light-color;
			}
		}

		&--inline {
			display: flex;
			flex-flow: row wrap;
			justify-content: center;

			> li {
				flex: 0 1 auto;
			}
		}
	}

	@include media(sm) {
		&-nav {
			&--list {
				display: flex;

				> li {
					border-bottom: 0;
				}
			}
		}
	}


	/* Molecules */
	&-aside {
		@include font(ftr-aside);

		background-color: getThemeColorValue("ftr-aside__bg");
		color: getThemeColorValue("ftr-aside__text");

		&__footer {
			align-items: center;
			border-top: 1px solid $black-extra-light-color;
			display: flex;
			flex-direction: column;
			margin: 0 $ftr-aside-margin;
			padding: $ftr-aside-footer-padding-v 0;
			text-align: center;
		}

		&__lang {
			// IE dropdown color
			background-color: getThemeColorValue("ftr-aside__bg");
			color: $white-color;
			font-size: 1rem;
			margin-bottom: $ftr-lang-margin-bottom;
			margin-top: $ftr-lang-margin-top;
		}

		&__highlight {
			color: $white-color;

			html:not(.can-touch) & {
				& > li:hover {
					color: $white-color;
				}
			}
		}

		&__legal {
			html:not(.can-touch) & {
				& > li:hover {
					color: $white-color;
				}
			}
		}

		@include media(sm) {
			&__container {
				display: flex;
				flex-flow: row-reverse wrap;
			}

			&__footer {
				flex: 1 0 90%;
				flex-direction: row;
				justify-content: space-between;
				text-align: left;
			}

			&__lang {
				margin: 0 12rem 0 0;
			}

			&__nav {
				flex: 1 0 auto;
			}

			&__highlight {
				justify-content: flex-end;
			}

			&__legal {
				justify-content: flex-start;
			}
		}


		@include media(md) {
			&__lang {
				display: none;
			}
		}


		@include media(lg) {
			&__footer {
				border-top: 0;
				flex: 1 0 auto;
				margin: 0;
			}
		}
	}

	&-contacts {
		@include section-padding;

		background-color: getThemeColorValue("ftr-contacts__bg");
		color: $black-medium-color;

		&__container {
			@include make-container(10px);
			@include make-row(0);
		}

		&__article {
			@include make-col;
			@include make-col-span(12);

			margin-bottom: $ftr-article-margin-bottom;
		}

		@include media(sm) {
			&__article {
				@include make-col-span(6);
			}

			&--three &__article:last-child {
				@include make-col-span(12);
			}
		}


		@include media(md) {
			&--two &__article .opn-list {
				width: 50%;
			}

			&--three &__article {
				@include make-col-span(4);
			}

			&--three &__article:last-child {
				@include make-col-span(4);
			}
		}


		@include media(lg) {
			&--four &__article {
				@include make-col-span(3);
			}
		}
	}

	&-social {
		@include section-padding;

		background-color: $white-color;

		.btn-round {
			margin: 0 $ftr-social-button-margin;
		}

		&__container {
			@include make-container(10px);
			@include make-row(0);

			align-items: center;
			margin: 0 auto;
			max-width: min-width(lg);
			width: 100%;
		}

		&__title {
			@include make-col;
			@include make-col-span(12);
			@include font(ftr-social);

			margin-bottom: $spacing-vertical-base;
			text-align: center;
		}

		&__btn-container {
			@include make-col;
			@include make-col-span(12);

			display: flex;
			flex-flow: row wrap;
			justify-content: center;
		}

		@include media(sm) {
			&__title {
				@include make-col-span(4);

				margin-bottom: 0;
			}

			&__btn-container {
				@include make-col-span(8);

				justify-content: flex-end;
			}
		}


		@include media(md) {
			&__title {
				@include make-col-span(6);
			}

			&__btn-container {
				@include make-col-span(6);

				justify-content: center;
			}
		}
	}

	&-footer {
		border-top: 1px solid $lines-global;
		position: relative;
		z-index: $z-index-page-footer;

		.ftr-aside__container,
		.ftr-contacts__container {
			margin: auto;
			max-width: $ftr-container-max-width;
		}
	}
}

.glr {
	@include make-container($grid-gutter-width);
	@include section-padding-asymmetric;

	&-image {
		background: $grey-light-color;
		cursor: pointer;
		padding-top: 100%;
		position: relative;

		&__expand {
			background-color: rgba(#000000, .5);
			bottom: 0px;
			color: $white-color;
			display: block;
			font-size: 1.5em;
			height: auto;
			padding: 0 5px;
			position: absolute;
			right: 0;
			width: auto;
		}

		&__img {
			position: absolute;
			top: 0;
			width: 100%;
		}
	}

	&-loadmore {
		display: flex;
		justify-content: center;
	}

	&-images {
		@include make-row;

		margin-bottom: -$base-size * .5;

		&__image {
			@include make-col;

			flex: 0 0 (1 / 2 * 100%);
			margin-bottom: $base-size * 2;
			max-width: 1 / 2 * 100%;
		}

		&--center {
			justify-content: center;
		}
	}

	@include media(sm) {
		&-images {
			margin-bottom: $base-size;

			&__image {
				flex: 0 0 (1 / 3 * 100%);
				max-width: 1 / 3 * 100%;
			}
		}
	}


	@include media(md) {
		&-images {
			//justify-content: center;

			&__image {
				flex: 0 0 (1 / 4 * 100%);
				max-width: 1 / 4 * 100%;
			}
		}
	}
}

.hdr {
	&-search {
		align-items: center;
		background-color: getThemeColorValue("mnu-sublist__bg");
		color: $black-60-color;
		display: flex;
		padding: 1rem;
		transition: max-height $search-transition;
		width: 100%;

		&__input {
			@include placeholder($placeholder-global-color);

			flex-grow: 1;
			font-family: inherit;
			font-size: inherit;
			height: 100%;
			margin-left: .5rem;
			padding: .5rem 0;

			&:focus {
				outline: none;
			}
		}

		&__close {
			color: getThemeColorValue("mnu-sublist__text");
		}

		&__input-container {
			align-items: center;
			background-color: $white-color;
			border-radius: 2px;
			display: flex;
			flex-grow: 1;
			margin-right: .5rem;
			padding: 0 .5rem;
		}
	}
}

.hdr {
	&-dimmer {
		background-color: $dimmer-color;
		bottom: 0;
		left: 0;
		opacity: 0;
		position: fixed;
		right: 0;
		top: 0;
		transition: opacity $hdr-dimmer-transition, visibility $hdr-dimmer-transition;
		visibility: hidden;
		z-index: 3;

		&--search {
			top: responsiveVal($hdr-header-height, xs);

			body.search-active & {
				opacity: 1;
				visibility: visible;
			}

			@include media(md) {
				top: responsiveVal($hdr-header-height, md);
			}
		}

		&--menu {
			z-index: 10;

			body.menu-active & {
				opacity: 1;
				visibility: visible;
			}
		}
	}

	&-header {
		align-items: center;
		background-color: getThemeColorValue("header");
		border-bottom: 1px solid getThemeColorValue("header__border");
		display: flex;
		height: responsiveVal($hdr-header-height, xs);
		justify-content: space-between;
		padding: 0 $hdr-header-padding-h;
		position: fixed;
		top: 0;
		width: 100%;
		z-index: $z-index-page-header;

		&__logo {
			flex: 1 0 auto;

			a {
				display: inline-block;
				@media screen and (-ms-high-contrast: active), screen and (-ms-high-contrast: none) {
					display: inline;
				}
			}
		}

		&__logo--landscape {
			padding: $hdr-logo-landscape-padding-v 0;

			img {
				height: $hdr-logo-landscape-height;
				width: auto;
			}

			@include media(md) {
				padding: $hdr-logo-landscape-padding-v-md 0;

				img {
					height: $hdr-logo-landscape-height-md;
				}
			}
		}

		&__logo--portrait {
			align-self: flex-start;
			z-index: 6;

			img {
				height: $hdr-logo-portrait-height;
				max-width: $hdr-logo-portrait-max-width;
				width: auto;
			}

			@include media(md) {
				img {
					height: $hdr-logo-portrait-height-md;
					max-width: $hdr-logo-portrait-max-width-md;
				}
			}
		}

		&__logo--llandscape {
			align-items: center;
			display: flex;

			img {
				height: auto;
				width: $hdr-logo-llandscape-width;
			}

			@include media(md) {
				img {
					width: $hdr-logo-llandscape-width-md;
				}
			}
		}

		&__burger,
		&__icon-search {
			flex: 0 0 auto;
		}

		&__burger {
			margin-left: $hdr-burger-margin-left;
			z-index: 12;
		}

		&__icon-search {
			color: getThemeColorValue("header__text");
			font-size: 1.85rem;
			z-index: 4;
		}

		&__menu {
			height: 100vh;
			overflow: scroll;
			position: absolute;
			right: 0;
			top: 0;
			transform: translateX(100%);
			z-index: 11;
		}

		&__search {
			bottom: -1px;
			opacity: 0;
			position: absolute;
			right: 0;
			transform: translateY(100%);
			transition: all $hdr-search-transition;
			visibility: hidden;
			z-index: 4;
		}

		body.menu-active &__menu {
			/* has to be scroll for "-webkit-overflow-scrolling: touch", not auto */
			// iOS bug: if the menu content fit in the viewport, the body get scrolled
			//-webkit-overflow-scrolling: touch;

			overflow: scroll;

			transform: translateX(0);
		}

		body.menu-active &__burger {
			background-color: getThemeColorValue("header");
			box-shadow: 2px 2px 4px getThemeColorValue("hdr-burger__shadow");
		}

		body.search-active &__search {
			opacity: 1;
			visibility: visible;
		}

		body.search-active &__icon-search {
			color: getThemeColorValue("header--hover__text");
		}

		@include media(md) {
			height: responsiveVal($hdr-header-height, md);
			padding: 0 $hdr-header-padding-h-md;

			&__burger {
				display: none;
			}

			&__lang {
				margin-left: $hdr-lang-margin-left;
				margin-right: $hdr-lang-margin-right;
			}

			&__icon-search {
				display: none;
			}

			&__menu {
				background-color: transparent;
				height: 100%;
				overflow: hidden;
				padding-top: $hdr-header-menu-padding-top;
				position: static;
				transform: none;
				z-index: auto;
			}

			&__search {
				width: 20rem;
			}

			body.menu-active &__menu {
				overflow-y: visible;
			}
		}


		@include media(lg) {
			padding: 0 $hdr-header-padding-h-lg;

			&__icon-search {
				display: none;
			}

			&__menu {
				padding-top: $hdr-header-menu-padding-top-lg;
			}

			&__search {
				display: none;
			}
		}
	}
}

.lgn {
	&-container {
		@include make-container;

		background: white;
		max-width: 100vw;
		padding: 1.5rem 2.5rem 2.5rem;
		position: relative;
		width: calc(#{min-width(md)} - 2 * #{$grid-container-padding});

		@include media(md) {
			.fltr-container-list {
				opacity: 0;
				position: absolute;
				top: 0;
				transform: translateY(-100%);
			}

			.content-tabs__content {
				padding-top: 0;
			}
		}

		&--small {
			max-width: 100%;
			width: calc(35rem - 2 * #{$grid-container-padding});
		}
	}

	&-hero {
		color: $white-color;
		display: none;
		position: static;

		&__background {
			font-family: 'object-fit: cover;'; // IE polyfill
			height: 100%;
			left: 0;
			object-fit: cover;
			position: absolute;
			top: 0;
			width: 50%;
		}

		&__body {

			position: relative;
			z-index: 1;
		}

		@include media(md) {
			display: block;

			&__body {
				@include section-margin;
			}
		}
	}

	// password forgotten, already registered...
	&-link {
		color: $primary-color;
		cursor: pointer;
		font-weight: bold;

		&:hover {
			color: getThemeColorValue("hover--on-bright");
		}
	}

	&-list {
		padding: 0;

		&__item {
			display: flex;
			list-style: none;
			margin-bottom: 1rem;
		}

		&__icon {
			color: $yellow-color;
			display: inline-block;
			flex: 0 0 2rem;
			height: 1.5rem;
			margin-right: .5rem;
		}
	}

	&-login {
		&__title {
			display: none;
			margin-top: 0;
		}

		@include media(md) {
			&__title {
				display: block;
			}

			// override grid gutter
			[class*="col-"] {
				padding-left: 25px;
				padding-right: 25px;
				position: static !important;
			}

			&.row {
				margin-left: -25px;
				margin-right: -25px;
			}
		}
	}

	&-form {
		.btc-bar {
			flex-direction: row;
		}

		@include media(sm) {
			.lgn-link {
				margin-bottom: 0rem;
			}
		}

		@include media(md) {
			@include section-margin;
		}
	}

	&-or-separator {
		@include title-sideline(1px, 0, $lines-global);

		color: $lines-global;
		margin-bottom: 2.5rem;
		margin-top: 3rem;

		&::after {
			height: 1px;
			margin-left: 1rem;
		}
		&::before {
			height: 1px;
			margin-right: 1rem;
		}
	}

	&-register {
		&__form {
			display: flex;
			flex-wrap: wrap;
			width: 100%;
		}

		.btc-bar {
			margin-top: 2rem;
		}

		.row {
			width: calc(100% + #{$grid-gutter-width});
		}

		@include media(md) {
			@include section-padding-asymmetric;
		}
	}

	&-social {
		text-align: center;

		&__button {
			color: $white-color;
			display: inline-block;
			margin-right: .5rem;
			margin-top: .5rem;
			padding: $btn-padding-vertical $btn-padding-horizontal;
		}
	}

	// vertically center registration button
	&-center {
		top: 50%;
		transform: translateY(-50%) scale(1.1);
	}
}

.nws {
	&-registration {
		background-color: getThemeColorValue("nws-registration-bg");
		color: $white-color;
		overflow: hidden;

		&__bg {
			bottom: -10rem;
			color: hsla(0, 0%, 100%, .4);
			height: $nws-registration-bg-width;
			position: absolute;
			right: calc(50% - #{$nws-registration-bg-width / 2}); //center
			transform: rotate(10deg);
			width: $nws-registration-bg-width;
		}

		&__content {
			position: relative;
		}

		&__text {
			position: relative;
			z-index: 1;
		}

		@include media(md) {
			&__bg {
				right: 5%; // -color right is always matched by gulp-extractcolor
			}
		}
	}
}

.qln {
	&__buttons {
		border-bottom: 1px solid $lines-global;
		display: flex;
		justify-content: center;
		padding-bottom: $grid-gutter-width / 2;
		padding-top: $grid-gutter-width / 2;

		@include media(sm) {
			padding-bottom: $grid-gutter-width;
			padding-top: $grid-gutter-width;
		}


		.btn-round {
			background-color: getThemeColorValue("qln-button__bg");
			color: getThemeColorValue("qln-button__fg");
		}

		.btncmp-round-label__label {
			color: getThemeColorValue("qln-button__label");
		}
	}

	.btncmp-round-label {
		@include media(sm) {
			margin: 0 $btn-round-size;

			&__label {
				max-width: $btn-round-size * 3;

				// here we have the necessity for a bit more space

			}
		}
	}

	&__flyout {
		background-color: $black-75-color;
		color: $white-color;
		max-height: 0;
		opacity: 0;
		overflow: hidden;
		transition: max-height .5s, opacity .5s;

		&.open {
			max-height: 700px;
			opacity: 1;
		}

		&__container {
			@include make-container;

			padding-bottom: $grid-gutter-width;
			padding-right: 75px;

			// 3 * container padding
			padding-top: $grid-gutter-width;
			position: relative;

			@include media(sm) {
				display: flex;
			}
		}

		&__infobox {
			margin-top: 1.5rem;
		}

		&__section {
			@include font(copy);

			p {
				margin: 0;
			}


			@include media(sm) {
				border-right: 1px solid $white-color;
				padding: 0 $grid-gutter-width;
				width: 33.333%;
			}


			&--headline {
				@include font(h5);

				text-transform: uppercase;

				@include media(sm) {
					margin: 0;
				}
			}

			&--countdown {
				font-weight: bold;
				text-align: left;
			}

			&--additional-information {
				@include reset-list;

				li {
					margin-top: 7px;

					a:hover,
					a:hover .icn-detail:before {
						color: $secondary-color;
					}

					&:first-child {
						margin-top: 0;
					}
				}
			}

			&:last-child {
				margin-top: $grid-gutter-width;

				@include media(sm) {
					border-right: none;
					margin-top: 0;
				}
			}
		}

		&__close {
			position: absolute;
			right: 25px;

			// container padding
			top: $grid-gutter-width / 2;
		}
	}
}

.src {
	&-title {
		margin-bottom: 2rem;

		&__title {
			margin-bottom: .5rem;

			&--grey {
				color: $grey-color;
			}
