.tsr-grp {
	@include section-padding-asymmetric;

	background-color: $white-color;
	position: relative;

	// for bg image case

	// because the teaser itself has to span the entire width the teaser sections have to be containerized.
	// because in case of a beackground image the teaser sections have to lay above they get positioned relative.
	.ttl,
	&__lead,
	&__no-results,
	.btc-bar {
		@include make-container;

		position: relative;

		// for bg image case

	}

	&__lead {
		@include font(lead);
		@include spacing-responsive-base;

		text-align: center;

		p {
			margin: 0;

			@include media(md) {
				@include make-col;
				@include make-col-span(10);
				@include make-col-offset(1);
			}
		}
	}

	&__teasers {
		// the teasers have a padding of 15 for themselves
		@include make-container(10px);
		@include make-row(0);
	}

	&__teaser {
		@include teaser-wrapper;

		display: flex;

		> * {
			width: 100%;
		}
	}

	.btc-bar {
		@include spacing-responsive-inc("margin", "top");
	}

	&__bg-image {
		height: 100%;
		left: 0;
		object-fit: cover;
		position: absolute;
		top: 0;
		width: 100%;
	}

	&__no-results {
		@include font(lead);

		text-align: center;
	}

	// workaround to IE / Safari Flex Child height problem
	.owl-item {
		display: flex;

		> * {
			flex-grow: 1;
		}
	}

	/*
	Shop Banderole

	At the moment is only included in the Teaser Group `Shop banderole`
	It can be used as a carousel, see also the owl-theme--banderole class.

	markup:
	<div class="tsr-grp-banderole owl-theme--banderole" data-module="carousel" data-module-options='{"loop": "true", "margin": 0, "autoplay": "true", "autoplayHoverPause": "true"}'>
		<sg-insert>2.1.12</sg-insert>
		...
	</div>
	*/

	&-banderole {
		overflow-x: hidden;
		padding: 0;
		white-space: nowrap;

		&__item {
			display: inline-block;
			padding: 1px 5px; //vertical to prevent rendering errors
			width: 175px;

			@include media(md) {
				width: 250px;
			}
		}
	}

	&--combi {
		.tsr-grp__teaser {
			margin-bottom: $spacing-vertical-base;

			&:last-of-type {
				margin-bottom: 0;
			}

			@include media(sm) {
				margin-bottom: 0;

				// special handling of three col teaser
				// standard three col teaser is one col in xs and sm
				// we need a two col version for sm here
				&--columns-3 {
					@include make-col-span(12);
				}

				// special handling for last of thre one col teasers
				// it would break line so we hide it
				&:nth-of-type(3) {
					display: none;
				}
			}


			@include media(md) {
				// special handling for last of thre one col teasers
				// was hidden in sm - show it again
				&:nth-of-type(3) {
					display: flex;
				}
			}
		}
	}

	&--shops {
		.tsr-grp__teasers {
			margin-bottom: -16px; // negate teaser spacing

			@include media(md) {
				margin-bottom: -$grid-gutter-width;
			}
		}

		.tsr-grp__teaser,
		.pge-wall__item {
			// quickfix so we can use teaser group markup shop for overview page
			@include make-col-span(6);

			margin-bottom: 16px;

			// bercause of responsive gutter

			@include media(sm) {
				@include make-col-span(3);
			}

			@include media(md) {
				flex: 0 0 20%;
				margin-bottom: $grid-gutter-width;
				max-width: 20%;
			}
		}
	}

	&--category {
		.tsr-grp__teasers {
			flex-wrap: nowrap;
			overflow-x: hidden;

			&.owl-loaded, &.owl-init {
				overflow-x: visible;
			}
		}

		.owl-item {
			.tsr-grp__teaser {
				max-width: none !important;
			}
		}

		.tsr__media {
			picture {
				animation: fade-in 0s ease-in;
				transition: transform 0s;
			}
		}

		.owl {
			&-stage {
				display: flex;
			}

			&-dots {
				margin-bottom: -$spacing-vertical-inc + $tsr-category-dot-space;
				margin-top: $tsr-category-dot-space;

				// negate the standard spacing and add dot spacing

				@include media(sm) {
					margin-bottom: -$spacing-vertical-inc-sm + $tsr-category-dot-space-sm;
					margin-top: $tsr-category-dot-space-sm;

					// negate the standard spacing and add dot spacing
				}
			}

			&-nav {
				height: 0;
				overflow-y: hidden;
			}

			@media (min-width: min-width(lg) + 60) {
				&-nav {
					height: auto;
					overflow: visible;
				}

				&-next,
				&-prev {
					position: absolute;
					top: calc(50% - #{$owl-arrow-size} / 2);
				}

				&-prev {
					left: 0;
					transform: translateX(-50vw) translateX(600px) translateX($owl-arrow-size / 2);
				}

				&-next {
					right: 0;
					transform: translateX(50vw) translateX(-600px) translateX(-$owl-arrow-size / 2);
				}
			}
		}

		// sine we need to make room for the dots we use increased spacing here
		.btc-bar {
			@include spacing-responsive-inc("margin", "top");
		}
	}

	&--services {
		.tsr-grp__teasers {
			justify-content: center;

			// negate bottom margin from .btncmp-round-label so height will kept
			margin-bottom: -1rem;
		}

		.btncmp-round-label {
			margin-bottom: 1rem;
		}
	}
}

// Variations
.tsr-grp {
	&--content-width {
		.tsr-grp__teasers {
			max-width: min-width(sm);
			padding-left: $grid-container-padding - $tsr-grp-content-width-padding;
			padding-right: $grid-container-padding - $tsr-grp-content-width-padding;
		}

		.tsr-grp__teaser {
			padding-left: $tsr-grp-content-width-padding;
			padding-right: $tsr-grp-content-width-padding;
		}

		.owl {
			&-prev {
				// IE doesn't support calc in translate
				transform: translateX(-50vw) translateX(#{min-width(sm) / 2}) translateX($owl-arrow-size / 2);
			}

			&-next {
				transform: translateX(50vw) translateX(-#{min-width(sm) / 2}) translateX(-$owl-arrow-size / 2);
			}
		}
	}
}

// theming
.tsr-grp-theme {
	&--dark {
		background-color: $primary-color;

		.ttl {
			&::before,
			&::after {
				background-color: $lines-global-bold-inverted;
			}

			&__headline {
				color: $white-color;
			}
		}

		.tsr-grp__no-results {
			color: $white-color;
		}

		.btc-bar {
			.btn--light {
				border-color: getThemeColorValue("btn-light--dark__border");
				color: $white-color;

				html:not(.can-touch) & {
					&:hover {
						background-color: $white-color;
						color: getThemeColorValue("btn-light--dark--hover__text");
					}
				}
			}
		}

		.owl {
			&-dot {
				border: 1px solid $white-color;

				html:not(.can-touch) & {
					&:hover {
						background-color: $white-color;
					}
				}

				&.active {
					background: $white-color;
				}
			}
		}
	}

	&--gray {
		background-color: $grey-light-color;
	}
}

// Load all the images together
.tsr-grp__teasers[data-lazy-load-aggregate="true"] .lazy-load-complete {
	opacity: 0 !important;
}

.tsr-grp__teasers.lazy-load-complete[data-lazy-load-aggregate="true"] .lazy-load-complete {
	opacity: 1 !important;
}

@import "modules/organisms/dark-site";
@import "modules/organisms/stage-detail";	//std
@import "modules/organisms/stage-fallback"; //stf
@import "modules/organisms/stage-overview";	//sto
@import "modules/organisms/stage-404";
@import "modules/organisms/login";

.st404 {
	@include spacing-responsive-base;

	display: flex;
	min-height: $stf-main-min-height;
	position: relative;

	@include media(sm) {
		min-height: $stf-main-min-height-f-sm;

		&__overlay {
			min-height: $stf-main-min-height-f-sm;
		}
	}


	@include media(md) {
		min-height: $stf-main-min-height-f-md;

		&__overlay {
			min-height: $stf-main-min-height-f-md;
		}
	}


	@include media(lg) {
		min-height: $stf-main-min-height-f-lg;

		&__overlay {
			min-height: $stf-main-min-height-f-lg;
		}
	}


	&__bg-image {
		font-family: 'object-fit: cover; object-position: 50% 50%;';

		// IE polyfill
		height: 100%;
		object-fit: cover;
		object-position: 50% 50%;
		position: absolute;
		width: 100%;
	}

	&__overlay {
		background-color: $generic-overlay-color;
		min-height: $stf-main-min-height;
		position: relative;
		width: 100%;
		z-index: 1;
	}

	&__content-container {
		@include make-container;

		padding-top: responsiveVal($hdr-header-height, xs);

		@include media(md) {
			padding-top: responsiveVal($hdr-header-height, md);
		}
	}

	&__content {
		@include word-wrap;

		color: $white-color;
		max-width: 602px;
		padding: ($base-size * 3) 0;

		@include media(sm) {
			padding-top: $base-size * 10;
		}


		@include media(lg) {
			padding-top: $base-size * 11;
		}
	}

	&__headline {
		@include font(display);

		margin-bottom: $base-size * 2;
		text-transform: uppercase;

		@include media(sm) {
			margin-bottom: $base-size * 4;
		}
	}

	&__form {
		position: relative;
	}

	&__subheadline {
		@include font(h2);

		margin-bottom: $base-size * 3;
		text-transform: uppercase;

		@include media(sm) {
			margin-bottom: $base-size * 4;
		}
	}

	.inp-search {
		&__input {
			@include media(sm) {
				height: 45px;
			}
		}

		&__icon {
			@include media(sm) {
				height: 45px;
				width: 45px;
			}
		}
	}
}

.std {
	&-breadcrumbs {
		@include font(std-breadcrumb);
		@include reset-list;

		color: $black-38-color;

		&__item {
			display: inline-block;

			&:not(:last-child)::after {
				content: "";
				display: inline-block;
				padding: 0 $std-breadcrumbs-after-padding-h;
			}

			html:not(.can-touch) & {
				& > a:hover {
					color: $secondary-color;
					transition: color $default-transition;
				}
			}
		}
	}

	&-button {
		@include font(std-button);

		text-transform: uppercase;

		&:hover {
			opacity: $std-hover-opacity;
		}
	}

	&-controls {
		color: $black-30-color;
		display: flex;
		justify-content: space-between;
		margin-bottom: $std-controls-margin-bottom;

		&__icons {
			display: flex;
			font-size: 2rem;
		}

		&__icon {
			cursor: pointer;
			margin-left: .5rem;

			&:hover {
				opacity: $std-hover-opacity;
			}
		}

		@include media(sm) {
			margin-bottom: 0;
		}
	}

	&-head {
		&__breadcrumbs {
			margin-bottom: $std-breadcrumbs-margin-bottom;
		}

		@include media(sm) {
			&__breadcrumbs {
				margin-bottom: $std-breadcrumbs-margin-bottom-sm;
			}
		}
	}

	&-stage {
		@include make-container;

		min-height: 190px;
		padding-top: $std-stage-padding-top;

		@include media(md) {
			padding-top: $std-stage-padding-top-md;
		}

		&__head {
			margin-bottom: $std-head-margin-bottom;
		}
	}
}

.std {
	&-social {
		&__list {
			display: flex;
			flex-wrap: wrap;
			justify-content: space-around;
			justify-content: space-evenly;
			list-style: none;
			margin: 0;
			padding: 0;
		}

		&__item {
			flex-basis: 33%;
			margin-bottom: 2rem;
			padding: 0 .5rem;
			text-align: center;
		}
	}
}

.std {
	&-social-icon {
		&__title {
			color: $black-60-color;
			margin-top: .5rem;
			text-transform: uppercase;
		}
	}
}

.stf {
	&-carousel {
		bottom: .5rem;
		left: 50%;
		list-style: none;
		margin: 0;
		max-width: responsiveVal($stf-carousel-max-width, xs);
		padding: 0 ($grid-gutter-width / 2);
		position: absolute !important; //owl carousel css overwrite
		transform: translateX(-50%);

		.owl {
			&-nav {
				visibility: hidden;
			}
		}

		@include media(sm) {
			max-width: responsiveVal($stf-carousel-max-width, sm);
		}

		@include media(md) {
			padding: 0;
		}
	}

	&-opening {
		@include font(stf-opening);

		background-color: $generic-overlay-color;
		bottom: 0;
		color: $white-color;
		display: none;
		padding: $base-size;
		position: absolute;
		width: 100%;

		&.state-opening,
		&.state-closing {
			align-items: center;
			display: flex;
			justify-content: center;
		}
	}

	&-main {
		align-items: flex-end;
		min-height: $stf-main-min-height;
		position: relative;

		&__carousel {
			height: 124px;
			opacity: 0;
			transition: opacity 1.5s;
			transition-delay: .25s;
			visibility: hidden;

			&--single {
				animation: fade-in .75s .75s both;
				height: auto;
				visibility: visible;
				width: 100%;
				z-index: 1;
			}
		}

		.owl-dots {
			margin-top: .5rem;
			transform: scale(.75);
		}

		// fade slider in when loaded
		.owl-loaded {
			height: auto;
			opacity: 1;
			visibility: visible;
		}
	}

	@include media(sm) {
		&-main {
			// IE11 flexbox align-items fix
			height: 100px;
			min-height: $stf-main-min-height-f-sm;

			.owl-dots {
				margin-top: 1rem;
				padding-bottom: 1rem;
				transform: scale(1);
			}
		}
	}


	@include media(md) {
		&-main {
			align-items: center;
			min-height: $stf-main-min-height-f-md;
		}

		&-carousel {
			bottom: 5%;
			left: auto;
			right: 5%;
			transform: translate(0);

			.owl-nav {
				visibility: visible;
			}
		}
	}


	@include media(lg) {
		&-main {
			min-height: $stf-main-min-height-f-lg;
		}
	}


	&-fallback {
		background-color: $grey-light-color;
		overflow: hidden;
		position: relative;

		&__background {
			background: linear-gradient(hsla(0, 0, 0, .5), hsla(0, 0, 0, .5)) no-repeat;
			background-position: center;
			background-size: cover;
			height: 100%;
			left: 0;
			position: absolute;
			top: 0;
			width: 100%;

			img {
				font-family: 'object-fit: cover;'; // IE polyfill
				height: 100%;
				object-fit: cover;
				width: 100%;
			}
		}

		&--no-slider {
			.stf-fallback__background {
				height: 100%;
			}
		}
	}
}

.sto {
	&-background {
		display: block;
		font-family: 'object-fit: cover;';

		// IE polyfill
		height: 100%;
		object-fit: cover;
		position: absolute;
		top: 0;
		width: 100%;
		z-index: -2;
	}

	&-teaser {
		color: $white-color;
		text-align: center;

		&__title {
			@include font(display);

			margin: $sto-teaser-title-margin-v 0;
			text-transform: uppercase;

			&::after {
				background-color: $sto-teaser-rule-color;
				content: "";
				display: block;
				height: $sto-teaser-rule-height;
				margin: auto;
				margin-top: $sto-teaser-rule-margin-top;
				width: $sto-teaser-rule-width;
			}
		}

		&__text {
			@include font(lead-inverted);

			@include media(md) {
				@include make-col;
				@include make-col-span(10);
				@include make-col-offset(1);
			}
		}
	}

	&-container {
		@include make-container;

		min-height: $sto-container-min-height;
		padding-bottom: $sto-container-padding-bottom;
		padding-top: $sto-container-padding-top;
	}

	&-stage {
		overflow-x: hidden;
		position: relative;

		&::after {
			// dimmer
			background-color: $black-color;
			content: "";
			display: block;
			height: 100%;
			opacity: .35;
			position: absolute;
			top: 0;
			width: 100%;
			z-index: -1;
		}
	}

	@include media(sm) {
		&-container {
			display: flex;
			flex-direction: column;
			justify-content: center;
			min-height: $sto-container-height-sm;
		}
	}


	@include media(md) {
		&-container {
			padding-top: $sto-container-padding-top-md;
		}
	}
}

.l-container {
	@include make-container;
	@include spacing-responsive-base("margin", "top");
	@include spacing-responsive-base("margin", "bottom");
}

body > .frame {
	position: relative;
	z-index: $z-index-page-content;
}

.pge {
	&-wall {
		@include make-row;

		margin-bottom: -$tsr-margin-bottom; // negate item bottom spacing

		&__item {
			@include teaser-wrapper;

			display: flex;
			margin-bottom: $tsr-margin-bottom;

			.tsr-solid {
				width: 100%;
			}
		}

		&__no-results {
			@include font(src-title);
			@include spacing-responsive-inc;

			color: $grey-color;
			text-align: center;
			text-transform: uppercase;
			width: 100%;
		}

		& + .btc-bar {
			@include spacing-responsive-base("margin", "top");
		}
	}

	// form containers
	&-form-container {
		margin-bottom: 5rem;
		margin-top: calc(#{responsiveVal($hdr-header-height, xs)} + 3rem);

		// Add borders to the content (from sm for small contents/ md for others)
		@include media(sm) {
			margin-bottom: 10rem;

			& > [class$='--small'] {
				border: 1px solid $lines-global;
			}
		}

		@include media(md) {
			margin-top: calc(#{responsiveVal($hdr-header-height, md)} + 5rem);

			& > * {
				border: 1px solid $lines-global;
			}
