.pda {
	&-header {
		border-bottom: 1px solid $lines-global;
		height: 70px;
		margin-bottom: 2.5rem;
	}

	&-section {
		&__heading {
			margin: .1rem 0 1.5rem 0;
		}

		// TODO use class in _section-separator mixin 
		&--separator {
			&-bottom {
				@include section-separator(bottom, $base-size * 2.4, $lines-global);
			}

			&-bottom--nm {
				@include section-separator(bottom, 0, $lines-global);
			}

			&-top {
				@include section-separator(top, $base-size * 2.4, $lines-global);
			}

			&-top--nm {
				@include section-separator(top, 0, $lines-global);
			}
		}
	}

	&-form {
		&__separator {
			border: 0;
			border-top: 1px solid $lines-global;
			height: 0;
			margin-bottom: 2rem;
			margin-top: 1rem;
		}

		&__label--bold {
			font-weight: 600;
		}

		&__checkbox-group {
			margin-top: 1rem;
			padding-left: 1.5rem;
		}

		&__hint {
			color: rgba(50, 50, 50, .65);
			margin-top: 2rem;
			padding-left: 0;
		}
	}
}

.no-pointer {
	cursor: default;
}

.btc-bar {
	&--mt-larger {
		margin-top: 1.2rem;
	}
}

.mdl {
	&-modal {
		&--change-password {
			width: 600px;
			@include media-down(md) {
				width: 100%;
			}
		}
		&--delete-account {
			width: 480px;
			@include media-down(md) {
				width: 100%;
			}
		}
	}
}

@import "modules/plugins/grid";

/* Communication groups */
