		}
	}

	[data-web-view="hide-mobile"] {
		display: none !important;

		@include media(sm) {
			display: block !important;
		}
	}
}

.app-view {
	[data-app-view="hide"] {
		display: none !important;
	}

	&--v1 {
		[data-app-view="hide-v1"] {
			display: none !important;
		}
	}

	&--v2 {
		[data-app-view="hide-v2"] {
			display: none !important;
		}
	}
}

.row {
	@include make-row;

	[class*="col-"] {
		@include make-col;
	}
}

@for $i from 1 through 12 {
	.col-xs-#{$i} {
		@include make-col-span($i);
	}
}

@include media(sm) {
	@for $i from 1 through 12 {
		.col-sm-#{$i} {
			@include make-col-span($i);
		}
	}
}


@include media(md) {
	@for $i from 1 through 12 {
		.col-md-#{$i} {
