@mixin hidden {
	display: none !important;
}

@mixin visuallyhidden {
	border: 0;
	clip: rect(0 0 0 0);
	height: 1px;
	margin: -1px;
	overflow: hidden;
	padding: 0;
	position: absolute !important;
	width: 1px;
}

.hidden {
	@include hidden;
}

.visuallyhidden {
	@include visuallyhidden;
}

.faded-out {
	opacity: 0 !important;
	pointer-events: none;
}

html.mobile .visuallyhidden-mobile {
	@include visuallyhidden;
}

html.mobile .hidden-mobile {
	@include hidden;
}

html:not(.mobile) .visuallyhidden-desktop {
	@include visuallyhidden;
}

html:not(.mobile) .hidden-desktop {
	@include hidden;
}

@each $size in sm, md, lg {
	@include media-up($size) {
		.hidden-#{$size}-up {
			display: none !important;
		}
	}

	@include media-down($size) {
		.hidden-#{$size}-down {
			display: none !important;
		}
	}
}

@mixin fixed-ratio($w, $h) {
	height: auto;

	&::before {
		content: "";
		display: block;
		padding-top: $h / $w * 100%;
	}
}

@mixin hover {
	html:not(.can-touch) & {
		&:hover {
			@content;
		}
	}
}

@mixin ie11-only {
	@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
		@content;
	}
}

// Padding and margin util classes
@each $direction in top,right,bottom,left {
