$banderole-bg-color: getThemeColorValue("highlight-color");
$banderole-bg-darker-color: darken($banderole-bg-color, 15%);
$banderole-bg-color--disabled: hsl(0, 0%, 20%);
$banderole-bg-darker-color--disabled: hsl(0, 0%, 15%);
$banderole-text-color: color-contrast($banderole-bg-color, $black-75-color, $white-color);
$banderole-size: 150px;
$banderole-envelope: 6px;

.bdr {
	&-banderole {
		height: $banderole-size;
		overflow: hidden;
		position: absolute;
		width: $banderole-size;
		z-index: $z-index-page-content;

		&::before, &::after {
			border: $banderole-envelope/2 solid $banderole-bg-darker-color;
			content: '';
			display: block;
			position: absolute;
			z-index: 0;
		}

		&__container {
			background-color: $banderole-bg-color;
			box-shadow: 0 1px 3px rgba(0, 0, 0, .3);
			color: $banderole-text-color;
			display: block;
			@include font(banderole-text);
			font-weight: 600;
			height: 48px;
			line-height: .9rem;
			padding: 15px 0;
			position: absolute;
			text-align: center;
			text-transform: uppercase;
			width: 225px;
		}

		&__text--disabled {
			display: none;
		}

		&--disabled {
			&::before, &::after {
				border-color: $banderole-bg-darker-color--disabled;
			}
		}

		&--disabled  &__container {
			background-color: $banderole-bg-color--disabled;
		}

		&--disabled &__text--default {
			display: none;
		}

		&--disabled &__text--disabled {
			display: block;
		}

		/* top left*/
		&--top-left {
			left: -$banderole-envelope;
			top: -$banderole-envelope;

			&::before, &::after {
				border-left-color: transparent;
				border-top-color: transparent;
			}

			&::before {
				right: 0;
				top: 0;
			}

			&::after {
				bottom: 0;
				left: 0;
			}
		}

		&--top-left &__container {
			@include flexbox;
			@include align-items(center);
			@include justify-content(center);
			right: -25px;
			top: 30px;
			transform: rotate(-45deg);
		}

		&--top-left &__text {
			max-width: 70px;
		}
	}
}

.burger {
	cursor: pointer;
	padding: $burger-padding;

	&__container {
		height: $burger-height;
		position: relative;
		width: $burger-width;
	}

	&__inner {
		top: 0px;

		&::before, &::after {
			content: "";
			display: block;
			position: absolute;
		}

		&, &::before, &::after {
			background-color: getThemeColorValue("header__text");
			height: 4px;
			transition: opacity, transform .3s ease;
			width: 100%;
		}

		&::before {
			top: 10px;
		}

		&::after {
			top: 20px;
		}

		body.menu-active & {
			transform: translateY(10px) rotate(45deg);

			&::before {
				opacity: 0;
				transform: translate(-6px, -6px) rotate(-45deg);
			}

			&::after {
				transform: translateY(-20px) rotate(-90deg);
			}
		}
	}
}

.btn {
	// Reset unusual Firefox-on-Android default style; see https://github.com/necolas/normalize.css/issues/214
	background-image: none;
	color: inherit;
	cursor: pointer;
	display: inline-block;
	overflow: hidden;
	position: relative;
	touch-action: manipulation;
	transition: $hover-transition;
	vertical-align: middle;

	&--facebook {
		background-color: $btn-social-facebook-color;
	}

	&--foursquare {
		background-color: $btn-social-foursquare-color;
	}

	&--yelp {
		background-color: $btn-social-yelp-color;
	}

	&--googleplus {
		background-color: $btn-social-googleplus-color;
	}

	&--instagram {
		background-color: $btn-social-instagram-color;
	}

	&--pinterest {
		background-color: $btn-social-pinterest-color;
	}

	&--twitter {
		background-color: $btn-social-twitter-color;
	}

	&--youtube {
		background-color: $btn-social-youtube-color;
	}

	&--email {
		background-color: $btn-social-email-color;
	}

	&--whatsapp {
		background-color: $btn-social-whatsapp-color;
	}

	&--dark-hover {
		overflow: hidden; // for round buttons

		&:before {
			background-color: hsla(0, 0, 0, 0);
			content: '';
			height: 100%;
			left: 0;
			position: absolute;
			top: 0;
			transition: $hover-transition;
			width: 100%;
		}

		&:hover::before {
			background-color: hsla(0, 0, 0, .3);
		}
	}

	&[disabled],
	&.disabled {
		opacity: .5;
		pointer-events: none;
	}
}

.btn {
	.btn__inner {
		display: inline-block;
		text-align: left;
	}

	.btn__icon,
	.btn__icon::before,
	.btn__label {
		display: inline-block; // all lined-up elements share the same display to get the same height
	}

	.btn__icon {
		position: relative;
		vertical-align: top;

		// let the empty inline element acquire the correct height if it had some text in it
		&::before {
			content: ' ';
			width: 0;
		}

		svg {
			color: currentColor;
			fill: currentColor;
			font-size: inherit;
			height: $btn-icon-size;
			margin-top: -$btn-icon-size / 2;
			overflow: hidden;
			position: absolute;
			right: $icon-inline-spacing;
			top: 50%;
			width: $btn-icon-size;
		}

		&--spinner {
			display: none;
		}

		&--follows {
			svg {
				left: $icon-inline-spacing;
			}
		}
	}

	// variant for links in copy text
	&--copy {
		color: $primary-transparent-color;
		font-weight: bold;
		text-decoration: underline;
	}

	&--icon {
		.btn__inner {
			// the padding is on the inner wraper just for multiline capability
			padding-left: calc(#{$btn-icon-size} + #{$icon-inline-spacing});
		}

		&-follows {
			.btn__inner {
				// the padding is on the inner wraper just for multiline capability
				padding-right: calc(#{$btn-icon-size} + #{$icon-inline-spacing});
			}
		}
	}
}

.btn {

	&--badge {
		overflow: visible;
		position: relative;

		&[data-badge]::before {
			background-color: getThemeColorValue('header__text');
			border-radius: 1em;
			color: getThemeColorValue('header');
			content: attr(data-badge);
			font-size: 12px;
			line-height: 1.4;
			margin-left: 10px;
			min-width: 1em;
			padding: 1px 4px 0px 4px;
			text-align: center;
			transition: $default-transition;
		}

		&[data-badge='0']::before {
			display: none;
		}

		@include media(md) {
			&[data-badge]::before {
				font-size: 8px;
				position: absolute;
				right: 0;
				top: 0;
				transform: translate(80%, -85%);
			}
		}
	}

	// rectangular variants
	&--light, &--solid {
		@include font(button);
		line-height: 1.5;
		padding: $btn-padding-vertical $btn-padding-horizontal;
		text-align: center;
		text-transform: uppercase;
	}

	&--light {
		background-color: getThemeColorValue('btn-light__bg');
		border: 1px solid getThemeColorValue('btn-light__border');
		color: getThemeColorValue('btn-light__text');

		@include hover {
			background-color: getThemeColorValue('btn-light--hover__bg');
			color: getThemeColorValue('btn-light--hover__text');
		}

	}

	&--solid {
		background-color: getThemeColorValue('btn-solid__bg');
		border: 1px solid;
		border-color: getThemeColorValue('btn-solid__border');
		color: getThemeColorValue('btn-solid__text');

		html:not(.can-touch) & {
			&:hover {
				background-color: getThemeColorValue('btn-solid--hover__bg');
				color: getThemeColorValue('btn-solid--hover__text');
			}
		}
	}

	&--solid.btn--important {
		background-color: getThemeColorValue('btn-solid__bg') !important;
		border: 1px solid !important;
		border-color: getThemeColorValue('btn-solid__border') !important;
		color: getThemeColorValue('btn-solid__text') !important;

		@include hover {
			background-color: getThemeColorValue('btn-solid--hover__bg') !important;
			color: getThemeColorValue('btn-solid--hover__text') !important;
		}
	}

	&--inverse {
		background-color: getThemeColorValue('btn-inverse__bg');
		border-color: getThemeColorValue('btn-inverse__border');
		color: getThemeColorValue('btn-inverse__text');

		html:not(.can-touch) & {
			&:hover {
				background-color: getThemeColorValue('btn-inverse--hover__bg');
				color: getThemeColorValue('btn-inverse--hover__text');
			}
		}
	}

	&--small {
		font-size: .875rem;
		padding: .25rem 1.5rem;
	}

	&--big {
		padding: .5625rem 2rem;
	}

	// full-width variant
	&--full-width {
		text-align: center;
		width: 100%;

		&-xs {
			@include media-down(sm) {
				width: 100%;
			}
		}
	}


	&--spinner {
		.icn-spinner {
			display: block;
		}

		.btn__icon {
			display: none;

			&--spinner {
				display: inline-block;

				svg {
					animation: spinner-rotate 1s infinite linear, spinner-stroke 1.5s alternate infinite linear;
					fill: transparent;
					stroke: currentColor;
					stroke-dasharray: 140 189;
					stroke-width: 8;
				}
			}
		}
	}
}

.btn {
	&-round {
		border-radius: 50%;
		color: getThemeColorValue('btn-solid__text');
		cursor: pointer;
		display: inline-block;
		height: $btn-round-size;
		width: $btn-round-size;

		svg {
			padding: 15%;
		}

		&--small {
			height: $btn-round-size--small;
			width: $btn-round-size--small;
		}

		&--hoverable {
			transition: $hover-transition;
			@include hover{
				background-color: getThemeColorValue('btn-solid--hover__bg');
				color: getThemeColorValue('btn-solid--hover__text');
			}
		}

		&--social {
			height: $btn-round-size * 1.25;
			position: relative;
			width: $btn-round-size * 1.25;
		}
	}

	&-to-top {
		background-color: getThemeColorValue('btn-solid__bg');
		bottom: $btn-round-size / 2;
		box-shadow: 0px 0px 3px 0px rgba(255, 255, 255, .5);
		cursor: pointer;
		padding: $btn-round-size * .25;
		position: fixed;
		right: $btn-round-size / 2;
		z-index: $z-index-btn-to-top;
	}

	&-play {
		align-items: center;
		background-color: $primary-color;
		border: 2px solid $white-color;
		border-radius: 100%;
		display: flex;
		height: $btn-round-size;
		justify-content: center;
		left: 50%;
		margin: (-$btn-round-size / 2) 0 0 (-$btn-round-size / 2);
		position: absolute;
		text-align: center;
		top: 50%;
		width: $btn-round-size;

		&::before {
			border-color: transparent;
			border-left-color: $white-color;
			border-style: solid;
			border-width: 10px 0 10px 18px;
			content: '';
			display: block;
			height: 0;
			margin-left: 4px;
			width: 0;
		}
	}
}

// Convert a color to the format rgb(xx,xx,xx)
@function colorToRGB($color) {
	@return 'rgb(#{floor(red($color))}, #{floor(green($color))}, #{floor(blue($color))})'
}

.btn {
	&-tooltip {
		overflow: visible; // IE11 button don't show pseudo elements otherwise
		position: relative;

		&::after {
			@include font(small);

			background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 100 50' width='100' height='50'><path fill='#{colorToRGB($btn-tooltip--bg)}' d='M25 25L0 0L50 0L100 0L75 25L50 50L25 25Z'/></svg>"), linear-gradient(to top, transparent, transparent #{$btn-tooltip-arrow - .2rem}, #{$btn-tooltip--bg} #{$btn-tooltip-arrow - .2rem}, #{$btn-tooltip--bg} );
			background-position: bottom center;
			background-repeat: no-repeat;
			background-size: #{$btn-tooltip-arrow * 2} $btn-tooltip-arrow, 100%;
			color: white;
			content: attr(data-tooltip-content);
			left: 50%;
			opacity: 0;
			padding: .5rem 1rem 1.25rem 1rem;
			position: absolute;
			top: 0;
			transform: translate(-50%, -100%) translateY(-.5rem);
			transition: opacity $default-transition;
			visibility: hidden;
			white-space: nowrap;
		}

		@include hover {
			&::after {
				opacity: 1;
				visibility: visible;
			}
		}

		&--no-margin {
			@include hover {
				&::after {
					transform: translate(-50%, -100%);
				}
			}
		}
	}
}

