		&:before {
			background-color: hsla(0, 0, 0, 0);
			content: '';
			height: 100%;
			left: 0;
			position: absolute;
			top: 0;
			transition: $hover-transition;
			width: 100%;
		}

		&:hover::before {
			background-color: hsla(0, 0, 0, .3);
		}
	}

	&[disabled],
	&.disabled {
		opacity: .5;
		pointer-events: none;
	}
}

.btn {
	.btn__inner {
		display: inline-block;
		text-align: left;
	}

	.btn__icon,
	.btn__icon::before,
	.btn__label {
		display: inline-block; // all lined-up elements share the same display to get the same height
	}

	.btn__icon {
		position: relative;
		vertical-align: top;

		// let the empty inline element acquire the correct height if it had some text in it
		&::before {
			content: ' ';
			width: 0;
		}

		svg {
			color: currentColor;
			fill: currentColor;
			font-size: inherit;
			height: $btn-icon-size;
			margin-top: -$btn-icon-size / 2;
			overflow: hidden;
			position: absolute;
			right: $icon-inline-spacing;
			top: 50%;
			width: $btn-icon-size;
		}

		&--spinner {
			display: none;
		}

		&--follows {
			svg {
				left: $icon-inline-spacing;
			}
		}
	}

	// variant for links in copy text
	&--copy {
		color: $primary-transparent-color;
		font-weight: bold;
		text-decoration: underline;
	}

	&--icon {
		.btn__inner {
			// the padding is on the inner wraper just for multiline capability
			padding-left: calc(#{$btn-icon-size} + #{$icon-inline-spacing});
		}

		&-follows {
			.btn__inner {
				// the padding is on the inner wraper just for multiline capability
				padding-right: calc(#{$btn-icon-size} + #{$icon-inline-spacing});
			}
		}
	}
}

.btn {

	&--badge {
		overflow: visible;
		position: relative;

		&[data-badge]::before {
			background-color: getThemeColorValue('header__text');
			border-radius: 1em;
			color: getThemeColorValue('header');
			content: attr(data-badge);
			font-size: 12px;
			line-height: 1.4;
			margin-left: 10px;
			min-width: 1em;
			padding: 1px 4px 0px 4px;
			text-align: center;
			transition: $default-transition;
		}

		&[data-badge='0']::before {
			display: none;
		}

		@include media(md) {
			&[data-badge]::before {
				font-size: 8px;
				position: absolute;
				right: 0;
				top: 0;
				transform: translate(80%, -85%);
			}
		}
	}

	// rectangular variants
	&--light, &--solid {
		@include font(button);
		line-height: 1.5;
		padding: $btn-padding-vertical $btn-padding-horizontal;
		text-align: center;
		text-transform: uppercase;
	}

	&--light {
		background-color: getThemeColorValue('btn-light__bg');
		border: 1px solid getThemeColorValue('btn-light__border');
		color: getThemeColorValue('btn-light__text');

		@include hover {
			background-color: getThemeColorValue('btn-light--hover__bg');
			color: getThemeColorValue('btn-light--hover__text');
		}

	}

	&--solid {
		background-color: getThemeColorValue('btn-solid__bg');
		border: 1px solid;
		border-color: getThemeColorValue('btn-solid__border');
		color: getThemeColorValue('btn-solid__text');

		html:not(.can-touch) & {
			&:hover {
				background-color: getThemeColorValue('btn-solid--hover__bg');
				color: getThemeColorValue('btn-solid--hover__text');
			}
		}
	}

	&--solid.btn--important {
		background-color: getThemeColorValue('btn-solid__bg') !important;
		border: 1px solid !important;
		border-color: getThemeColorValue('btn-solid__border') !important;
		color: getThemeColorValue('btn-solid__text') !important;

		@include hover {
			background-color: getThemeColorValue('btn-solid--hover__bg') !important;
			color: getThemeColorValue('btn-solid--hover__text') !important;
		}
	}

	&--inverse {
		background-color: getThemeColorValue('btn-inverse__bg');
		border-color: getThemeColorValue('btn-inverse__border');
		color: getThemeColorValue('btn-inverse__text');

		html:not(.can-touch) & {
			&:hover {
				background-color: getThemeColorValue('btn-inverse--hover__bg');
				color: getThemeColorValue('btn-inverse--hover__text');
			}
		}
	}

	&--small {
		font-size: .875rem;
		padding: .25rem 1.5rem;
	}

	&--big {
		padding: .5625rem 2rem;
	}

	// full-width variant
	&--full-width {
		text-align: center;
		width: 100%;

		&-xs {
			@include media-down(sm) {
				width: 100%;
			}
		}
	}


	&--spinner {
		.icn-spinner {
			display: block;
		}

		.btn__icon {
			display: none;

			&--spinner {
				display: inline-block;

				svg {
					animation: spinner-rotate 1s infinite linear, spinner-stroke 1.5s alternate infinite linear;
					fill: transparent;
					stroke: currentColor;
					stroke-dasharray: 140 189;
					stroke-width: 8;
				}
			}
		}
	}
}

.btn {
	&-round {
		border-radius: 50%;
		color: getThemeColorValue('btn-solid__text');
		cursor: pointer;
		display: inline-block;
		height: $btn-round-size;
		width: $btn-round-size;

		svg {
			padding: 15%;
		}

		&--small {
			height: $btn-round-size--small;
			width: $btn-round-size--small;
		}

		&--hoverable {
			transition: $hover-transition;
			@include hover{
				background-color: getThemeColorValue('btn-solid--hover__bg');
				color: getThemeColorValue('btn-solid--hover__text');
			}
		}

		&--social {
			height: $btn-round-size * 1.25;
			position: relative;
			width: $btn-round-size * 1.25;
		}
	}

	&-to-top {
		background-color: getThemeColorValue('btn-solid__bg');
		bottom: $btn-round-size / 2;
		box-shadow: 0px 0px 3px 0px rgba(255, 255, 255, .5);
		cursor: pointer;
		padding: $btn-round-size * .25;
		position: fixed;
		right: $btn-round-size / 2;
		z-index: $z-index-btn-to-top;
	}

	&-play {
		align-items: center;
		background-color: $primary-color;
		border: 2px solid $white-color;
		border-radius: 100%;
		display: flex;
		height: $btn-round-size;
		justify-content: center;
		left: 50%;
		margin: (-$btn-round-size / 2) 0 0 (-$btn-round-size / 2);
		position: absolute;
		text-align: center;
		top: 50%;
		width: $btn-round-size;

		&::before {
			border-color: transparent;
			border-left-color: $white-color;
			border-style: solid;
			border-width: 10px 0 10px 18px;
			content: '';
			display: block;
			height: 0;
			margin-left: 4px;
			width: 0;
		}
	}
}

// Convert a color to the format rgb(xx,xx,xx)
@function colorToRGB($color) {
	@return 'rgb(#{floor(red($color))}, #{floor(green($color))}, #{floor(blue($color))})'
}

.btn {
	&-tooltip {
		overflow: visible; // IE11 button don't show pseudo elements otherwise
		position: relative;

		&::after {
			@include font(small);

			background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 100 50' width='100' height='50'><path fill='#{colorToRGB($btn-tooltip--bg)}' d='M25 25L0 0L50 0L100 0L75 25L50 50L25 25Z'/></svg>"), linear-gradient(to top, transparent, transparent #{$btn-tooltip-arrow - .2rem}, #{$btn-tooltip--bg} #{$btn-tooltip-arrow - .2rem}, #{$btn-tooltip--bg} );
			background-position: bottom center;
			background-repeat: no-repeat;
			background-size: #{$btn-tooltip-arrow * 2} $btn-tooltip-arrow, 100%;
			color: white;
			content: attr(data-tooltip-content);
			left: 50%;
			opacity: 0;
			padding: .5rem 1rem 1.25rem 1rem;
			position: absolute;
			top: 0;
			transform: translate(-50%, -100%) translateY(-.5rem);
			transition: opacity $default-transition;
			visibility: hidden;
			white-space: nowrap;
		}

		@include hover {
			&::after {
				opacity: 1;
				visibility: visible;
			}
		}

		&--no-margin {
			@include hover {
				&::after {
					transform: translate(-50%, -100%);
				}
			}
		}
	}
}

.icn-icon {
	color: currentColor;
	fill: currentColor;
	height: 1em;
	overflow: hidden;
	vertical-align: -.15em;
	width: 1em;

	&--fit {
		height: 100%;
		vertical-align: baseline;
		width: 100%;
	}
}

.icn-icon--hover2 {
	transition: $hover-transition;

	@include hover {
		color: $secondary-color;
	}
}

.icn--svg, .icn-container {
	svg {
		height: 100%;
		width: 100%;
	}

	svg, path, polygon, rect, circle {
		fill: currentColor !important;
	}
}

.icn-like {
	cursor: pointer;

	&__container {
		transition: opacity .25s .25s;

		// Hide for Services and other items, that cannot be bookmarked
		&[data-uid=""], &[data-uid="-"], &:not([data-uid]) {
			display: none;
		}
	}

	.border {
		fill: currentColor;
	}

	.solid-fill {
		fill: transparent;
	}

	&:not(.icn-like--active):hover {
		.solid-fill {
			fill: currentColor;
			opacity: .15;
		}
	}

	&--active {
		animation: icn-heartbeat ease-in-out .25s;

		.solid-fill {
			fill: currentColor;
		}
	}
}


