			outline: none;
			position: relative;
			width: 100%;
			z-index: 1;

			@include media(md) {
				font-size: .875rem;
			}


			&::-ms-expand {
				display: none;
			}
		}

		&--disabled {
			cursor: not-allowed;
			opacity: .5;

			select {
				pointer-events: none;
			}
		}

		&--language {
			border: 1px solid $black-extra-light-color;
