			background-color: $white-color;
			border: 1px $grey-color solid;

			&::after {
				background-color: $grey-color;
				content: "";
				height: calc(100% - 2 * #{$inp-select-dropdown-padding});
				pointer-events: none;
				position: absolute;
				right: 2rem;
				top: $inp-select-dropdown-padding;
				width: 1px;
				z-index: 2;
			}
		}

		&--dropdown &__select {
			padding: $inp-select-dropdown-padding 2.5rem $inp-select-dropdown-padding .5rem;
		}
	}
}

// Hide native show password in IE
::-ms-reveal {
	display: none;
}

.inp {
	&-list {
		@include reset-list;

		background-color: $white-color;
		box-shadow: 4px 4px 8px transparentize($black-medium-color, .6);
		padding-bottom: $mnu-dropdown-padding-bottom;
		width: $mnu-dropdown-width;

		&__item {
			color: $black-medium-color;
			transition: background-color .3s ease;

			&:hover, &:focus {
				background-color: $grey-light-color;
			}

			&[data-status="ACT"] {
				color: transparentize($black-color, .5);

				@include hover {
					background-color: transparent;
				}
			}
		}

		&__text {
			cursor: pointer;
			display: block;
			padding: $mnu-dropdown-item-padding-v $mnu-dropdown-item-padding-h;
		}

		&__separator {
			@include font(inp-dropdown-separator);

			border-top: 1px solid $grey-color;
			color: $black-38-color;
			padding: .5rem;
		}
	}

	&-dropdown {
		&__trigger {
			cursor: pointer;
		}
		&__list {
			margin-top: .75rem;
			opacity: 0;
			position: absolute;
			transition: opacity $mnu-dropdown-opacity-transition, visibility $mnu-dropdown-visibility-transition;
			visibility: hidden;
			z-index: 11;

			.dropdown-active ~ & {
				opacity: 1;
				visibility: visible;
			}
		}
	}
}

.inp {
	&-search {
		display: flex;
		position: relative;

		&__input {
			@include font(lead);
			@include placeholder($placeholder-global-color, italic);

			background-color: transparent;
			border: none;
			border-radius: 0;
			flex: 1 1 0px;
			height: calc(2rem - 1px);
			min-width: 0;

			// clear icon by default in 'X' in IE
			&::-ms-clear {
				height: 0;
				width: 0;
			}
		}

		&__icon {
			color: $black-light-color;
			font-size: 1.5em;
			height: calc(2rem - 1px);
			max-width: 2rem;

			//IE bug
			transition: opacity $hover-transition;
		}

		// Search autosuggest
		&__dropdown {
			bottom: 0;
			left: 0;
			position: absolute;
			transform: translateY(100%);
			width: 100%;
			z-index: 50;
		}

		// hide/show the right icon (search, clear or spinner)
		.js-icon-close {
			cursor: pointer;
			display: none;
		}

		.js-icon-spinner {
			display: none;
		}

		&__input:not([value=""]) ~ &__icon {
			.js-icon-close {
				display: inline;
			}
			.js-icon-search {
				display: none;
			}
		}

		&--active {
			.js-icon-close {
				display: none !important;
			}
			.js-icon-search {
				display: none !important;
			}
			.js-icon-spinner {
				display: inline;
			}
		}

		&--default {
			background-color: $white-color;

			.inp-search__input {
				padding-left: $base-size / 2;

				@include media(sm) {
					padding-left: $base-size;
				}
			}
		}

		&--light {
			border-bottom: 2px $grey-color solid;
		}
	}
}

