		.inp-filter-highlight {
			background: yellow;
		}

		.inp-filter-element:not(.inp-filter-match) {
			display: none;
		}
	}
}

.mda {
	&-frame {
		background-color: $white-color;
		border: 1px solid $lines-global;
		height: $shl-logo-height;
		position: relative;

		&__container {
			// background for lazy-loading
			background-position: 50% 50%;
			background-repeat: no-repeat;
			background-size: contain;
			height: 60%;
			left: 20%;
			position: absolute;
			top: 20%;
			transform: translate3d(0, 0, 0); //Safari pixeled base64
			width: 60%;
		}

		img, &__media {
			font-family: 'object-fit: contain;'; // IE polyfill
			height: 100%;
			object-fit: contain;
			position: absolute;
			top: 0;
			width: 100%;
		}

		&--16-9 {
			@include fixed-ratio(16, 9);
		}

		&--4-3 {
			@include fixed-ratio(4, 3);
		}

		&--1-1 {
			@include fixed-ratio(1, 1);
		}

		&--5-6 {
			@include fixed-ratio(5, 6);
		}

		&--no-border {
			border-width: 0;
		}

		&--grey {
			background-color: $grey-medium-color;
			border-color: $grey-medium-color;

			.mda-frame__media {
				color: $grey-light-color;
			}
		}

		&--small-padding {
			.mda-frame__media {
				padding: 6px;
			}
		}
	}
}

