.tbl-table {
	@include section-margin;

	table {
		border-collapse: collapse;
		width: 100%;
	}

	thead {
		background-color: $grey-medium-color;
		color: $black-color;
		th {
			border-right: 1px solid $white-color;
			&:last-child {
				border-right: 0;
			}
		}
	}

	tr {
		border-bottom: 1px solid $lines-global-bold;
	}

	&--striped {
		tr {
			border: 0;
		}
		tr {
			&:nth-child(2n) {
				background-color: $grey-light-color;
			}
		}
	}

	@include media(sm) {
		td, th {
			padding: .5rem .75rem;
			text-align: left;
		}
	}

	@include media-down(sm) {
		thead {
			display: none;
		}

		td {
			display: block;

			&::before {
				@include font(tbl-header);
				content: attr(data-header) ":";
				display: inline-block;
				min-width: 30%;
			}
		}

		tr {
			border-color: $lines-global-bold;
			display: block;
			padding: 1rem;

			&:last-child {
				border-bottom: 0;
			}
		}
	}
}

.tme {
	@include spacing-responsive-base;

	&-media {
		width: 100%;

		&__container {
			position: relative;
		}

		img, video, iframe {
			left: 0;
			position: absolute;
			top: 0;
			width: 100%;
		}

		iframe {
			height: 100%;
		}
	}

	&-styles {
		h3 {
			@include content-section;
		}

		abstract {
			@include font(lead);

			display: block;
			margin-bottom: $base-size * 2;
		}

		a:not(.btn) {
			@extend .btn--copy;
		}

		a.line-through {
			text-decoration: line-through;
		}

		a.ga-disabled {
			text-decoration: none;
			text-indent: 1rem;
		}

		a.ga-disabled:before {
			content: " ";
		}

		p {
			@include font(copy);
		}

		del {
			text-decoration: line-through;
		}

		small {
			font-size: 14px;
		}

		strong {
			font-weight: 600;
		}

		sub {
			font-size: .6em;
			vertical-align: bottom;
		}

		sup {
			font-size: .6em;
			vertical-align: top;
		}

		.text-center {
			text-align: center;
		}

		.text-justify {
			text-align: justify;
		}

		.text-right {
			text-align: right;
		}

		blockquote {
			border-left: 4px solid $grey-light-color;
			color: $black-extra-light-color;
			margin: 0;
			padding-left: $txt-blockquote-padding-left;
		}

		.ce-gallery {
			margin-bottom: 2rem;

			.ce-column {
				display: flex;
				justify-content: center;
			}

			.video,
			.image {
				flex: 0 0 auto;

				// hello there IE11
				max-width: 100%;
			}

			.image-embed-item,
			.video-embed-item {
				max-width: 100%;
			}

			figcaption {
				@include font(small);

				margin-top: .25rem;
			}
		}
	}
}

.tme {
	&__header {
		&--heading-2 {
			@include make-container;
		}

		&--heading-3 {
			@include make-content-container;

			.heading-3 {
				border-bottom: 1px solid $lines-global;
				margin-bottom: $base-size * 2;
				padding-bottom: $dtl-header-title-padding-bottom;
			}
		}
	}
}

.tme-strong {
	font-weight: 600;
}

.tme-center {
	text-align: center;
}

.tme-list {
	padding-left: 1rem;

	li {
		margin-bottom: 1rem;
		margin-top: 1rem;
	}

	&__paragraph {
		display: block;
		margin-bottom: .5rem;
		margin-top: .5rem;
	}
}

.tme-placeholder-block {
	box-shadow: inset 0 .75em $placeholder-paragraph-color;
	color: transparent;
}

