@mixin content-section {
	// used also in text-media-element
	@include font(h3);

	margin: ($base-size * 2) 0;
	padding-bottom: $dtl-header-title-padding-bottom;
	text-transform: uppercase;

	// after instead of border to account for padding
	&::after {
		background: $lines-global;
		bottom: -$dtl-header-title-padding-bottom;
		content: "";
		display: block;
		height: 1px;
		position: relative;
		width: 100%;
	}

	&:first-child {
		margin-top: 0;
	}
}

.ttl {
	color: getThemeColorValue("ttl__text");

	&__headline {
		display:block;
		margin: 0;
		padding: 0 $ttl-headline-padding-h;
		text-align: center;
		text-transform: uppercase;
	}

	&--page {
		.ttl__headline {
			@include font(h1);

			border-top: 3px solid $lines-global-bold;
			padding-top: $grid-gutter-width / 2;
			width: 100%;
		}

		@include media(sm) {
			.ttl__headline {
				border-top: none;
				padding-top: 0;
				width: auto;
			}
		}
	}

	&--section {
		@include spacing-responsive-base;

		.ttl__headline {
			@include font(h2);
		}

		@include media(sm) {
			&::before,
			&::after {
				height: 3px;
			}
		}
	}

	&--subsection {
		@include content-section;
	}
}


