.bkm {
	&-button {
		@include font(bkm-button);

		color: $black-extra-light-color;
		cursor: pointer;

		&:hover {
			opacity: .6;
		}
	}

	&-footer {
		display: flex;
		justify-content: flex-end;
	}

	&-dropdown {
		&__trigger {
			cursor: pointer;
		}

		&__trigger:hover &__list {
			opacity: 1;
			transition-delay: 0s;
			visibility: visible;
		}

		&__list {
			@include reset-list;

			background-color: $white-color;
			box-shadow: 4px 4px 8px transparentize($black-medium-color, .6);
			margin-top: .75rem;
			opacity: 0;
			padding-bottom: $mnu-dropdown-padding-bottom;
			position: absolute;
			transition: opacity $mnu-dropdown-opacity-transition, visibility $mnu-dropdown-visibility-transition;
			transition-delay: .5s;
			visibility: hidden;
			width: 22rem;
			z-index: 11;
		}

		&__headline {
			@include font(h3);

			margin-bottom: 0;
			text-transform: uppercase;
		}

		h4 {
			font-size: 12px;
			font-weight: 600;
			margin-bottom: 0;
			text-transform: none;
		}

		&__item {
			color: $black-medium-color;
			padding: $mnu-dropdown-item-padding-v $mnu-dropdown-item-padding-h;

			.btn {
				width: 100%;
			}

			.mnu-infolist__item & {
				html:not(.can-touch) & {
					.btn:hover {
						color: $white-color;
					}
				}
			}

			&--scrollable {
				padding-right: 0;

				ul {
					@include reset-list;

					max-height: 25rem;
					overflow-y: auto;
				}
			}

			.icn-spinner {
				color: getThemeColorValue("mdl-modal-inline-spinner");
				display: block;
				font-size: 1.5rem;
				margin: 1rem auto;
			}
		}
	}

	&-list {
		&__item {
			background-color: white;
			padding: $mnu-dropdown-item-padding-v $mnu-dropdown-item-padding-h $mnu-dropdown-item-padding-v 0;
			position: relative;
			z-index: $z-index-page-highlight;
		}

		article {
			border-bottom: 1px solid $lines-global;
			display: flex;
			flex-direction: column;
			padding-bottom: 1.25rem;

			> a {
				display: inherit;
			}
		}
	}
}

.js-bookmark-removing {
	filter: grayscale(1) opacity(.5);
	pointer-events: none;
	transition: filter $default-transition, margin $default-transition, opacity 1s;
}

.js-bookmark-removed {
	opacity: 0;
	z-index: calc(#{$z-index-page-highlight} - 1);
}

.btc-bar {
	align-items: center;
	display: flex;
	justify-content: center;

	&__width-adjust {
		align-items: stretch;
		display: inline-flex;
		flex-direction: column;
		flex-grow: 1;
		justify-content: center;
	}

	.btn {
		margin-top: $base-size;

		&:first-of-type {
			margin-top: 0;
		}
	}

	&__text {
		margin: 0;

		&:first-child {
			margin-bottom: 2rem;
		}

		&:last-child {
			margin-top: 2rem;
		}
	}

	@include media(sm) {
		&__width-adjust {
			flex-direction: row;
		}

		.btn {
			margin: 0 1rem;

			&:first-child {
				margin-left: 0;
			}

			&:last-child {
				margin-right: 0;
			}
		}

		&__text {
			max-width: 50%;

			&:first-child {
				margin: 0;
				margin-right: 2rem;
			}

			&:last-child {
				margin: 0;
				margin-left: 2rem;
			}
		}
	}

	&--left {
		.btc-bar__width-adjust {
			justify-content: flex-start;
			width: 100%;
		}
	}

	&--right {
		.btc-bar__width-adjust {
			justify-content: flex-end;
			width: 100%;
		}
	}

	&--space-between {
		@include media(sm) {
			.btc-bar__width-adjust {
				justify-content: space-between;
				width: 100%;
			}
		}
	}

	&--vertical-center {
		.btc-bar__width-adjust {
			align-items: center;
		}
	}
}

.btc {
	&-round-label {
		display: block;
		max-width: 110px;
		text-align: center;
		width: calc(#{$srv-icon-size} + 5%);

		&__button {
			background-color: getThemeColorValue("btn-round-label__bg");
			color: getThemeColorValue("btn-round-label__fg");
			display: inline-block;
			height: $srv-icon-size;
			margin-bottom: $srv-icon-margin-bottom;
			overflow: hidden;
			transition: $hover-transition;
			width: $srv-icon-size;
		}

		&__label {
			@include font(srv-button-text);

			color: $black-extra-light-color;
			display: block;
			overflow: hidden;
			text-align: center;
			text-overflow: ellipsis;
			text-transform: uppercase;
			white-space: nowrap;
			width: 100%;

			@include media(lg) {
				hyphens: auto;
				white-space: normal;
			}
		}

		&--small {
			max-width: 80px;
			width: calc(#{$btn-round-small} + 5%);

			.btc-round-label {
				&__button {
					height: $btn-round-small;
					width: $btn-round-small;
				}

				&__label {
					font-size: 9px;
				}
			}
		}

		// BUTTON and LABEL hover and active with variants
		// TODO II refactor as --bright
		&:hover &__button, &--active &__button {
			background-color: getThemeColorValue("header__bg");
			color: getThemeColorValue("header__fg");
		}

		&:hover &__label, &--active &__label {
			color: getThemeColorValue("btn-round-label--hover__label");
		}

		&--secondary &__button {
			background-color: $grey-medium-color;
			color: getThemeColorValue("srv-button-icon--secondary__fg");
		}

		&--secondary:hover &__button, .srv-button--active.srv-button--secondary &__icon {
			background-color: getThemeColorValue("srv-button-icon--secondary--active__bg");
			color: getThemeColorValue("srv-button-icon--secondary--active__fg");
		}

		&--header {
			.btc-round-label__button{
				background-color: getThemeColorValue("header__bg");
				color: getThemeColorValue("header__fg");
			}
			.btc-round-label__label{
				color: getThemeColorValue("header__text");
			}

			@include hover {
				// disable hover
				.btc-round-label__button {
					background-color: getThemeColorValue("header__bg");
					color: getThemeColorValue("header__fg");
