.fltr {
	&-menu {
		@include spacing-responsive-base;

		background-color: $grey-light-color;
		border-bottom: $fltr-menu-border-bottom solid $lines-global;

		&.has-submenu {
			margin-bottom: 0;
		}
	}

	&-container {
		@include make-container;
	}

	&-container-list {
		display: none;
		position: relative;

		html.js & {
			overflow: hidden;

			.fltr-list {
				&::-webkit-scrollbar {
					display: none;
				}

				//will hopefully work in the future
				overflow: -moz-scrollbars-none;
			}

			.fltr-nav {
				visibility: visible;
			}

			&:not(.fltr-container-list--has-overflow) {
				.fltr-nav {
					visibility: hidden;
				}
			}

			&:not(.fltr-container-list--overflow-prev) {
				.fltr-nav__prev {
					visibility: hidden;
				}
			}

			&:not(.fltr-container-list--overflow-next) {
				.fltr-nav__next {
					visibility: hidden;
				}
			}
		}
	}

	&-list {
		-webkit-overflow-scrolling: touch;
		display: flex;
		overflow-x: scroll;
		overflow-y: hidden;
		padding: 0px;
		position: relative;

		&__item {
			@include font(fltr-list);

			border-bottom: $fltr-list-item-border-width solid transparent;
			color: $black-medium-color;
			cursor: pointer;
			display: block;
			padding: $fltr-list-item-padding;
			text-align: center;
			text-transform: uppercase;
			white-space: nowrap;

			//preserve the list from resizing on hover
			//https://stackoverflow.com/questions/556153/inline-elements-shifting-when-made-bold-on-hover#answer-20249560
			&::after {
				color: transparent;
				content: attr(title);
				display: block;
				font-weight: bold;
				height: 1px;
				overflow: hidden;
				visibility: hidden;
			}

			&:hover, &--active {
				color: $primary-color;
				font-weight: bold;
			}

			&--active {
				border-bottom-color: $primary-color;
			}
		}
	}

	&-container-dropdown {
		background-color: $grey-light-color;
		text-align: center;
	}

	&-dropdown {
		background-color: $white-color;
		margin: $fltr-dropdown-margin auto;
		max-width: $fltr-dropdown-max-width;
		width: 90%;
	}

	&-nav {

		align-items: center;
		cursor: pointer;
		display: flex;
		font-style: normal;
		height: 100%;
		position: absolute;
		top: 0px;
		visibility: hidden;

		&::before, &::after {
			display: block;
		}

		&__prev {
			left: 0px;
		}

		&__next {
			right: 0px;
		}

		.icn-icon {
			font-size: 1.5em;
		}
	}

	@include media(sm) {
		&-container-list {
			display: block;
		}

		&-dropdown {
			display: none;
		}
	}
}

