	}

	&-container {
		@include make-container;

		display: flex;
		flex-direction: column;
	}

	&-control {
		display: block;
		line-height: 2rem;
		margin: 1rem .5rem;
	}

	&-control__search {
		margin-bottom: 2rem;
	}

	&-control__dropdown {
		color: $black-60-color;
	}

	@include media(sm) {
		&-container {
			align-items: flex-start;
			flex-direction: row;
		}

		&-controls {
			display: flex;
			flex: 1 1 0px;
			flex-wrap: wrap;
		}

		&-control__search {
			margin-bottom: 1rem;
			width: 100%;
		}

		&-control__dropdown {
			flex: 1 1 0px;
		}

		&-ribbons {
			display: flex;
			margin-left: 1rem;
		}

		&-ribbon {
			margin: -1rem .5rem 0;
			min-height: 4.25rem;
		}
	}

	&-result {
		&__title {
			@include make-container;
			@include font(src-title);

			color: $grey-color;
			margin-bottom: 2rem;
			text-transform: uppercase;
		}

		&__loading-indicator {
			@include make-container;

			text-align: center;

			.icn-spinner {
				display: inline-block;
				height: 50px;
				stroke: $primary-color;
				width: 50px;
			}
		}
	}

	@include media(md) {
		&-controls {
			flex-wrap: nowrap;
		}

		&-control__search {
			flex: 1.5 1 0px;
			width: auto;
		}
	}
}

.info-box {
	background-color: transparentize($white-color, .65);
	color: currentColor;
	opacity: .9;
	padding: $base-size $base-size $base-size ($base-size * 3.5);
	position: relative;

	.icn--svg {
		height: $base-size * 1.5;
		left: $base-size;
		position: absolute;
		top: $base-size;
		width: $base-size * 1.5;
	}

	&__text {
		@include font(emph);

		text-transform: uppercase;

		&--light {
			@include font(small);
		}
	}
}

.mnu {
	&-sublist {
		@include font(h6);
		@include reset-list;

		background-color: getThemeColorValue("mnu-sublist__bg");
		color: getThemeColorValue("mnu-sublist__text");

		&__item {
			> a {
				color: inherit;
				display: block;
				padding: $mnu-sublist-item-padding;
				transition: color $hover-transition, border-color $hover-transition;
			}

			&--active {
				background-color: $black-transparent-color;
			}

			&--accent {
				text-transform: uppercase;
			}
		}

		@include media(md) {
			@include font(menu-sublist);

			display: flex;
			flex-wrap: wrap;
			justify-content: center;
			text-transform: uppercase;

			&__item {
				flex: 0 1 auto;
				margin: $mnu-sublist-item-margin-v-md 0;
				max-width: 19em;
				text-align: center;

				> a {
					padding: $mnu-sublist-item-padding-md;
				}

				&:not(:first-child) {
					border-left: 1px solid $grey-color;
				}

				&--active {
					background-color: transparent;

					> a {
						text-decoration: underline;
					}
				}

				html:not(.can-touch) & {
					&:hover {
						color: getThemeColorValue("mnu-sublist--hover__text");

						> a {
							border-bottom-color: $black-light-color;
						}
					}
				}
			}
		}
	}
}

