		display: none;
	}

	&__container {
		display: flex;
		height: auto;
		position: relative;
		transition: $hover-transition;
	}

	&__content {
		bottom: 0;
		left: 0;
		position: absolute;
		transition: $hover-transition;
		width: 100%;
	}

	&__media {
		display: flex;
		filter: brightness(100%);
		height: auto;
		overflow: hidden;
		transition: $hover-transition;
		width: 100%;

		img {
			// IE polyfill
			font-family: 'object-fit: cover;';
			height: 100%;
			object-fit: cover;
			width: 100%;
		}

		picture {
			display: flex;
			width: 100%;
		}

		video {
			height: 100%;
			left: 50%;
			position: absolute;
			transform: translateX(-50%);
			width: auto;
		}
	}

	html:not(.can-touch) & {
		&:hover {
			.tsr-transparent__media {
				filter: brightness(85%);
			}
		}
	}


	@include media(sm) {
		height: auto;
		min-height: $tsr-transparent-min-height-sm;
	}


	&--columns-2 {
		@include media(md) {
			.tsr__content {
				transform: translate(25px, -25px);
				width: 50%;
			}
		}
	}

	&--columns-3 {
		@include media(md) {
			.tsr__content {
				transform: translate(25px, -25px);
				width: 35%;
			}
		}
	}

	&--event {
		.tsr__category {
			background-color: getThemeColorValue("tsr-category--dark__bg");
			color: getThemeColorValue("tsr-category--dark__text");
		}

		.tsr__container {
			background-color: $primary-color;
		}

		.tsr__content {
			background-color: getThemeColorValue("tsr-content--event__bg");
			color: $white-color;
		}
	}

	&--blog {
		.tsr__container {
			background-color: $white-color;
		}

		.tsr__content {
			background-color: $white-light-transparent-color;
		}
	}
}

