	.col-xs-#{$i} {
		@include make-col-span($i);
	}
}

@include media(sm) {
	@for $i from 1 through 12 {
		.col-sm-#{$i} {
			@include make-col-span($i);
		}
	}
}


@include media(md) {
	@for $i from 1 through 12 {
		.col-md-#{$i} {
			@include make-col-span($i);
		}
	}
}


@include media(lg) {
	@for $i from 1 through 12 {
		.col-lg-#{$i} {
			@include make-col-span($i);
		}
	}
}

.content-container {
	@include make-content-container;
}

.container {
	@include make-container;
}

.two-col-grid {
	@include make-content-container;

	// override container padding of content
	.tme-content {
		padding: 0;
	}
}

@mixin acc-hover {
	.acc-accordion__title {
		border-color: $primary-color;
		color: $primary-color;
	}

	.acc-accordion__title::after {
		color: $primary-color;
	}

	.acc-accordion__icon {
		color: $primary-color;
	}
}

.acc {
	&-accordion {
		&__title {
			border-bottom: thin solid $lines-global;
			cursor: pointer;
			display: flex;
			justify-content: space-between;
			padding-bottom: .5rem;

			&:hover {
				border-color: $primary-color;
				color: $primary-color;

				@include acc-hover;
			}
		}

		&__icon {
			color: $lines-global;
			display: inline-block;
			height: 1.75rem;
			margin: 0 .25rem;
			vertical-align: middle;
			width: 1.75rem;

			&--open {
				display: none;
			}
		}

		&__body {
			height: 0;
			overflow: hidden;

			// TODO it could be e.g. a div, with own margin or no margin
			p:last-of-type {
				margin-bottom: 0;
			}

			// TODO II it should be possible to have a tme that is not a container
			.tme__content {
				padding: 0;
			}
		}

		&--active {
			@include acc-hover;
		}

		&--active &__body {
			@include spacing-responsive-base;

			height: auto;
		}

		&--active &__icon {
			display: none;

			&--open {
				display: block;
			}
		}
	}

	// No javascript fallback
	&-section--no-js {
		.acc-accordion__title:focus,
		.acc-accordion:focus-within .acc-accordion__title {
			@include acc-hover;

			pointer-events: none;
		}

		.acc-accordion__title:focus .acc-accordion__icon {
			display: none;
		}

		.acc-accordion__title:focus .acc-accordion__icon--open {
			display: block;
		}
		.acc-accordion__title:focus ~ .acc-accordion__body,
		.acc-accordion__body:focus,
		.acc-accordion__body:focus-within {
			@include spacing-responsive-base;

			height: auto;
		}
	}
}

.acc {
	&-section {
		&__title {
			margin-bottom: .75rem;
		}
	}
}

.content-switch {
	@include make-content-container;
	@include section-padding;

	&__label {
		display: none;

		&--inner {
			@include font(button);

			border: 1px solid $grey-color;
			color: $grey-color;
			display: block;
			padding: .25em 1em;
			text-transform: uppercase;
		}
	}

	&__radio {
		left: -9999px;
		position: absolute;

		&:checked {
			& + .content-switch__label {
				background-color: $primary-color;
				border: 1px solid $primary-color;
				color: $white-color;
			}

			@for $i from 1 through 5 {
				&:nth-of-type(#{$i}) ~ .content-switch__content-wrapper:nth-of-type(#{$i}) {
					height: auto;

					.content-switch__label--inner {
						background-color: $primary-color;
						border: 1px solid $primary-color;
						color: $white-color;
					}

					.content-switch__content {
						height: auto;
					}
				}
			}
		}
	}

	&__content {
		// we can't hide the content with display none or filter-taby js in content
		// cannot compute its dimensions so we hide with height 0 and overflow hidden.
		height: 0;
		margin: 0 -25px;
		overflow: hidden;
	}

	@include media(sm) {
		&__label {
			@include font(button);

			border: 1px solid $grey-color;
			color: $grey-color;
			display: block;
			float: left;
			padding: .25em 1em;
			text-transform: uppercase;

			&--inner {
				display: none;
			}
		}

		&__content-wrapper {
			height: 0;
			overflow: hidden;
		}

		&__content {
			height: auto;
		}
	}
}

.content-tabs {
	.fltr-container-list {
		display: block;
		z-index: 2;

		&:before {
			background-color: $grey-color;
			bottom: 0;
			content: '';
			display: block;
			height: 1px;
			position: absolute;
			width: 100%;
		}
	}

	&__content {
		@include spacing-responsive-base("padding", "top");

		display: none;
		z-index: 1;

		&--active {
			display: block;
		}
	}
}

.cpn {
	&-detail {
		.col-xs-12:nth-child(2) {
			margin-top: 1.5rem;
			@include media-up(sm) {
				margin-top: 0;
			}
		}

		&__discountpane {
			position: relative;

			img {
				height: auto;
				width: 100%;
			}
		}

		&__app {
			margin-top: 3rem;
			h3 {
				color: getThemeColorValue("ttl__text")
			}
			a {
				display: inline-block;
			}
		}

		.btc-bar {
			align-items: normal;
			@include media-up(sm) {
				align-items: center;
			}
