.lgn {
	&-container {
		@include make-container;

		background: white;
		max-width: 100vw;
		padding: 1.5rem 2.5rem 2.5rem;
		position: relative;
		width: calc(#{min-width(md)} - 2 * #{$grid-container-padding});

		@include media(md) {
			.fltr-container-list {
				opacity: 0;
				position: absolute;
				top: 0;
				transform: translateY(-100%);
			}

			.content-tabs__content {
				padding-top: 0;
			}
		}

		&--small {
			max-width: 100%;
			width: calc(35rem - 2 * #{$grid-container-padding});
		}
	}

	&-hero {
		color: $white-color;
		display: none;
		position: static;

		&__background {
			font-family: 'object-fit: cover;'; // IE polyfill
			height: 100%;
			left: 0;
			object-fit: cover;
			position: absolute;
			top: 0;
			width: 50%;
		}

		&__body {

			position: relative;
			z-index: 1;
		}

		@include media(md) {
			display: block;

			&__body {
				@include section-margin;
			}
		}
	}

	// password forgotten, already registered...
	&-link {
		color: $primary-color;
		cursor: pointer;
		font-weight: bold;

		&:hover {
			color: getThemeColorValue("hover--on-bright");
		}
	}

	&-list {
		padding: 0;

		&__item {
			display: flex;
			list-style: none;
			margin-bottom: 1rem;
		}

		&__icon {
			color: $yellow-color;
			display: inline-block;
			flex: 0 0 2rem;
			height: 1.5rem;
			margin-right: .5rem;
		}
	}

	&-login {
		&__title {
			display: none;
			margin-top: 0;
		}

		@include media(md) {
			&__title {
				display: block;
			}

			// override grid gutter
			[class*="col-"] {
				padding-left: 25px;
				padding-right: 25px;
				position: static !important;
			}

			&.row {
				margin-left: -25px;
				margin-right: -25px;
			}
		}
	}

	&-form {
		.btc-bar {
			flex-direction: row;
		}

		@include media(sm) {
			.lgn-link {
				margin-bottom: 0rem;
			}
		}

		@include media(md) {
			@include section-margin;
		}
	}

	&-or-separator {
		@include title-sideline(1px, 0, $lines-global);

		color: $lines-global;
		margin-bottom: 2.5rem;
		margin-top: 3rem;

		&::after {
			height: 1px;
			margin-left: 1rem;
		}
		&::before {
			height: 1px;
			margin-right: 1rem;
		}
	}

	&-register {
		&__form {
			display: flex;
			flex-wrap: wrap;
			width: 100%;
		}

		.btc-bar {
			margin-top: 2rem;
		}

		.row {
			width: calc(100% + #{$grid-gutter-width});
		}

		@include media(md) {
			@include section-padding-asymmetric;
		}
	}

	&-social {
		text-align: center;

		&__button {
			color: $white-color;
			display: inline-block;
			margin-right: .5rem;
			margin-top: .5rem;
			padding: $btn-padding-vertical $btn-padding-horizontal;
		}
	}

	// vertically center registration button
	&-center {
		top: 50%;
		transform: translateY(-50%) scale(1.1);
	}
}

