.stf {
	&-carousel {
		bottom: .5rem;
		left: 50%;
		list-style: none;
		margin: 0;
		max-width: responsiveVal($stf-carousel-max-width, xs);
		padding: 0 ($grid-gutter-width / 2);
		position: absolute !important; //owl carousel css overwrite
		transform: translateX(-50%);

		.owl {
			&-nav {
				visibility: hidden;
			}
		}

		@include media(sm) {
			max-width: responsiveVal($stf-carousel-max-width, sm);
		}

		@include media(md) {
			padding: 0;
		}
	}

	&-opening {
		@include font(stf-opening);

		background-color: $generic-overlay-color;
		bottom: 0;
		color: $white-color;
		display: none;
		padding: $base-size;
		position: absolute;
		width: 100%;

		&.state-opening,
		&.state-closing {
			align-items: center;
			display: flex;
			justify-content: center;
		}
	}

	&-main {
		align-items: flex-end;
		min-height: $stf-main-min-height;
		position: relative;

		&__carousel {
			height: 124px;
			opacity: 0;
			transition: opacity 1.5s;
			transition-delay: .25s;
			visibility: hidden;

			&--single {
				animation: fade-in .75s .75s both;
				height: auto;
				visibility: visible;
				width: 100%;
				z-index: 1;
			}
		}

		.owl-dots {
			margin-top: .5rem;
			transform: scale(.75);
		}

		// fade slider in when loaded
		.owl-loaded {
			height: auto;
			opacity: 1;
			visibility: visible;
		}
	}

	@include media(sm) {
		&-main {
			// IE11 flexbox align-items fix
			height: 100px;
			min-height: $stf-main-min-height-f-sm;

			.owl-dots {
				margin-top: 1rem;
				padding-bottom: 1rem;
				transform: scale(1);
			}
		}
	}


	@include media(md) {
		&-main {
			align-items: center;
			min-height: $stf-main-min-height-f-md;
		}

		&-carousel {
			bottom: 5%;
			left: auto;
			right: 5%;
			transform: translate(0);

			.owl-nav {
				visibility: visible;
			}
		}
