.frm {
	&-radio {
		input[type=radio] {
			opacity: 0;
			position: absolute
		}

		&__label {
			margin-right: .5rem;
			position: relative;
		}

		&__span {
			&::before {
				background-color: $grey-light-color;
				border-radius: 100%;
				content: "";
				display: inline-block;
				height: 1rem;
				margin-right: .25rem;
				vertical-align: -2px;
				width: 1rem;
			}
		}

		input:checked ~ &__span::before {
			background-color: getThemeColorValue("hover--on-bright");
			border: .25rem solid $grey-light-color;
		}

		&__span:active::before {
			box-shadow: inset 0 0 2px $lines-global;
		}
	}
}

.frm {
	&-checkbox {
		input[type=checkbox] {
			cursor: pointer;
			opacity: 0;
			position: absolute;
		}

		input:focus ~ &__label:before {
			outline: 1px dotted;
		}

		&__label {
			&::before {
				border: 1px solid black;
				content: "";
				display: inline-block;
				height: 1rem;
				margin-right: .25rem;
				vertical-align: -2px;
				width: 1rem;
			}

			&:active::before {
				box-shadow: inset 0 0 1px $lines-global;
			}
		}

		input:checked ~ &__label::after {
			border-color: getThemeColorValue("hover--on-bright");
			border-style: solid;
			border-width: 0 2px 2px 0;
			content: "";
			height: 1rem;
			left: .5rem;
			position: absolute;
			top: 2px;
			transform: rotate(50deg);
			transform-origin: center;
			width: .5rem;
		}

		.has-error &__label::before {
			background-color: transparentize($error-color, .9);
			border-color: $error-color;
		}
	}
}

.frm {
	&-recaptcha {
		box-shadow: 0 0 0 1px $lines-global;
		filter: grayscale(100%);
		min-height: 60px;
		overflow: hidden;
		transform: scale(.8);
		transform-origin: bottom left;
	}
}

.frm {
	&-error {
		@include font(frm-error);

		color: $error-color;
		display: block;
		margin-top: $base-size / 2;
	}
}

.control-label {
	display: block;
	font-weight: bold;
	margin-bottom: $base-size / 2;

	.has-error & {
		color: $error-color;
	}
}

.error { // TODO clean
	color: $error-color;
	display: block;
	font-weight: bold;
	margin-top: $base-size / 2;
	transform: scale(.75);
	transform-origin: left top;
}

.t3-form {
	position: relative;

	fieldset {
		border: 0;
		padding: 0;
	}

	input:invalid {
		box-shadow: none;
		outline: none;
	}

	.general-response {
		@include spacing-responsive-base;

		align-items: center;
		display: flex;
		padding: $base-size (2 * $base-size);

		&__icon {
			height: 2 * $base-size;
			margin-right: $base-size;
			width: 2 * $base-size;
		}

		&.error {
			background-color: transparentize($error-color, .9);
			border: 1px solid $error-color;
			color: $error-color;

			.general-response__icon {
				color: $error-color;
			}
		}

		&.success {
			background-color: transparentize($success-color, .9);
			border: 1px solid $success-color;
			color: $success-color;

			.general-response__icon {
				color: $success-color;
			}
		}

		&.info {
			background-color: transparentize($black-38-color, .9);
			border: 1px solid $black-38-color;
			color: $black-60-color;

			.general-response__icon {
				color: $black-60-color;
			}
		}
	}

	.clearfix {
		.dtl-title {
			margin-top: $base-size * 3;
		}
	}

	.form-group {
		margin: $base-size 0 ($base-size * 2);
		position: relative;


		input[type=text], input[type=password], input[type=email] {
			background-color: $grey-light-color;
			font: inherit;
			height: $base-size * 3;
			padding-left: $base-size / 2;
			width: 100%;
		}

		&--hint{
			input {
				position: relative;
				z-index: 1;
			}

			label {
				position: relative;
				z-index: 2;
			}

			.input {
				overflow: hidden;
			}
		}

		.pwd-hint {
			border-color: $lines-global;
			border-style: solid;
			border-width: 0px;
			display: block;
			max-height: 0;
			overflow: hidden;
			padding: 0 .5rem;
			transform: translateY(-100%);
			transition: transform .6s, max-height .6s, border-width 0s .6s;

			&--match {
				color: green;
			}

			&__body {
				margin: .5rem 0;
			}
		}

		&.is-writing .pwd-hint {
			border-width: 1px;
			max-height: 500px;
			transform: translateY(0);
			transition: transform .6s, max-height .6s;
		}

		.show-password {
			@include font(small);

			color: $black-75-color;
			cursor: pointer;
			position: absolute;
			right: 0;
			top: 0;

			&:hover {
				color: $black-38-color;
			}
		}

		textarea {
			// Stop Safari Mobile from giving input buttons rounded corners
			-webkit-appearance: none;

			background-color: $grey-light-color;
			border: none;
			border-radius: 0;
			font: inherit;
			height: $base-size * 10;
			padding-left: $base-size / 2;
			padding-top: $base-size / 2;
			resize: none;
			width: 100%;
		}

		.inp-select {
			width: 100%;
		}

		input:focus, textarea:focus {
			background-color: $grey-light-color;
			outline: none;
		}

		.inputs-list .form-group {
			display: flex;
			margin: 0;

			.form-check {
				margin-right: $base-size;
			}
		}

		.form-check-label {
			input {
				margin-right: $base-size / 2;
			}
		}

		&.floating-label {
			margin-top: 0;

			.control-label {
				color: $placeholder-global-color;
				font-style: italic;
				font-weight: normal;
				margin-bottom: 0;

				//opacity: 0.5;
				pointer-events: none;
				transform: translate3d($base-size / 2, 2.25em, 0) scale(1);
				transform-origin: left top;
				transition: 240ms;
			}

			&.focused-or-filled .control-label {
				font-style: normal;
				opacity: 1;
				text-transform: uppercase;
				transform: scale(.75);
			}

			::-webkit-input-placeholder {
				/* Chrome/Opera/Safari */
				color: transparent;
			}

			::-moz-placeholder {
				/* Firefox 19+ */
				color: transparent;
			}

			:-ms-input-placeholder {
				/* IE 10+ */
				color: transparent;
			}
		}

		&.has-error {
			.pwd-hint {
				border-top-width: 0;
			}

			input[type=text], input[type=password], input[type=email], textarea {
				border-bottom: 2px solid $error-color;
			}
		}
	}

	.actions {
		margin-bottom: $base-size;
	}

	.hint {
		@include spacing-responsive-base("margin", "top");
	}
}

.frm {
	&-radio {
		input[type=radio] {
			opacity: 0;
			position: absolute
		}

		&__label {
			margin-right: .5rem;
			position: relative;
		}

		&__span {
			&::before {
				background-color: $grey-light-color;
				border-radius: 100%;
				content: "";
				display: inline-block;
				height: 1rem;
				margin-right: .25rem;
				vertical-align: -2px;
				width: 1rem;
			}
		}

		input:checked ~ &__span::before {
			background-color: getThemeColorValue("hover--on-bright");
			border: .25rem solid $grey-light-color;
		}

		&__span:active::before {
			box-shadow: inset 0 0 2px $lines-global;
		}
	}
}

.frm {
	&-checkbox {
		input[type=checkbox] {
			cursor: pointer;
			opacity: 0;
			position: absolute;
		}

		input:focus ~ &__label:before {
			outline: 1px dotted;
		}

		&__label {
			&::before {
				border: 1px solid black;
				content: "";
				display: inline-block;
				height: 1rem;
				margin-right: .25rem;
				vertical-align: -2px;
				width: 1rem;
			}

			&:active::before {
				box-shadow: inset 0 0 1px $lines-global;
			}
		}

		input:checked ~ &__label::after {
			border-color: getThemeColorValue("hover--on-bright");
			border-style: solid;
			border-width: 0 2px 2px 0;
			content: "";
			height: 1rem;
			left: .5rem;
			position: absolute;
			top: 2px;
			transform: rotate(50deg);
			transform-origin: center;
			width: .5rem;
		}

		.has-error &__label::before {
			background-color: transparentize($error-color, .9);
			border-color: $error-color;
		}
	}
}

